import{_ as i,o as l,c as a,e}from"./app.6085dcd2.js";const r={},t=e('<h1 id="\u1106\u1165\u1109\u1175\u11AB\u1105\u1165\u1102\u1175\u11BC\u110B\u1175\u1105\u1161\u11AB" tabindex="-1"><a class="header-anchor" href="#\u1106\u1165\u1109\u1175\u11AB\u1105\u1165\u1102\u1175\u11BC\u110B\u1175\u1105\u1161\u11AB" aria-hidden="true">#</a> \uBA38\uC2E0\uB7EC\uB2DD\uC774\uB780?</h1><p>\uC8FC\uC5B4\uC9C4 \uB370\uC774\uD130\uB97C \uBD84\uC11D\uD558\uC5EC \uC77C\uBC18\uC801\uC778 \uADDC\uCE59\uC774\uB098 \uC0C8\uB85C\uC6B4 \uC9C0\uC2DD\uC744 \uAE30\uACC4 \uC2A4\uC2A4\uB85C\uAC00 \uC790\uB3D9\uC73C\uB85C \uCD94\uCD9C\uD558\uAE30 \uC704\uD55C \uC811\uADFC \uBC29\uBC95</p><div class="custom-container tip"><p class="custom-container-title">\uC65C \uD544\uC694\uD560\uAE4C?</p><p>\uB370\uC774\uD130\uC758 \uB2E4\uC591\uD55C <strong>\uBCC0\uD615</strong>\uC744 \uB2E4\uB8E8\uAE30 \uC704\uD574\uC11C</p></div><div class="custom-container tip"><p class="custom-container-title">\uB525\uB7EC\uB2DD\uC774\uB780?</p><ul><li>\uC2EC\uCE35 \uD559\uC2B5</li><li>\uC2EC\uCE35 \uC2E0\uACBD\uB9DD \uAE30\uBC18\uC758 \uBA38\uC2E0\uB7EC\uB2DD \uBD84\uC57C</li></ul></div><h2 id="\u1106\u1165\u1109\u1175\u11AB\u1105\u1165\u1102\u1175\u11BC\u110B\u1174-\u110E\u1165\u1105\u1175-\u1100\u116A\u110C\u1165\u11BC" tabindex="-1"><a class="header-anchor" href="#\u1106\u1165\u1109\u1175\u11AB\u1105\u1165\u1102\u1175\u11BC\u110B\u1174-\u110E\u1165\u1105\u1175-\u1100\u116A\u110C\u1165\u11BC" aria-hidden="true">#</a> \uBA38\uC2E0\uB7EC\uB2DD\uC758 \uCC98\uB9AC \uACFC\uC815</h2><ul><li><strong>\uD559\uC2B5 \uB2E8\uACC4</strong> : \uD559\uC2B5 \uB370\uC774\uD130 \uC9D1\uD569\uC758 \uBD84\uC11D\uC744 \uD1B5\uD574 \uC6D0\uD558\uB294 \uC785,\uCD9C\uB825\uC758 \uAD00\uACC4\uB97C \uC54C\uB824\uC8FC\uB294 \uB9E4\uD551 \uD568\uC218\uB97C \uCC3E\uB294 \uACFC\uC815</li><li><strong>\uCD94\uB860 \uB2E8\uACC4</strong> : \uD559\uC2B5\uC744 \uD1B5\uD574 \uCC3E\uC740 \uB9E4\uD551 \uD568\uC218\uB97C \uC2E4\uC81C \uB370\uC774\uD130(&quot;\uD14C\uC2A4\uD2B8 \uB370\uC774\uD130&quot;)\uC5D0 \uC801\uC6A9\uD558\uC5EC \uACB0\uACFC\uB97C \uC5BB\uB294 \uACFC\uC815</li></ul><h2 id="\u1106\u1165\u1109\u1175\u11AB\u1105\u1165\u1102\u1175\u11BC-\u1100\u1162\u1107\u1161\u11AF-\u1100\u116A\u110C\u1165\u11BC" tabindex="-1"><a class="header-anchor" href="#\u1106\u1165\u1109\u1175\u11AB\u1105\u1165\u1102\u1175\u11BC-\u1100\u1162\u1107\u1161\u11AF-\u1100\u116A\u110C\u1165\u11BC" aria-hidden="true">#</a> \uBA38\uC2E0\uB7EC\uB2DD \uAC1C\uBC1C \uACFC\uC815</h2><ol><li>\uBB38\uC81C \uD30C\uC545</li><li>\uCDA9\uBD84\uD55C \uB370\uC774\uD130 \uC218\uC9D1 \uBC0F \uC774\uD574</li><li><strong>\uB370\uC774\uD130 \uC900\uBE44(\uC804\uCC98\uB9AC) \uBC0F \uD2B9\uC9D5 \uCD94\uCD9C</strong></li><li><strong>\uBAA8\uB378 \uC218\uB9BD \uBC0F \uBD84\uC11D</strong></li><li>\uBAA8\uB378 \uD3C9\uAC00 (\uBAA8\uB378\uC774 \uCDA9\uBD84\uD558\uC9C0 \uC54A\uB2E4\uBA74 1~5\uBC88\uC911\uC5D0 \uC54C\uB9DE\uC740 \uB2E8\uACC4\uB85C \uB2E4\uC2DC \uD68C\uADC0)</li><li>\uBC30\uD3EC</li></ol><div class="custom-container tip"><p class="custom-container-title">\uBA38\uC2E0\uB7EC\uB2DD\uC758 \uD575\uC2EC</p><ul><li>\uB370\uC774\uD130 \uC900\uBE44(\uC804\uCC98\uB9AC) \uBC0F \uD2B9\uC9D5 \uCD94\uCD9C</li><li>\uBAA8\uB378 \uC218\uB9BD \uBC0F \uBD84\uC11D</li></ul></div><h2 id="\u1106\u1165\u1109\u1175\u11AB\u1105\u1165\u1102\u1175\u11BC\u110B\u1174-\u1100\u1175\u1107\u1169\u11AB-\u110B\u116D\u1109\u1169" tabindex="-1"><a class="header-anchor" href="#\u1106\u1165\u1109\u1175\u11AB\u1105\u1165\u1102\u1175\u11BC\u110B\u1174-\u1100\u1175\u1107\u1169\u11AB-\u110B\u116D\u1109\u1169" aria-hidden="true">#</a> \uBA38\uC2E0\uB7EC\uB2DD\uC758 \uAE30\uBCF8 \uC694\uC18C</h2><h3 id="\u1103\u1166\u110B\u1175\u1110\u1165\u110B\u116A-\u1103\u1166\u110B\u1175\u1110\u1165-\u1107\u116E\u11AB\u1111\u1169" tabindex="-1"><a class="header-anchor" href="#\u1103\u1166\u110B\u1175\u1110\u1165\u110B\u116A-\u1103\u1166\u110B\u1175\u1110\u1165-\u1107\u116E\u11AB\u1111\u1169" aria-hidden="true">#</a> \uB370\uC774\uD130\uC640 \uB370\uC774\uD130 \uBD84\uD3EC</h3><ul><li>\uB370\uC774\uD130 \uD45C\uD604 \uBC29\uBC95 : n\uCC28\uC6D0\uC758 \uBCA1\uD130 (\uC5F4\uBCA1\uD130)</li><li>\uB370\uC774\uD130 \uC9D1\uD569\uC758 \uBD84\uD3EC \uD2B9\uC131 <ul><li>\uD574\uB2F9 \uACF5\uAC04\uC0C1\uC5D0\uC11C \uC810\uB4E4\uC774 \uBD84\uD3EC\uB41C \uBAA8\uC591</li><li>2\uCC28\uC6D0 \uB370\uC774\uD130 \uC9D1\uD569\uC758 <strong>\uC0B0\uC810\uB3C4(scatter plot)</strong><ul><li>\uAC00\uC6B0\uC2DC\uC548 \uBD84\uD3EC</li></ul></li></ul></li></ul><div class="custom-container tip"><p class="custom-container-title">\uC6A9\uC5B4</p><ul><li>flatten : \uBCA1\uD130\uB97C \uC5F4\uBCA1\uD130\uB85C \uBCC0\uD658\uD558\uB294 \uD589\uC704</li></ul></div><h3 id="\u1110\u1173\u11A8\u110C\u1175\u11BC-\u110E\u116E\u110E\u116E\u11AF" tabindex="-1"><a class="header-anchor" href="#\u1110\u1173\u11A8\u110C\u1175\u11BC-\u110E\u116E\u110E\u116E\u11AF" aria-hidden="true">#</a> \uD2B9\uC9D5 \uCD94\uCD9C</h3><ul><li>\uC0AC\uC601(projection)\uC5D0 \uC758\uD55C \uD2B9\uC9D5 \uCD94\uCD9C</li></ul><h3 id="\u1109\u1165\u11BC\u1102\u1173\u11BC-\u1111\u1167\u11BC\u1100\u1161" tabindex="-1"><a class="header-anchor" href="#\u1109\u1165\u11BC\u1102\u1173\u11BC-\u1111\u1167\u11BC\u1100\u1161" aria-hidden="true">#</a> \uC131\uB2A5 \uD3C9\uAC00</h3><ul><li>\uD559\uC2B5 \uC2DC\uC2A4\uD15C <ul><li>\uB370\uC774\uD130\uB85C\uBD80\uD130 \uD559\uC2B5\uC744 \uD1B5\uD574 \uCD94\uCD9C\uD558\uACE0\uC790 \uD558\uB294 \uC815\uBCF4\uB97C \uD45C\uD604\uD558\uB294 \uC2DC\uC2A4\uD15C</li><li>\uC785,\uCD9C\uB825 \uB9E4\uD551 \uD615\uD0DC\uC758 \uD568\uC218\uB85C \uC815\uC758</li><li>\uD559\uC2B5 <ul><li>\uB370\uC774\uD130\uB97C \uC774\uC6A9\uD558\uC5EC \uD568\uC218 f\uB97C \uCC3E\uB294 \uAC83</li><li>\uD559\uC2B5 \uC2DC\uC2A4\uD15C\uC758 \uB9E4\uAC1C\uBCC0\uC218 \u0398\uB97C \uCC3E\uB294\uAC83</li></ul></li><li>\uD559\uC2B5\uC758 \uAD81\uADF9\uC801 \uBAA9\uD45C: \uC55E\uC73C\uB85C \uC8FC\uC5B4\uC9C8 \uC0C8\uB85C\uC6B4 \uB370\uC774\uD130\uC5D0 \uB300\uD55C \uC131\uB2A5\uC744 \uCD5C\uB300\uD654\uD558\uB294 \uAC83</li></ul></li></ul><h3 id="\u110B\u1169\u110E\u1161\u1112\u1161\u11B7\u1109\u116E\u1105\u1173\u11AF-\u110B\u1175\u110B\u116D\u11BC\u1112\u1161\u11AB-\u1109\u1165\u11BC\u1102\u1173\u11BC-\u1111\u1167\u11BC\u1100\u1161-\u1100\u1175\u110C\u116E\u11AB" tabindex="-1"><a class="header-anchor" href="#\u110B\u1169\u110E\u1161\u1112\u1161\u11B7\u1109\u116E\u1105\u1173\u11AF-\u110B\u1175\u110B\u116D\u11BC\u1112\u1161\u11AB-\u1109\u1165\u11BC\u1102\u1173\u11BC-\u1111\u1167\u11BC\u1100\u1161-\u1100\u1175\u110C\u116E\u11AB" aria-hidden="true">#</a> \uC624\uCC28\uD568\uC218\uB97C \uC774\uC6A9\uD55C \uC131\uB2A5 \uD3C9\uAC00 \uAE30\uC900</h3><ul><li>\uD559\uC2B5 \uC624\uCC28 : \uD559\uC2B5\uC5D0 \uC0AC\uC6A9\uB41C \uB370\uC774\uD130(<code>\uD559\uC2B5 \uB370\uC774\uD130</code>) \uC9D1\uD569\uC5D0 \uB300\uD574 \uACC4\uC0B0\uB41C \uC624\uCC28</li><li>\uD14C\uC2A4\uD2B8 \uC624\uCC28 : \uD559\uC2B5\uC5D0 \uC0AC\uC6A9\uB418\uC9C0 \uC54A\uC740 \uC0C8\uB85C\uC6B4 \uB370\uC774\uD130(<code>\uD14C\uC2A4\uD2B8 \uB370\uC774\uD130</code>) \uC9D1\uD569\uC5D0 \uB300\uD574 \uACC4\uC0B0\uB41C \uC624\uCC28</li><li>\uC77C\uBC18\uD654 \uC624\uCC28 <ul><li>\uAD00\uCC30\uB420 \uC218 \uC788\uB294 \uBAA8\uB4E0 \uB370\uC774\uD130 \uBD84\uD3EC \uC804\uCCB4\uC5D0 \uB300\uD574 \uC815\uC758\uB418\uB294 \uC624\uCC28</li><li><strong>\uC2E4\uC81C \uACC4\uC0B0\uC774 \uBD88\uAC00\uB2A5</strong>\uD574\uC11C \uD14C\uC2A4\uD2B8 \uC624\uCC28\uB85C \uB300\uC2E0\uD558\uC5EC \uD3C9\uAC00</li></ul></li></ul><h3 id="\u110B\u1175\u11AF\u1107\u1161\u11AB\u1112\u116A-\u110B\u1169\u110E\u1161\u110B\u1174-\u110E\u116E\u110C\u1165\u11BC" tabindex="-1"><a class="header-anchor" href="#\u110B\u1175\u11AF\u1107\u1161\u11AB\u1112\u116A-\u110B\u1169\u110E\u1161\u110B\u1174-\u110E\u116E\u110C\u1165\u11BC" aria-hidden="true">#</a> \uC77C\uBC18\uD654 \uC624\uCC28\uC758 \uCD94\uC815</h3><ul><li>\uAD50\uCC28\uAC80\uC99D\uBC95 : \uC81C\uD55C\uB41C \uB370\uC774\uD130 \uC9D1\uD569\uC744 \uC774\uC6A9\uD558\uC5EC \uC77C\uBC18\uD654 \uC624\uCC28\uC5D0 \uC880 \uB354 \uADFC\uC811\uD55C \uC624\uCC28\uAC12\uC744 \uC5BB\uC5B4 \uB0B4\uAE30 \uC704\uD55C \uBC29\uBC95</li></ul><h2 id="\u1106\u1165\u1109\u1175\u11AB\u1105\u1165\u1102\u1175\u11BC\u110B\u1166\u1109\u1165\u110B\u1174-\u110C\u116E\u110C\u1166" tabindex="-1"><a class="header-anchor" href="#\u1106\u1165\u1109\u1175\u11AB\u1105\u1165\u1102\u1175\u11BC\u110B\u1166\u1109\u1165\u110B\u1174-\u110C\u116E\u110C\u1166" aria-hidden="true">#</a> \uBA38\uC2E0\uB7EC\uB2DD\uC5D0\uC11C\uC758 \uC8FC\uC81C</h2><ul><li><strong>\uB370\uC774\uD130 \uBD84\uC11D</strong><ul><li>\uBD84\uB958(classification)</li><li>\uD68C\uADC0(regression)</li><li>\uAD70\uC9D1\uD654(clustering)</li></ul></li><li><strong>\uB370\uC774\uD130 \uD45C\uD604</strong><ul><li>\uD2B9\uC9D5\uCD94\uCD9C(feature extraction)</li></ul></li></ul><h3 id="\u1107\u116E\u11AB\u1105\u1172-classification" tabindex="-1"><a class="header-anchor" href="#\u1107\u116E\u11AB\u1105\u1172-classification" aria-hidden="true">#</a> \uBD84\uB958(classification)</h3><ul><li>\uC785\uB825 \uB370\uC774\uD130\uAC00 \uC5B4\uB5A4 \uBD80\uB958\uC5D0 \uC18D\uD558\uB294\uC9C0\uB97C \uC790\uB3D9\uC73C\uB85C \uD310\uB2E8\uD558\uB294 \uBB38\uC81C (\uC5BC\uAD74\uC778\uC2DD, \uC0DD\uCCB4\uC778\uC2DD, \uC22B\uC790\uC778\uC2DD)</li><li><strong>\uD559\uC2B5 \uBAA9\uD45C</strong> : \uBD84\uB958 \uC624\uCC28\uB97C \uCD5C\uC18C\uD654\uD558\uB294 <strong>\uCD5C\uC801\uC758 \uACB0\uC815\uACBD\uACC4</strong> <code>g(x;\u0398) = 0</code>\uB97C \uCC3E\uB294 \uAC83</li><li><strong>\uC131\uB2A5 \uD3C9\uAC00 \uCC99\uB3C4</strong><ul><li><code>\uBD84\uB958\uC728(%)</code> = <code>\uBD84\uB958 \uC131\uACF5 \uB370\uC774\uD130 \uAC1C\uC218</code> / <code>\uC804\uCCB4 \uB370\uC774\uD130 \uAC1C\uC218</code> X 100</li><li><code>\uBD84\uB958\uC624\uCC28(%)</code> = <code>\uBD84\uB958 \uC2E4\uD328 \uB370\uC774\uD130 \uAC1C\uC218</code> / <code>\uC804\uCCB4 \uB370\uC774\uD130 \uAC1C\uC218</code> X 100</li></ul></li></ul><h3 id="\u1112\u116C\u1100\u1171-regression" tabindex="-1"><a class="header-anchor" href="#\u1112\u116C\u1100\u1171-regression" aria-hidden="true">#</a> \uD68C\uADC0(regression)</h3><ul><li>\uC785\uB825\uBCC0\uC218\uC640 \uCD9C\uB825\uBCC0\uC218 \uC0AC\uC774\uC758 \uB9E4\uD551 \uAD00\uACC4\uB97C \uBD84\uC11D (\uC2DC\uAC8C\uC5F4 \uC608\uCE21, \uC2DC\uC7A5 \uC608\uCE21, \uD658\uC728 \uC608\uCE21, \uC8FC\uAC00 \uC608\uCE21)</li><li>\uD68C\uADC0 \uC624\uCC28\uB97C \uCD5C\uC18C\uD654\uD558\uB294 \uCD5C\uC801\uC758 \uD68C\uADC0\uD568\uC218 <code>y = f(x;0)</code>\uB97C \uCC3E\uB294 \uAC83</li><li>\uD68C\uADC0 \uC624\uCC28\uB294 \uACE7 \uC81C\uACF1 \uC624\uCC28\uC774\uB2E4.</li></ul><h3 id="\u1100\u116E\u11AB\u110C\u1175\u11B8\u1112\u116A-clustering" tabindex="-1"><a class="header-anchor" href="#\u1100\u116E\u11AB\u110C\u1175\u11B8\u1112\u116A-clustering" aria-hidden="true">#</a> \uAD70\uC9D1\uD654(clustering)</h3><ul><li>\uB370\uC774\uD130 \uC9D1\uD569\uC744 \uC11C\uB85C \uBE44\uC2B7\uD55C \uBA87 \uAC1C\uC758 \uADF8\uB8F9(cluster)\uC73C\uB85C \uBB36\uB294 \uBB38\uC81C. \uBD84\uB958 \uBB38\uC81C\uC640 \uB2EC\uB9AC \uD074\uB798\uC2A4 \uC815\uBCF4\uAC00 \uC8FC\uC5B4\uC9C0\uC9C0 \uC54A\uC74C (\uB370\uC774\uD130 \uADF8\uB8F9\uD654, \uC601\uC0C1 \uBD84\uD560)</li><li>\uCD5C\uC801\uC758 \uD074\uB7EC\uC2A4\uD130\uC758 \uC9D1\uD569\uC744 \uCC3E\uB294 \uAC83</li><li>\uD074\uB7EC\uC2A4\uD130 \uB0B4\uC758 \uBD84\uC0B0\uC740 \uCD5C\uC18C\uD654, \uD074\uB7EC\uC2A4\uD130 \uAC04\uC758 \uBD84\uC0B0\uC740 \uCD5C\uB300\uD654</li></ul><h3 id="\u1110\u1173\u11A8\u110C\u1175\u11BC\u110E\u116E\u110E\u116E\u11AF-feature-extraction" tabindex="-1"><a class="header-anchor" href="#\u1110\u1173\u11A8\u110C\u1175\u11BC\u110E\u116E\u110E\u116E\u11AF-feature-extraction" aria-hidden="true">#</a> \uD2B9\uC9D5\uCD94\uCD9C(feature extraction)</h3><ul><li>\uC6D0\uB798 \uB370\uC774\uD130\uB85C\uBD80\uD130 \uB370\uC774\uD130 \uBD84\uC11D\uC5D0 \uC801\uC6A9\uD558\uAE30 \uC88B\uC740 \uD2B9\uC9D5\uC744 \uCC3E\uC544\uB0B4\uB294 \uBB38\uC81C (\uC601\uC0C1 \uB370\uC774\uD130\uC758 \uCC28\uC6D0 \uCD95\uC18C, \uB370\uC774\uD130 \uC2DC\uAC01\uD654)</li></ul><h2 id="\u1106\u1165\u1109\u1175\u11AB\u1105\u1165\u1102\u1175\u11BC\u110B\u1174-\u110B\u1172\u1112\u1167\u11BC" tabindex="-1"><a class="header-anchor" href="#\u1106\u1165\u1109\u1175\u11AB\u1105\u1165\u1102\u1175\u11BC\u110B\u1174-\u110B\u1172\u1112\u1167\u11BC" aria-hidden="true">#</a> \uBA38\uC2E0\uB7EC\uB2DD\uC758 \uC720\uD615</h2><h3 id="\u110C\u1175\u1103\u1169\u1112\u1161\u11A8\u1109\u1173\u11B8-\u1107\u116E\u11AB\u1105\u1172-\u1112\u116C\u1100\u1171" tabindex="-1"><a class="header-anchor" href="#\u110C\u1175\u1103\u1169\u1112\u1161\u11A8\u1109\u1173\u11B8-\u1107\u116E\u11AB\u1105\u1172-\u1112\u116C\u1100\u1171" aria-hidden="true">#</a> \uC9C0\uB3C4\uD559\uC2B5 (\uBD84\uB958, \uD68C\uADC0)</h3><p>\uD559\uC2B5\uD560 \uB54C \uC2DC\uC2A4\uD15C\uC774 \uCD9C\uB825\uD574\uC57C \uD560 \uBAA9\uD45C \uCD9C\uB825\uAC12(<code>\uAD50\uC0AC</code>)\uC744 \uD568\uAED8 \uC81C\uACF5</p><h3 id="\u1107\u1175\u110C\u1175\u1103\u1169\u1112\u1161\u11A8\u1109\u1173\u11B8-\u1100\u116E\u11AB\u110C\u1175\u11B8\u1112\u116A" tabindex="-1"><a class="header-anchor" href="#\u1107\u1175\u110C\u1175\u1103\u1169\u1112\u1161\u11A8\u1109\u1173\u11B8-\u1100\u116E\u11AB\u110C\u1175\u11B8\u1112\u116A" aria-hidden="true">#</a> \uBE44\uC9C0\uB3C4\uD559\uC2B5 (\uAD70\uC9D1\uD654)</h3><p>\uD559\uC2B5\uD560 \uB54C \uBAA9\uD45C \uCD9C\uB825\uAC12\uC5D0 \uB300\uD55C \uC815\uBCF4\uAC00 \uC5C6\uC74C</p><h3 id="\u110C\u116E\u11AB\u110C\u1175\u1103\u1169\u1112\u1161\u11A8\u1109\u1173\u11B8" tabindex="-1"><a class="header-anchor" href="#\u110C\u116E\u11AB\u110C\u1175\u1103\u1169\u1112\u1161\u11A8\u1109\u1173\u11B8" aria-hidden="true">#</a> \uC900\uC9C0\uB3C4\uD559\uC2B5</h3><p>\uC9C0\uB3C4\uD559\uC2B5 + \uBE44\uC9C0\uB3C4\uD559\uC2B5 -&gt; <strong>\uD074\uB798\uC2A4 \uB808\uC774\uBE14\uB9C1 \uBE44\uC6A9\uC744 \uC904\uC774\uB824\uB294 \uBAA9\uC801</strong></p><div class="custom-container tip"><p class="custom-container-title">\uD074\uB798\uC2A4 \uB808\uC774\uBE14\uB9C1\uC774\uB780?</p><p>\uC9C0\uB3C4\uD559\uC2B5\uC5D0 \uD544\uC694\uD55C \uBAA9\uD45C \uCD9C\uB825\uAC12\uC744 \uAD6C\uD558\uB294 \uD589\uC704</p></div><h3 id="\u1100\u1161\u11BC\u1112\u116A\u1112\u1161\u11A8\u1109\u1173\u11B8-\u1103\u1175\u11B8\u1105\u1165\u1102\u1175\u11BC\u110B\u1166\u1109\u1165-\u1106\u1161\u11AD\u110B\u1175-\u110A\u1173\u110B\u1175\u11B7" tabindex="-1"><a class="header-anchor" href="#\u1100\u1161\u11BC\u1112\u116A\u1112\u1161\u11A8\u1109\u1173\u11B8-\u1103\u1175\u11B8\u1105\u1165\u1102\u1175\u11BC\u110B\u1166\u1109\u1165-\u1106\u1161\u11AD\u110B\u1175-\u110A\u1173\u110B\u1175\u11B7" aria-hidden="true">#</a> \uAC15\uD654\uD559\uC2B5 (\uB525\uB7EC\uB2DD\uC5D0\uC11C \uB9CE\uC774 \uC4F0\uC784)</h3><ul><li>\uCD9C\uB825\uAC12\uC5D0 \uB300\uD55C \uAD50\uC0AC \uC2E0\uD638\uAC00 <code>\uBCF4\uC0C1</code>\uD615\uD0DC\uB85C \uC81C\uACF5</li><li>\uAD50\uC0AC \uC2E0\uD638\uB294 \uC815\uD655\uD55C \uAC12\uC774 \uC544\uB2C8\uACE0, \uC989\uC2DC \uC8FC\uC5B4\uC9C0\uC9C0 \uC54A\uC74C</li></ul><h2 id="\u1112\u1161\u11A8\u1109\u1173\u11B8-\u1109\u1175\u1109\u1173\u1110\u1166\u11B7-\u1100\u116A\u11AB\u1105\u1167\u11AB-\u1100\u1162\u1102\u1167\u11B7" tabindex="-1"><a class="header-anchor" href="#\u1112\u1161\u11A8\u1109\u1173\u11B8-\u1109\u1175\u1109\u1173\u1110\u1166\u11B7-\u1100\u116A\u11AB\u1105\u1167\u11AB-\u1100\u1162\u1102\u1167\u11B7" aria-hidden="true">#</a> \uD559\uC2B5 \uC2DC\uC2A4\uD15C \uAD00\uB828 \uAC1C\uB150</h2><h3 id="\u1100\u116A\u1103\u1161\u110C\u1165\u11A8\u1112\u1161\u11B8-overfitting" tabindex="-1"><a class="header-anchor" href="#\u1100\u116A\u1103\u1161\u110C\u1165\u11A8\u1112\u1161\u11B8-overfitting" aria-hidden="true">#</a> \uACFC\uB2E4\uC801\uD569(overfitting)</h3><p>\uD559\uC2B5 \uC2DC\uC2A4\uD15C\uC774 \uD559\uC2B5 \uB370\uC774\uD130\uC5D0 \uB300\uD574\uC11C\uB9CC \uC9C0\uB098\uCE58\uAC8C \uC801\uD569\uD55C \uD615\uD0DC\uB85C \uACB0\uC815\uACBD\uACC4\uAC00 \uD615\uC131\uB418\uB294 \uD604\uC0C1</p><ul><li><strong>\uC6D0\uC778</strong> : \uD559\uC2B5 \uB370\uC774\uD130\uC758 \uD655\uB960\uC801 \uC7A1\uC74C\uACFC <strong>\uD559\uC2B5 \uB370\uC774\uD130 \uAC1C\uC218\uC758 \uBD80\uC871</strong></li><li><strong>\uC601\uD5A5</strong> : \uC77C\uBC18\uD654 \uC131\uB2A5 \uC800\uD558 \uCD08\uB798</li><li>\uD559\uC2B5 \uC2DC\uC2A4\uD15C\uC758 \uBCF5\uC7A1\uB3C4\uB97C \uC870\uC815\uD558\uB294 \uBC29\uBC95</li><li>\uCDA9\uBD84\uD55C \uD559\uC2B5 \uB370\uC774\uD130 \uC0AC\uC6A9</li><li>\uC870\uAE30 \uC885\uB8CC \uBC29\uBC95</li><li>\uC815\uADDC\uD56D\uC744 \uAC00\uC9C4 \uC624\uCC28\uD568\uC218 \uC0AC\uC6A9</li><li>\uBAA8\uB378 \uC120\uD0DD \uBC29\uBC95</li></ul>',45),n=[t];function d(s,o){return l(),a("div",null,n)}var h=i(r,[["render",d],["__file","01. \uBA38\uC2E0\uB7EC\uB2DD\uC774\uB780.html.vue"]]);export{h as default};
