import{_ as e,o as n,c as a,e as s}from"./app.38bd00bc.js";var i="/images/TIL/Infra-Kubernetes/01-cluster.svg";const r={},d=s('<h1 id="_01-\u110F\u116E\u1107\u1165\u1102\u1166\u1110\u1175\u1109\u1173\u1105\u1161\u11AB" tabindex="-1"><a class="header-anchor" href="#_01-\u110F\u116E\u1107\u1165\u1102\u1166\u1110\u1175\u1109\u1173\u1105\u1161\u11AB" aria-hidden="true">#</a> 01. \uCFE0\uBC84\uB124\uD2F0\uC2A4\uB780?</h1><p><img src="'+i+`" alt="/images/TIL/Infra-Kubernetes/01-cluster.svg"></p><p>Containerized application\uC744 \uAD00\uB9AC\uD574\uC8FC\uB294 \uC624\uD508\uC18C\uC2A4 \uD50C\uB7AB\uD3FC\uC774\uB2E4.</p><p>container\uB97C \uAC1C\uBC1C\uC790\uAC00 \uC9C1\uC811 \uAD00\uB9AC\uD558\uB294\uAC8C \uC544\uB2C8\uB77C, k8s\uC5D0\uAC8C \uC5B4\uB5A4 \uBC29\uC2DD\uC73C\uB85C \uAD00\uB9AC\uD558\uB77C\uACE0 \uC9C0\uC2DC\uB97C \uB0B4\uB9AC\uBA74 k8s\uAC00 \uB300\uC2E0 \uAD00\uB9AC\uB97C \uB2F4\uB2F9\uD558\uB294 \uBC29\uC2DD\uC774\uB2E4.</p><p>\uC5EC\uAE30\uC11C \uC774\uC57C\uAE30\uD558\uB294 \uAD00\uB9AC\uC5D0\uB294 \uBC30\uD3EC\uBC29\uC2DD, scailing, network \uB4F1\uB4F1 \uB2E4\uC591\uD55C \uAE30\uB2A5\uB4E4\uC744 \uD3EC\uD568\uD55C\uB2E4.</p><p>\uAE30\uC874\uC5D0\uB294 \uCEE8\uD14C\uC774\uB108\uB97C \uAD00\uB9AC\uD560 \uB54C \uCEE8\uD14C\uC774\uB108 \uAC2F\uC218\uB97C \uB298\uB9AC\uAC70\uB098 scailing\uC744 \uC801\uC6A9\uD558\uAC70\uB098 \uB2E4\uB978 \uAE30\uB2A5\uC744 \uCD94\uAC00\uD560 \uB54C \uD574\uB2F9 \uC791\uC5C5\uB4E4\uC744 \uCEE8\uD14C\uC774\uB108\uC5D0 \uC9C1\uC811 \uAC00\uD574\uC918\uC57C \uD588\uB2E4.</p><p>\uADF8\uC5D0 \uBE44\uD574 \uCFE0\uBC84\uB124\uD2F0\uC2A4\uB294 \uBA85\uB839\uC744 \uC804\uB2EC\uBC1B\uC73C\uBA74 \uCEE8\uD14C\uC774\uB108\uB4E4\uC5D0\uAC8C \uC54C\uC544\uC11C \uBA85\uB839\uC744 \uC9C0\uC18D\uC801\uC73C\uB85C \uB0B4\uB9AC\uBA74\uC11C \uAD00\uB9AC\uD574\uC900\uB2E4.</p><h2 id="core-components" tabindex="-1"><a class="header-anchor" href="#core-components" aria-hidden="true">#</a> Core Components</h2><h3 id="cluster" tabindex="-1"><a class="header-anchor" href="#cluster" aria-hidden="true">#</a> Cluster</h3><p>\uB178\uB4DC \uBA38\uC2E0, \uC6CC\uCEE4\uB178\uB4DC, \uB9C8\uC2A4\uD130 \uB178\uB4DC, \uBC30\uD3EC \uD639\uC740 \uC6D0\uD558\uB294 \uCD5C\uC885 \uC0C1\uD0DC\uB97C \uAD6C\uC131\uD558\uB294 \uBAA8\uB4E0 \uAC83\uC758 \uCEEC\uB809\uC158 \uC138\uD2B8.</p><h3 id="nodes" tabindex="-1"><a class="header-anchor" href="#nodes" aria-hidden="true">#</a> Nodes</h3><p>\uD074\uB7EC\uC2A4\uD130\uC640 \uD1B5\uC2E0\uD558\uAC70\uB098 \uD074\uB7EC\uC2A4\uD130 \uB0B4\uC5D0\uC11C \uD1B5\uC2E0\uD558\uB294 \uBB3C\uB9ACor\uAC00\uC0C1 \uBA38\uC2E0</p><h3 id="control-plane" tabindex="-1"><a class="header-anchor" href="#control-plane" aria-hidden="true">#</a> Control Plane</h3><p>\uD074\uB7EC\uC2A4\uD130 \uB0B4\uBD80 \uC694\uC18C\uB4E4\uC744 \uC81C\uC5B4\uD558\uB294 \uC5ED\uD560. \uD074\uB7EC\uC2A4\uD130 \uB0B4\uC758 \uB9C8\uC2A4\uD130 \uB178\uB4DC\uB4E4\uC758 \uC9D1\uD569\uC774\uB77C\uACE0 \uBCF4\uBA74 \uB41C\uB2E4.</p><h3 id="master-node" tabindex="-1"><a class="header-anchor" href="#master-node" aria-hidden="true">#</a> Master Node</h3><p>\uD074\uB7EC\uC2A4\uD130 \uB0B4\uC758 \uBAA8\uB4E0 \uC6CC\uCEE4 \uB178\uB4DC\uB97C \uAC78\uCCD0 pod\uB97C \uAD00\uB9AC\uD568</p><ul><li>API Server: \uC6CC\uCEE4\uC640 \uB9C8\uC2A4\uD130 \uB178\uB4DC\uAC04\uC758 \uD1B5\uC2E0\uC744 \uC704\uD55C \uCE74\uC6B4\uD130 \uD30C\uD2B8(\uB300\uC751\uC790)</li><li>Scheduler: \uD3EC\uB4DC\uB97C \uAD00\uCC30\uD558\uACE0 \uC0C8 \uD3EC\uB4DC\uAC00 \uC0DD\uC131\uB418\uC5B4\uC57C \uD558\uB294 \uC6CC\uCEE4 \uB178\uB4DC\uB97C \uC120\uD0DD\uD558\uB294 \uC77C\uC744 \uB2F4\uB2F9\uD558\uB294 \uC2A4\uCF00\uC974\uB7EC. \uC6CC\uCEE4 \uB178\uB4DC\uC5D0\uAC8C \uBB34\uC5C7\uC744 \uD574\uC57C \uD558\uB294\uC9C0 api \uC11C\uBC84\uC5D0 \uC54C\uB9AC\uB294 \uC5ED\uD560\uC744 \uD568</li><li>Kube-Controller-Manager: \uC6CC\uCEE4 \uB178\uB4DC \uC804\uCCB4\uB97C \uAC10\uC2DC\uD558\uACE0 \uC81C\uC5B4\uD558\uBA70 \uC801\uB2F9\uD55C \uC218\uC758 \uD3EC\uB4DC\uB97C \uAC00\uB3D9 \uC911\uC5D0 \uC788\uB294\uC9C0 \uD655\uC778\uD558\uB294 \uC5ED\uD560\uC744 \uD568.</li><li>Cloud-Contoller-Manager: Kube-Controller-Manager\uC640 \uB3D9\uC77C\uD55C \uC77C\uC744 \uD558\uC9C0\uB9CC , \uD074\uB77C\uC6B0\uB4DC \uD504\uB85C\uBC14\uC774\uB354\uC5D0\uAC8C \uCFE0\uBC84\uB124\uD2F0\uC2A4\uC5D0\uC11C \uC2E4\uD589\uD574\uC57C \uD560 \uBA85\uB839\uC744 \uBC88\uC5ED\uD574\uC900\uB2E4.</li></ul><h3 id="worker-node" tabindex="-1"><a class="header-anchor" href="#worker-node" aria-hidden="true">#</a> Worker Node</h3><p>pod\uB97C \uC2E4\uD589\uD558\uB294 \uB178\uB4DC. \uC989, application\uC744 \uC2E4\uD589\uD558\uB294 \uB178\uB4DC</p><p>\uC77C\uBC18\uC801\uC73C\uB85C\uB294 pod\uC5D0\uC11C \uC2E4\uD589\uB418\uB294 \uCEE8\uD14C\uC774\uB108\uB97C \uC0DD\uC131\uD558\uAE30 \uC704\uD558\uC5EC docker \uB610\uD55C \uC6CC\uCEE4\uB178\uB4DC\uC5D0 \uD3EC\uD568\uC2DC\uCF1C\uB193\uB294\uB2E4.</p><ul><li>pod: \uC6CC\uCEE4\uB178\uB4DC \uD558\uB098\uC5D0 \uC5EC\uB7EC\uAC1C\uC758 pod\uC744 \uAC00\uC9C8 \uC218 \uC788\uB2E4.</li><li>kubelet: \uC6CC\uCEE4\uB178\uB4DC\uC5D0 \uD3EC\uD568\uB418\uC5B4 \uC788\uC73C\uBA70 \uB9C8\uC2A4\uD130 \uB178\uB4DC\uC640 \uD1B5\uC2E0\uD558\uC5EC \uB9C8\uC2A4\uD130 \uB178\uB4DC\uAC00 \uD574\uB2F9 \uC6CC\uCEE4 \uB178\uB4DC\uC758 \uD3EC\uB4DC\uB97C \uC81C\uC5B4\uD560 \uC218 \uC788\uB3C4\uB85D \uD568</li><li>kube-proxy: \uD5C8\uC6A9\uB41C \uD2B8\uB798\uD53D\uB9CC\uC774 \uC6CC\uCEE4\uB178\uB4DC\uC5D0 \uB4E4\uC5B4\uC624\uAC70\uB098 \uB5A0\uB0A0 \uC218 \uC788\uAC8C \uAD00\uB9AC\uD568</li></ul><h3 id="pod" tabindex="-1"><a class="header-anchor" href="#pod" aria-hidden="true">#</a> Pod</h3><p>\uCFE0\uBC84\uB124\uD2F0\uC2A4\uC5D0\uC11C \uC5EC\uB7EC\uAC1C\uC758 \uCEE8\uD14C\uC774\uB108\uB97C \uAC00\uC9C8 \uC218 \uC788\uB294 \uAC00\uC7A5 \uC791\uC740 \uB2E8\uC704</p><p>\uC77C\uBC18\uC801\uC73C\uB85C\uB294 pod \uD558\uB098\uC5D0 \uCEE8\uD14C\uC774\uB108 \uD558\uB098\uB9CC \uC0AC\uC6A9\uD558\uC9C0\uB9CC, \uBC00\uC811\uD558\uAC8C \uAD00\uB828\uB41C \uCEE8\uD14C\uC774\uB108\uB4E4\uC744 pod \uB0B4\uBD80\uC5D0 \uAC19\uC774 \uB450\uAE30\uB3C4 \uD55C\uB2E4. \uC774\uB7F0 \uACBD\uC6B0\uC5D0\uB294 pod \uB0B4\uBD80\uC5D0 volumes \uB610\uD55C \uC124\uC815\uD558\uC5EC \uCEE8\uD14C\uC774\uB108\uB4E4\uC774 \uB370\uC774\uD130\uB97C \uC8FC\uACE0 \uBC1B\uC744 \uC218 \uC788\uAC8C \uD55C\uB2E4.</p><h3 id="services" tabindex="-1"><a class="header-anchor" href="#services" aria-hidden="true">#</a> Services</h3><h2 id="\u1106\u1167\u11BC\u1105\u1167\u11BC\u110B\u1165" tabindex="-1"><a class="header-anchor" href="#\u1106\u1167\u11BC\u1105\u1167\u11BC\u110B\u1165" aria-hidden="true">#</a> \uBA85\uB839\uC5B4</h2><div class="language-bash ext-sh line-numbers-mode"><pre class="language-bash"><code><span class="token comment"># \uBC84\uC804\uD655\uC778</span>
minikube version

<span class="token comment"># \uAC00\uC0C1\uBA38\uC2E0 \uC2DC\uC791 (intel \uBC84\uC804)</span>
minikube start --driver<span class="token operator">=</span>hyperkit
<span class="token comment"># \uAC00\uC0C1\uBA38\uC2E0 \uC2DC\uC791 (M1 \uBC84\uC804 - docker desktop \uC124\uCE58 \uD544\uC694)</span>
minikube start --driver<span class="token operator">=</span>docker
<span class="token comment"># driver \uC5D0\uB7EC\uAC00 \uBC1C\uC0DD\uD558\uBA74 virtual box\uB97C \uC0AC\uC6A9</span>
minikube start --driver<span class="token operator">=</span>virtualbox
<span class="token comment"># \uD2B9\uC815 k8s \uBC84\uC804 \uC2E4\uD589</span>
minikube start --kubernetes-version<span class="token operator">=</span>v1.23.1

<span class="token comment"># \uC0C1\uD0DC\uD655\uC778</span>
minikube status

<span class="token comment"># \uC815\uC9C0</span>
minikube stop

<span class="token comment"># \uC0AD\uC81C</span>
minikube delete

<span class="token comment"># ssh \uC811\uC18D</span>
minikube <span class="token function">ssh</span>

<span class="token comment"># ip \uD655\uC778</span>
minikube <span class="token function">ip</span>

<span class="token comment"># docker desktop \uC774\uC6A9\uC2DC\uC5D0 \uD574\uB2F9 \uBA85\uB839\uC5B4\uB85C \uC11C\uBE44\uC2A4 \uC811\uC18D \uAC00\uB2A5</span>
minikube <span class="token function">service</span> <span class="token punctuation">[</span>service name<span class="token punctuation">]</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div>`,27),l=[d];function o(c,t){return n(),a("div",null,l)}var u=e(r,[["render",o],["__file","01. \uC2DC\uC791\uD558\uAE30.html.vue"]]);export{u as default};
