import{_ as t,o as e,c as r,e as i}from"./app.96321e0f.js";var n="/images/TIL/CS-OS/5-\uC0C1\uD0DC\uBAA8\uB378.png";const a={},l=i('<h1 id="\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-process" tabindex="-1"><a class="header-anchor" href="#\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-process" aria-hidden="true">#</a> \uD504\uB85C\uC138\uC2A4 (Process)</h1><h2 id="\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173\u1105\u1161\u11AB" tabindex="-1"><a class="header-anchor" href="#\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173\u1105\u1161\u11AB" aria-hidden="true">#</a> \uD504\uB85C\uC138\uC2A4\uB780?</h2><p><u>\uC2E4\uD589 \uC911\uC778 \uD504\uB85C\uADF8\uB7A8</u>\uC744 \uC758\uBBF8\uD55C\uB2E4.</p><p>\uD504\uB85C\uC138\uC2A4\uB294 \uC6B4\uC601\uCCB4\uC81C\uB85C\uBD80\uD130 \uC790\uC6D0\uC744 \uD560\uB2F9\uBC1B\uC544 \uB3D9\uC791\uD55C\uB2E4.</p><div class="custom-container tip"><p class="custom-container-title">\uD504\uB85C\uADF8\uB7A8\uACFC \uD504\uB85C\uC138\uC2A4\uC758 \uCC28\uC774</p><ul><li><strong>\uD504\uB85C\uADF8\uB7A8</strong>: \uB3D9\uC791\uC744 \uD558\uC9C0 \uC54A\uB294 <strong>\uC815\uC801, \uC218\uB3D9\uC801</strong> \uAC1C\uCCB4</li><li><strong>\uD504\uB85C\uC138\uC2A4</strong>: \uB3D9\uC791\uC744 \uD558\uB294 <strong>\uB2A5\uB3D9\uC801</strong> \uAC1C\uCCB4</li><li><strong>\uC790\uC6D0</strong>: CPU, \uBA54\uBAA8\uB9AC, I/O\uC7A5\uCE58, \uD30C\uC77C \uB4F1</li><li><strong>\uB3D9\uC791</strong>: CPU\uAC00 \uD504\uB85C\uC138\uC2A4\uC758 \uBA85\uB839\uC744 \uC2E4\uD589</li></ul></div><br><h2 id="\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173\u110B\u116A-\u110B\u116E\u11AB\u110B\u1167\u11BC\u110E\u1166\u110C\u1166" tabindex="-1"><a class="header-anchor" href="#\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173\u110B\u116A-\u110B\u116E\u11AB\u110B\u1167\u11BC\u110E\u1166\u110C\u1166" aria-hidden="true">#</a> \uD504\uB85C\uC138\uC2A4\uC640 \uC6B4\uC601\uCCB4\uC81C</h2><h3 id="\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1100\u116A\u11AB\u1105\u1175\u110C\u1161\u110B\u1174-\u110B\u1167\u11A8\u1112\u1161\u11AF" tabindex="-1"><a class="header-anchor" href="#\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1100\u116A\u11AB\u1105\u1175\u110C\u1161\u110B\u1174-\u110B\u1167\u11A8\u1112\u1161\u11AF" aria-hidden="true">#</a> \uD504\uB85C\uC138\uC2A4 \uAD00\uB9AC\uC790\uC758 \uC5ED\uD560</h3><ul><li>\uD504\uB85C\uC138\uC2A4\uB97C \uC0DD\uC131 \uBC0F \uC0AD\uC81C</li><li>\uD504\uB85C\uC138\uC2A4 \uC2E4\uD589<u>(CPU \uD560\uB2F9)\uC744 \uC704\uD55C \uC2A4\uCF00\uC904 \uACB0\uC815</u></li><li>\uD504\uB85C\uC138\uC2A4\uC758 \uC0C1\uD0DC\uB97C \uAD00\uB9AC\uD558\uBA74 <u>\uC0C1\uD0DC\uB97C \uAD00\uB9AC</u>\uD558\uBA70 <u>\uC0C1\uD0DC \uC804\uC774\uB97C \uCC98\uB9AC</u></li></ul><br><h2 id="\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173\u110B\u1174-\u1109\u1161\u11BC\u1110\u1162" tabindex="-1"><a class="header-anchor" href="#\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173\u110B\u1174-\u1109\u1161\u11BC\u1110\u1162" aria-hidden="true">#</a> \uD504\uB85C\uC138\uC2A4\uC758 \uC0C1\uD0DC</h2><h3 id="_5-\u1109\u1161\u11BC\u1110\u1162-\u1106\u1169\u1103\u1166\u11AF" tabindex="-1"><a class="header-anchor" href="#_5-\u1109\u1161\u11BC\u1110\u1162-\u1106\u1169\u1103\u1166\u11AF" aria-hidden="true">#</a> 5-\uC0C1\uD0DC \uBAA8\uB378</h3><img src="'+n+'" style="background-color:white;"><ol><li><strong>\uC0DD\uC131</strong>: \uCC98\uC74C \uC791\uC5C5\uC774 <strong>\uC2DC\uC2A4\uD15C\uC5D0 \uC8FC\uC5B4\uC9C4 \uC0C1\uD0DC</strong></li><li><strong>\uC900\uBE44</strong>: \uC2E4\uD589 \uC900\uBE44\uAC00 \uB418\uC5B4 <strong>CPU \uD560\uB2F9\uC744 \uAE30\uB2E4\uB9AC\uB294 \uC0C1\uD0DC</strong></li><li><strong>\uB300\uAE30</strong>: \uD504\uB85C\uC138\uC2A4\uAC00 \uD2B9\uC815 \uC790\uC6D0\uC744 \uD560\uB2F9\uBC1B\uC744 \uB54C\uAE4C\uC9C0 \uB610\uB294 I/O \uC791\uC5C5\uC774 \uB05D\uB0A0 \uB54C\uAE4C\uC9C0 <strong>\uC791\uC5C5\uC774 \uBCF4\uB958\uB418\uB294 \uC0C1\uD0DC</strong></li><li><strong>\uC2E4\uD589</strong>: <strong>\uD504\uB85C\uC138\uC2A4\uAC00 \uCC98\uB9AC\uB418\uB294 \uC0C1\uD0DC</strong></li><li><strong>\uC885\uB8CC</strong>: \uBAA8\uB4E0 \uCC98\uB9AC\uAC00 \uC644\uB8CC\uB418\uC5B4 <strong>\uC0AC\uC6A9\uC790\uC5D0\uAC8C \uBC18\uD658\uB418\uB294 \uC0C1\uD0DC</strong></li></ol><br><h2 id="\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u110C\u1166\u110B\u1165-\u1107\u1173\u11AF\u1105\u1169\u11A8-process-control-block-pcb" tabindex="-1"><a class="header-anchor" href="#\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u110C\u1166\u110B\u1165-\u1107\u1173\u11AF\u1105\u1169\u11A8-process-control-block-pcb" aria-hidden="true">#</a> \uD504\uB85C\uC138\uC2A4 \uC81C\uC5B4 \uBE14\uB85D (Process Control Block, PCB)</h2><ul><li>\uAC01 \uD504\uB85C\uC138\uC2A4\uB9C8\uB2E4 \uC874\uC7AC\uD568</li><li>\uD504\uB85C\uC138\uC2A4\uB97C \uAD00\uB9AC\uD574\uC8FC\uBA70 \uD504\uB85C\uC138\uC2A4\uC758 \uC815\uBCF4\uB97C \uBCF4\uAD00</li><li>\uD504\uB85C\uC138\uC2A4\uAC00 \uC9C4\uD584\uD568\uC5D0 \uB530\uB77C \uB0B4\uC6A9\uC774 \uBCC0\uACBD\uB428</li></ul><h3 id="pcb\u110B\u1166\u1109\u1165-\u1100\u1161\u110C\u1175\u1100\u1169-\u110B\u1175\u11BB\u1102\u1173\u11AB-\u110C\u116E\u110B\u116D-\u110C\u1165\u11BC\u1107\u1169\u1103\u1173\u11AF" tabindex="-1"><a class="header-anchor" href="#pcb\u110B\u1166\u1109\u1165-\u1100\u1161\u110C\u1175\u1100\u1169-\u110B\u1175\u11BB\u1102\u1173\u11AB-\u110C\u116E\u110B\u116D-\u110C\u1165\u11BC\u1107\u1169\u1103\u1173\u11AF" aria-hidden="true">#</a> PCB\uC5D0\uC11C \uAC00\uC9C0\uACE0 \uC788\uB294 \uC8FC\uC694 \uC815\uBCF4\uB4E4</h3><table><thead><tr><th style="text-align:center;">Title</th><th style="text-align:center;">Description</th></tr></thead><tbody><tr><td style="text-align:center;">\uD504\uB85C\uC138\uC2A4 \uC0C1\uD0DC</td><td style="text-align:center;">\uD504\uB85C\uC138\uC2A4\uC758 \uC0C1\uD0DC</td></tr><tr><td style="text-align:center;">\uD504\uB85C\uC138\uC2A4 \uBC88\uD638 (PID)</td><td style="text-align:center;">\uD504\uB85C\uC138\uC2A4\uC758 \uBC88\uD638</td></tr><tr><td style="text-align:center;">\uD504\uB85C\uADF8\uB7A8 \uCE74\uC6B4\uD130 (PC)</td><td style="text-align:center;">\uD604\uC7AC \uD504\uB85C\uC138\uC2A4 \uC911\uC5D0\uC11C \uCC98\uB9AC\uB418\uACE0 \uC788\uB294\uAC8C \uBB34\uC5C7\uC778\uC9C0,<br>\uB2E4\uC74C\uC5D0 \uC2E4\uD589 \uB420 \uD504\uB85C\uC138\uC2A4\uAC00 \uBB34\uC5C7\uC778\uC9C0\uC5D0 \uB300\uD55C \uC815\uBCF4</td></tr><tr><td style="text-align:center;">\uB808\uC9C0\uC2A4\uD130\uB4E4</td><td style="text-align:center;">\uD574\uB2F9 \uD504\uB85C\uC138\uC2A4\uC758 \uC9C4\uD589\uC815\uBCF4\uB97C \uC800\uC7A5\uD568.<br>cpu\uC5D0 \uD504\uB85C\uC138\uC2A4\uB97C \uC7AC\uD560\uB2F9\uD560 \uB54C \uC9C4\uD589\uC815\uBCF4\uB97C \uB118\uACA8\uC11C \uD558\uB358 \uC791\uC5C5\uBD80\uD130 \uC7AC\uAC1C\uD560 \uC218 \uC788\uAC8C \uD574\uC90C</td></tr></tbody></table><h2 id="\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173\u110B\u1174-\u1109\u1162\u11BC\u1109\u1165\u11BC\u1100\u116A-\u110C\u1169\u11BC\u1105\u116D" tabindex="-1"><a class="header-anchor" href="#\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173\u110B\u1174-\u1109\u1162\u11BC\u1109\u1165\u11BC\u1100\u116A-\u110C\u1169\u11BC\u1105\u116D" aria-hidden="true">#</a> \uD504\uB85C\uC138\uC2A4\uC758 \uC0DD\uC131\uACFC \uC885\uB8CC</h2><h3 id="_1-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1109\u1162\u11BC\u1109\u1165\u11BC-\u110C\u1161\u11A8\u110B\u1165\u11B8" tabindex="-1"><a class="header-anchor" href="#_1-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1109\u1162\u11BC\u1109\u1165\u11BC-\u110C\u1161\u11A8\u110B\u1165\u11B8" aria-hidden="true">#</a> 1) \uD504\uB85C\uC138\uC2A4 \uC0DD\uC131 \uC791\uC5C5</h3><ol><li>\uD504\uB85C\uC138\uC2A4\uC758 \uC774\uB984(\uBC88\uD638, PID) \uACB0\uC815</li><li>\uC900\uBE44 queue\uC5D0 \uC0BD\uC785</li><li>\uCD08\uAE30 \uC6B0\uC120\uC21C\uC704 \uBD80\uC5EC</li><li>\uD504\uB85C\uC138\uC2A4 \uC81C\uC5B4 \uBE14\uB85D(PCB) \uC0DD\uC131</li></ol><h3 id="_1-2-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1109\u1162\u11BC\u1109\u1165\u11BC-\u1109\u1175\u1109\u1173\u1110\u1166\u11B7-\u1112\u1169\u110E\u116E\u11AF" tabindex="-1"><a class="header-anchor" href="#_1-2-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1109\u1162\u11BC\u1109\u1165\u11BC-\u1109\u1175\u1109\u1173\u1110\u1166\u11B7-\u1112\u1169\u110E\u116E\u11AF" aria-hidden="true">#</a> 1-2) \uD504\uB85C\uC138\uC2A4 \uC0DD\uC131 \uC2DC\uC2A4\uD15C \uD638\uCD9C</h3><ul><li><strong>\uD558\uB098\uC758 \uD504\uB85C\uC138\uC2A4</strong>\uAC00 <strong>\uD504\uB85C\uC138\uC2A4 \uC0DD\uC131 \uC2DC\uC2A4\uD15C\uC744 \uD638\uCD9C</strong>\uD558\uC5EC <strong>\uC0C8\uB85C\uC6B4 \uD504\uB85C\uC138\uC2A4\uB97C \uC0DD\uC131</strong> (ex. <code>fork()</code>)</li><li><strong>\uD504\uB85C\uC138\uC2A4\uB294 \uCD5C\uC0C1\uC704 \uD504\uB85C\uC138\uC2A4\uBD80\uD130 \uC2DC\uC791\uD574\uC11C tree \uAD6C\uC870\uB85C \uC774\uB8E8\uC5B4\uC9D0</strong></li></ul><div class="custom-container tip"><p class="custom-container-title">TIP</p><ul><li><strong>\uC2DC\uC2A4\uD15C \uD504\uB85C\uC138\uC2A4(\uCD5C\uC0C1\uC704 \uD504\uB85C\uC138\uC2A4)</strong>: OS\uAC00 \uBD80\uD305\uD558\uBA74\uC11C \uC81C\uC77C \uBA3C\uC800 \uB9CC\uB4E4\uC5B4\uC9C0\uB294 \uD504\uB85C\uC138\uC2A4</li><li><strong>\uC0AC\uC6A9\uC790 \uD504\uB85C\uC138\uC2A4</strong>: \uC0AC\uC6A9\uC790\uAC00 \uB9CC\uB4E4\uC5B4\uB0B8 \uD504\uB85C\uC138\uC2A4</li><li><strong>\uBD80\uBAA8 \uD504\uB85C\uC138\uC2A4</strong>: \uD504\uB85C\uC138\uC2A4 \uC0DD\uC131 \uC2DC\uC2A4\uD15C \uD638\uCD9C\uC744 \uD558\uAC8C\uB418\uB294 \uD504\uB85C\uC138\uC2A4</li><li><strong>\uC790\uC2DD \uD504\uB85C\uC138\uC2A4</strong>: \uBD80\uBAA8 \uD504\uB85C\uC138\uC2A4\uC5D0 \uC758\uD574 \uC0DD\uC131\uB41C \uD504\uB85C\uC138\uC2A4</li></ul></div><h3 id="_1-3-\u1109\u1162\u11BC\u1109\u1165\u11BC\u1103\u116C\u1102\u1173\u11AB-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173\u110B\u1174-\u110C\u1161\u110B\u116F\u11AB" tabindex="-1"><a class="header-anchor" href="#_1-3-\u1109\u1162\u11BC\u1109\u1165\u11BC\u1103\u116C\u1102\u1173\u11AB-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173\u110B\u1174-\u110C\u1161\u110B\u116F\u11AB" aria-hidden="true">#</a> 1-3) \uC0DD\uC131\uB418\uB294 \uD504\uB85C\uC138\uC2A4\uC758 \uC790\uC6D0</h3><ul><li>\uC6B4\uC601\uCCB4\uC81C\uB85C\uBD80\uD130 \uC9C1\uC811 \uC5BB\uB294 \uACBD\uC6B0</li><li>\uBD80\uBAA8 \uD504\uB85C\uC138\uC2A4 \uC790\uC6D0\uC758 \uC77C\uBD80\uB97C \uC5BB\uB294 \uACBD\uC6B0</li></ul><div class="custom-container warning"><p class="custom-container-title">\uC65C \uBD80\uBAA8 \uD504\uB85C\uC138\uC2A4\uC758 \uC790\uC6D0\uC758 \uC77C\uBD80\uB97C \uBC1B\uC544\uC11C \uC0AC\uC6A9\uD560\uAE4C?</p><p>\uACFC\uB3C4\uD55C \uC790\uC2DD \uD504\uB85C\uC138\uC2A4 \uC0DD\uC131\uC5D0 \uB530\uB978 \uC2DC\uC2A4\uD15C \uACFC\uBD80\uD558 \uBC29\uC9C0\uB97C \uC704\uD574\uC11C!</p></div><h3 id="_2-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u110C\u1169\u11BC\u1105\u116D" tabindex="-1"><a class="header-anchor" href="#_2-\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u110C\u1169\u11BC\u1105\u116D" aria-hidden="true">#</a> 2) \uD504\uB85C\uC138\uC2A4 \uC885\uB8CC</h3><p><strong>\uD504\uB85C\uC138\uC2A4\uAC00 \uC885\uB8CC\uB418\uBA70 \uBD80\uBAA8 \uD504\uB85C\uC138\uC2A4\uC5D0\uAC8C \uC2E4\uD589\uACB0\uACFC\uB97C \uB418\uB3CC\uB824 \uC90C</strong><br> (\uD504\uB85C\uC138\uC2A4 \uC885\uB8CC \uC2DC\uC2A4\uD15C\uC740 \uBD80\uBAA8\uC5D0 \uC758\uD574\uC11C\uB9CC \uD638\uCD9C\uB41C\uB2E4!)</p><div class="custom-container tip"><p class="custom-container-title">\uD504\uB85C\uC138\uC2A4 \uC885\uB8CC \uBC1C\uC0DD \uC870\uAC74</p><ul><li>\uD504\uB85C\uC138\uC2A4\uC758 \uB9C8\uC9C0\uB9C9 \uBA85\uB839\uC774 \uC2E4\uD589\uC744 \uB9C8\uCE58\uB294 \uACBD\uC6B0</li><li>\uD504\uB85C\uC138\uC2A4 \uC885\uB8CC \uC2DC\uC2A4\uD15C \uD638\uCD9C\uC774 \uC77C\uC5B4\uB098\uB294 \uACBD\uC6B0 (ex. <code>exit()</code>)</li><li>\uC790\uC2DD\uC5D0\uAC8C \uD560\uB2F9\uB41C \uC790\uC6D0\uBCF4\uB2E4 \uB354 \uC0AC\uC6A9\uD558\uAC70\uB098 \uB354 \uC774\uC0C1 \uD544\uC694\uCE58 \uC54A\uC744 \uB54C</li></ul></div><br><h2 id="\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1100\u1161\u11AB\u110B\u1174-\u1100\u116A\u11AB\u1100\u1168" tabindex="-1"><a class="header-anchor" href="#\u1111\u1173\u1105\u1169\u1109\u1166\u1109\u1173-\u1100\u1161\u11AB\u110B\u1174-\u1100\u116A\u11AB\u1100\u1168" aria-hidden="true">#</a> \uD504\uB85C\uC138\uC2A4 \uAC04\uC758 \uAD00\uACC4</h2><table><thead><tr><th style="text-align:center;"></th><th style="text-align:center;">\uB3C5\uB9BD\uC801 \uD504\uB85C\uC138\uC2A4</th><th style="text-align:center;">\uC720\uAE30\uC801 \uD504\uB85C\uC138\uC2A4</th></tr></thead><tbody><tr><td style="text-align:center;">\uC758\uBBF8</td><td style="text-align:center;">\uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uC640 \uB3C5\uB9BD\uC801\uC784</td><td style="text-align:center;">\uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uC640 \uC601\uD5A5\uC744 \uC8FC\uACE0 \uBC1B\uC74C</td></tr><tr><td style="text-align:center;">\uD504\uB85C\uC138\uC2A4 \uC0C1\uD0DC</td><td style="text-align:center;">\uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uC640 \uC790\uC6D0\uC758 \uC0C1\uD0DC \uB4F1\uC744 \uACF5\uC720\uD558\uC9C0 \uC54A\uC74C</td><td style="text-align:center;">\uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uC640 \uC790\uC6D0\uC758 \uC0C1\uD0DC \uB4F1\uC744 \uACF5\uC720\uD568</td></tr><tr><td style="text-align:center;">\uC2E4\uD589</td><td style="text-align:center;">\uB3D9\uC77C\uD55C \uC785\uB825\uC5D0 \uB3D9\uC77C\uD55C \uACB0\uACFC\uB97C \uBCF4\uC7A5\uD568.<br>\uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uC640 \uBB34\uAD00\uD558\uAC8C \uC911\uB2E8 \uBC0F \uC7AC\uC2DC\uC791 \uAC00\uB2A5</td><td style="text-align:center;">\uB3D9\uC77C\uD55C \uC785\uB825\uC5D0 \uB2E4\uB978 \uACB0\uACFC\uAC00 \uB098\uC62C \uC218 \uC788\uC74C.<br>\uB2E4\uB978 \uACB0\uACFC\uAC00 \uB098\uC62C \uC218 \uC788\uAE30\uC5D0 \uC7AC\uC0DD \uBD88\uAC00\uB2A5</td></tr><tr><td style="text-align:center;">\uB370\uC774\uD130</td><td style="text-align:center;">\uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uC640 \uACF5\uC720\uD558\uC9C0 \uC54A\uC74C</td><td style="text-align:center;">\uB2E4\uB978 \uD504\uB85C\uC138\uC2A4\uC640 \uACF5\uC720\uD568</td></tr></tbody></table>',34),s=[l];function d(o,c){return e(),r("div",null,s)}var g=t(a,[["render",d],["__file","01. \uD504\uB85C\uC138\uC2A4.html.vue"]]);export{g as default};
