import{e as a}from"./app.6a6c9c31.js";import{_ as r}from"./plugin-vue_export-helper.21dcd24c.js";const e={},p=a('<h1 id="_1\u110C\u1161\u11BC-\u1101\u1162\u1101\u1173\u11BA\u1112\u1161\u11AB-\u110F\u1169\u1103\u1173" tabindex="-1"><a class="header-anchor" href="#_1\u110C\u1161\u11BC-\u1101\u1162\u1101\u1173\u11BA\u1112\u1161\u11AB-\u110F\u1169\u1103\u1173" aria-hidden="true">#</a> 1\uC7A5. \uAE68\uB057\uD55C \uCF54\uB4DC</h1><p>\uAE68\uB057\uD55C \uCF54\uB4DC\uB97C \uC791\uC131\uD558\uB294 \uBC29\uBC95\uC740 <strong>\uBC30\uC6B0\uAE30 \uC5B4\uB835\uB2E4</strong>.</p><p>\uB2E8\uC21C\uD788 \uC6D0\uCE59\uACFC \uD328\uD134\uC744 \uC548\uB2E4\uACE0 \uAE68\uB057\uD55C \uCF54\uB4DC\uAC00 \uB098\uC624\uC9C0 \uC54A\uB294\uB2E4.<br> (\uC0DD\uAC01\uD574\uBCF4\uBA74 \uD328\uD134\uC740 \uADF8\uC800 \uB3C4\uC6C0\uC744 \uC8FC\uAE30 \uC704\uD574 \uC774\uB7F0\uAC8C \uC788\uB2E4\uBFD0, \uC5B4\uB5A0\uD55C \uBC95\uCE59 \uAC19\uC740 \uAC83\uC774 \uC544\uB2C8\uB2E4...)</p><h2 id="\u110F\u1169\u1103\u1173\u1100\u1161-\u110C\u1169\u11AB\u110C\u1162\u1112\u1161\u1105\u1175\u1105\u1161" tabindex="-1"><a class="header-anchor" href="#\u110F\u1169\u1103\u1173\u1100\u1161-\u110C\u1169\u11AB\u110C\u1162\u1112\u1161\u1105\u1175\u1105\u1161" aria-hidden="true">#</a> \uCF54\uB4DC\uAC00 \uC874\uC7AC\uD558\uB9AC\uB77C</h2><p><strong>\uD504\uB85C\uADF8\uB798\uBC0D\uC740 \uC694\uAD6C\uC0AC\uD56D\uC744 \uBA85\uC2DC\uD558\uB294 \uC791\uC5C5</strong>\uC774\uB2E4.<br> \uB610\uD55C <strong>\uCF54\uB4DC\uB294 \uC694\uAD6C\uC0AC\uD56D\uC744 \uC0C1\uC138\uD788 \uD45C\uD604\uD558\uB294 \uC218\uB2E8</strong>\uC774\uB2E4.</p><p>\uC5B8\uC820\uAC00 \uCF54\uB4DC\uB97C \uC790\uB3D9\uC73C\uB85C \uC0DD\uC131\uD558\uB294 \uC2DC\uB300\uAC00 \uC624\uB354\uB77C\uB3C4 \uD504\uB85C\uADF8\uB798\uBA38\uB294 \uC0AC\uB77C\uC9C0\uC9C0 \uC54A\uB294\uB2E4\uACE0 \uD55C\uB2E4.</p><p>\uC774\uC720\uB97C \uC124\uBA85\uD558\uAE30 \uC704\uD574 \uC800\uC790\uB294 \uCF54\uB4DC\uB97C \uC218\uD559\uC5D0 \uBE44\uC720\uD55C\uB2E4. \uCF54\uB4DC\uAC00 \uC0AC\uB77C\uC9C4\uB2E4\uB294 \uAC83\uC740 \uBE44\uC815\uD615\uC801\uC778 \uC218\uD559\uC774 \uB098\uC624\uB9AC\uB77C \uAE30\uB300\uD558\uB294 \uC218\uD559\uC790\uC640 \uBE44\uC2B7\uD558\uB2E4\uACE0 \uD45C\uD604\uD55C\uB2E4.</p><p>\uB610\uD55C \uACE0\uAC1D\uC774 \uC6D0\uD558\uB294 \uC11C\uBE44\uC2A4\uB97C \uC815\uD655\uD788 \uAD6C\uD604\uD558\uAE30 \uC704\uD574\uC11C\uB294 \uC5B4\uB290\uC815\uB3C4 ai\uC758 \uB3C4\uC6C0\uC744 \uBE4C\uB9B4 \uC218 \uC788\uACA0\uC9C0\uB9CC, \uC774 \uB610\uD55C \uB9C8\uBB34\uB9AC\uB294 \uC0AC\uB78C\uC774 \uC9C1\uC811 \uD574\uC57C \uD55C\uB2E4.</p><p>\uACB0\uAD6D \uD504\uB85C\uADF8\uB798\uBA38\uB294 \uC0AC\uB77C\uC9C8 \uC218 \uC5C6\uB2E4. <s>(\uC544\uC9C1 \uCE58\uD0A8\uC9D1 \uC548\uD574\uB3C4 \uB418\uACA0\uB124 \u314E)</s></p><p>\uD504\uB85C\uADF8\uB798\uBC0D \uC5B8\uC5B4\uC5D0\uC11C \uCD94\uC0C1\uD654 \uC218\uC900\uC744 \uACC4\uC18D \uB192\uC544\uC9C8 \uAC83\uC774\uB2E4.</p><br><h2 id="\u1102\u1161\u1108\u1173\u11AB-\u110F\u1169\u1103\u1173" tabindex="-1"><a class="header-anchor" href="#\u1102\u1161\u1108\u1173\u11AB-\u110F\u1169\u1103\u1173" aria-hidden="true">#</a> \uB098\uC05C \uCF54\uB4DC</h2><p>\uB098\uC05C \uCF54\uB4DC\uB294 \uD68C\uC0AC\uB97C \uB9DD\uD558\uAC8C \uD560 \uC218\uB3C4 \uC788\uB2E4.</p><ul><li>\uBC84\uADF8\uAC00 \uB0A8\uC544 \uC788\uACE0, \uD504\uB85C\uADF8\uB7A8\uC774 \uC8FD\uB294 \uD69F\uC218\uAC00 \uB298\uC5B4\uB0A8</li><li>\uCD9C\uC2DC\uC5D0 \uBC14\uBE60 \uCF54\uB4DC\uB97C \uB9C8\uC74C\uB300\uB85C \uC9DC\uACE0, \uAE30\uB2A5\uC744 \uCD94\uAC00\uD560 \uC218\uB85D \uC5C9\uB9DD\uC774 \uB428</li></ul><p>\uCF54\uB4DC\uB97C \uC798 \uC9DC\uC57C\uD558\uB294 \uC88B\uC740 \uC774\uC720\uB294 \uC5EC\uB7EC\uAC00\uC9C0\uAC00 \uC788\uACA0\uC9C0\uB9CC \uADF8 \uC911\uC5D0 \uD558\uB098\uB97C \uAF3D\uC790\uBA74 <strong>\uB974\uBE14\uB791\uC758 \uBC95\uCE59</strong>\uC774 \uC788\uB2E4.</p><div class="custom-container tip"><p class="custom-container-title">\uB974\uBE14\uB791\uC758 \uBC95\uCE59</p><p>\uB098\uC911\uC740 \uACB0\uCF54 \uC624\uC9C0 \uC54A\uB294\uB2E4.</p></div><br><h2 id="\u1102\u1161\u1108\u1173\u11AB-\u110F\u1169\u1103\u1173\u1105\u1169-\u110E\u1175\u1105\u1173\u1102\u1173\u11AB-\u1103\u1162\u1100\u1161" tabindex="-1"><a class="header-anchor" href="#\u1102\u1161\u1108\u1173\u11AB-\u110F\u1169\u1103\u1173\u1105\u1169-\u110E\u1175\u1105\u1173\u1102\u1173\u11AB-\u1103\u1162\u1100\u1161" aria-hidden="true">#</a> \uB098\uC05C \uCF54\uB4DC\uB85C \uCE58\uB974\uB294 \uB300\uAC00</h2><h3 id="\u1100\u1162\u1107\u1161\u11AF-\u1109\u1169\u11A8\u1103\u1169\u1105\u1173\u11AF-\u110F\u1173\u1100\u1166-\u1104\u1165\u11AF\u110B\u1165\u1104\u1173\u1105\u1175\u11AB\u1103\u1161" tabindex="-1"><a class="header-anchor" href="#\u1100\u1162\u1107\u1161\u11AF-\u1109\u1169\u11A8\u1103\u1169\u1105\u1173\u11AF-\u110F\u1173\u1100\u1166-\u1104\u1165\u11AF\u110B\u1165\u1104\u1173\u1105\u1175\u11AB\u1103\u1161" aria-hidden="true">#</a> \uAC1C\uBC1C \uC18D\uB3C4\uB97C \uD06C\uAC8C \uB5A8\uC5B4\uB728\uB9B0\uB2E4.</h3><p>\uB098\uC05C \uCF54\uB4DC\uC77C\uC218\uB85D \uCF54\uB4DC\uB97C \uC77D\uB294 \uAC83\uC774 \uC544\uB2CC <strong>\uD574\uB3C5</strong>\uC744 \uD558\uAC8C \uB41C\uB2E4.</p><h3 id="\u1101\u1162\u1101\u1173\u11BA\u1112\u1161\u11AB-\u110F\u1169\u1103\u1173\u1105\u1173\u11AF-\u1106\u1161\u11AB\u1103\u1173\u1102\u1173\u11AB-\u1102\u1169\u1105\u1167\u11A8\u110B\u1175-\u1107\u1175\u110B\u116D\u11BC\u110B\u1173\u11AF-\u110C\u1165\u11AF\u1100\u1161\u11B7\u1112\u1161\u11AF-\u1108\u116E\u11AB-\u110B\u1161\u1102\u1175\u1105\u1161-\u110C\u1165\u11AB\u1106\u116E\u11AB\u1100\u1161\u1105\u1169\u1109\u1165-\u1109\u1161\u11AF\u110B\u1161\u1102\u1161\u11B7\u1102\u1173\u11AB\u1103\u1161" tabindex="-1"><a class="header-anchor" href="#\u1101\u1162\u1101\u1173\u11BA\u1112\u1161\u11AB-\u110F\u1169\u1103\u1173\u1105\u1173\u11AF-\u1106\u1161\u11AB\u1103\u1173\u1102\u1173\u11AB-\u1102\u1169\u1105\u1167\u11A8\u110B\u1175-\u1107\u1175\u110B\u116D\u11BC\u110B\u1173\u11AF-\u110C\u1165\u11AF\u1100\u1161\u11B7\u1112\u1161\u11AF-\u1108\u116E\u11AB-\u110B\u1161\u1102\u1175\u1105\u1161-\u110C\u1165\u11AB\u1106\u116E\u11AB\u1100\u1161\u1105\u1169\u1109\u1165-\u1109\u1161\u11AF\u110B\u1161\u1102\u1161\u11B7\u1102\u1173\u11AB\u1103\u1161" aria-hidden="true">#</a> \uAE68\uB057\uD55C \uCF54\uB4DC\uB97C \uB9CC\uB4DC\uB294 \uB178\uB825\uC774 \uBE44\uC6A9\uC744 \uC808\uAC10\uD560 \uBFD0 \uC544\uB2C8\uB77C \uC804\uBB38\uAC00\uB85C\uC11C \uC0B4\uC544\uB0A8\uB294\uB2E4.</h3><p>\uB098\uC05C \uCF54\uB4DC\uB294 \uC7AC\uC124\uACC4\uB97C \uD558\uAC8C \uB9CC\uB4E4\uACE0, \uC7AC\uC124\uACC4 \uB610\uD55C \uCD94\uAC00\uAE30\uB2A5\uC744 \uB530\uB77C\uC7A1\uB290\uB77C \uC7AC\uC7AC\uC124\uACC4\uB97C \uD558\uAC8C \uB420 \uC218\uB3C4 \uC788\uB2E4.</p><p>\uCC98\uC74C\uBD80\uD130 \uC798 \uC9DC\uC790.</p><p>\uD2B9\uD788 \uC694\uC998 \uC77C\uD558\uBA74\uC11C \uB290\uB080\uB2E4... <strong>\uCC98\uC74C\uBD80\uD130 \uC798 \uC9DC\uB194\uC57C \uB098\uC911\uC5D0 \uCF54\uB4DC\uB97C \uB2E4\uC2DC \uC77D\uB294 \uC2DC\uAC04\uC774 \uC815~\uB9D0 \uB9CE\uC774 \uC904\uC5B4\uB4E0\uB2E4.</strong></p><p>\uC77C\uB2E8 \uAC1C\uBC1C\uD558\uBA74\uC11C \uC0DD\uAC01\uD558\uB294\uAC8C \uC544\uB2C8\uB77C, \uAD6C\uCCB4\uC801\uC73C\uB85C \uAD6C\uC870\uB97C \uC5B4\uB5BB\uAC8C \uAC00\uC838\uAC08\uC9C0 \uC0DD\uAC01\uD558\uB294\uAC8C \uC911\uC694\uD558\uB2E4\uACE0 \uB290\uB080\uB2E4.</p><h3 id="\u1105\u1175\u1111\u1162\u11A8\u1110\u1169\u1105\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#\u1105\u1175\u1111\u1162\u11A8\u1110\u1169\u1105\u1175\u11BC" aria-hidden="true">#</a> \uB9AC\uD329\uD1A0\uB9C1...?</h3><p>\uAC1C\uC778\uC801\uC73C\uB85C... \uB9AC\uD329\uD1A0\uB9C1\uC740 \uAC1C\uBC1C\uC758 \uC911\uC810\uC0AC\uD56D\uC774\uB77C\uACE0 \uC0DD\uAC01\uD55C\uB2E4.</p><p>\uC544\uBB34\uB9AC \uCF54\uB4DC\uB97C \uD074\uB9B0\uD558\uAC8C \uC9DC\uB824\uACE0 \uB178\uB825\uD574\uB3C4 \uB9AC\uD329\uD1A0\uB9C1\uC740 \uD560 \uC218 \uBC16\uC5D0 \uC5C6\uB2E4.</p><p>\uCC45\uC5D0\uC11C\uB294 <code>\uBA54\uC11C\uB4DC \uCD94\uCD9C</code>\uC774\uB77C\uB294 \uB9AC\uD329\uD130\uB9C1 \uAE30\uBC95\uC744 \uC774\uC6A9\uD574\uC11C \uBA54\uC11C\uB4DC\uB97C \uC5EC\uB7EC\uAC1C\uB85C \uB098\uB204\uB294\uAC83\uB3C4 \uC88B\uB2E4\uACE0 \uD55C\uB2E4.</p><p><strong>\uC911\uBCF5</strong>\uACFC <strong>\uD45C\uD604\uB825</strong>\uB9CC \uC2E0\uACBD\uC368\uB3C4 \uAE68\uB057\uD55C \uCF54\uB4DC\uB77C\uB294 \uBAA9\uD45C\uC5D0 \uC131\uD07C \uB2E4\uAC00\uC120\uB2E4.</p><p>\uD2B9\uD788 \uAE00\uC4F4\uC774\uB294 \uCD94\uC0C1 \uD074\uB798\uC2A4\uB098 \uCD94\uC0C1 \uBA54\uC11C\uB4DC\uB97C \uB9CC\uB4E4\uC5B4 \uC2E4\uC81C \uAD6C\uD604\uC744 \uAC10\uC2F8\uB294\uB370, \uC774\uB807\uAC8C \uD558\uBA74 &#39;\uC9C4\uC9DC&#39; \uBB38\uC81C\uC5D0 \uC2E0\uACBD \uC4F8 \uC5EC\uC720\uAC00 \uC0DD\uAE34\uB2E4\uACE0 \uD55C\uB2E4.</p><p>\uC989 \uAC00\uC7A5 \uC911\uC694\uD55C \uAC83\uB4E4\uC740,</p><ul><li>\uC911\uBCF5 \uC904\uC774\uAE30</li><li>\uD45C\uD604\uB825 \uB192\uC774\uAE30</li><li>\uCD08\uBC18\uBD80\uD130 \uAC04\uB2E8\uD55C \uCD94\uC0C1\uD654 \uACE0\uB824\uD558\uAE30</li></ul><p>\uB77C\uACE0 \uD55C\uB2E4.</p><p>\uCD08\uBC18\uBD80\uD130 \uAC04\uB2E8\uD55C \uCD94\uC0C1\uD654\uB97C \uACE0\uB824\uD560\uC218\uB85D, \uBA54\uC11C\uB4DC\uAC04\uC758 \uC758\uC874\uC131\uC774 \uC801\uC740 \uC790\uADF8\uB9CC\uD55C \uD568\uC218\uB4E4\uC774 \uB9CE\uC774 \uC0DD\uAE34\uB2E4.</p><p>\uADF8\uAC83\uB4E4\uC744 \uC774\uC6A9\uD574\uC11C \uAE30\uB2A5\uAD50\uCCB4\uAC00 \uC26C\uC6CC\uC9C0\uB294 \uC5EC\uB7EC \uAE30\uB2A5\uC744 \uC218\uD589\uD558\uB294 \uBA54\uC11C\uB4DC\uB97C \uB9CC\uB4E4 \uC218 \uC788\uC744\uD150\uB370... \uAC1C\uC778\uC801\uC73C\uB85C \uAD49\uC7A5\uD788 \uC88B\uC740 \uBC29\uBC95\uC774\uB77C\uACE0 \uC0DD\uAC01\uD55C\uB2E4.</p><h3 id="\u110C\u1175\u11B7\u110C\u1161\u11A8-\u1100\u1161\u1102\u1173\u11BC\u1112\u1161\u1100\u1169-\u1103\u1161\u11AB\u1109\u116E\u11AB\u1112\u1161\u11AB-\u110F\u1169\u1103\u1173\u1100\u1161-\u1101\u1162\u1101\u1173\u11BA\u1112\u1161\u11AB-\u110F\u1169\u1103\u1173\u1103\u1161" tabindex="-1"><a class="header-anchor" href="#\u110C\u1175\u11B7\u110C\u1161\u11A8-\u1100\u1161\u1102\u1173\u11BC\u1112\u1161\u1100\u1169-\u1103\u1161\u11AB\u1109\u116E\u11AB\u1112\u1161\u11AB-\u110F\u1169\u1103\u1173\u1100\u1161-\u1101\u1162\u1101\u1173\u11BA\u1112\u1161\u11AB-\u110F\u1169\u1103\u1173\u1103\u1161" aria-hidden="true">#</a> \uC9D0\uC791 \uAC00\uB2A5\uD558\uACE0 \uB2E8\uC21C\uD55C \uCF54\uB4DC\uAC00 \uAE68\uB057\uD55C \uCF54\uB4DC\uB2E4.</h3><p>\uADF8\uB807\uB2E4. \uB9D0 \uADF8\uB300\uB85C\uB2E4. \uCF54\uB4DC\uB97C \uB3C5\uD574\uD560 \uC77C\uC774 \uC904\uC5B4\uB4E4\uC218\uB85D \uB2E8\uC21C\uD560\uC218\uB85D \uAE68\uB057\uD55C \uCF54\uB4DC\uB2E4.</p><p>\uB2E8\uC21C\uD574\uC11C \uC9DC\uAE30 \uC26C\uC6CC\uBCF4\uC77C\uC218\uB3C4 \uC788\uC9C0\uB9CC, \uADF8\uB807\uAC8C \uC9DC\uAE30\uAE4C\uC9C0 \uC5BC\uB9C8\uB098 \uB9CE\uC740 \uB178\uB825\uC774 \uD544\uC694\uD55C\uC9C0 \uC54C\uB4EF \uBAA8\uB97C\uB4EF \uD558\uB2E4....</p>',39);function i(t,n){return p}var s=r(e,[["render",i]]);export{s as default};
