import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";import{o as r,c as n,e as a}from"./app.404da4f0.js";const i={},s=a(`<h1 id="_02-graphql\u110B\u1174-\u1100\u116E\u1109\u1165\u11BC\u110B\u116D\u1109\u1169-\u110C\u1161\u11A8\u1109\u1165\u11BC\u110C\u116E\u11BC" tabindex="-1"><a class="header-anchor" href="#_02-graphql\u110B\u1174-\u1100\u116E\u1109\u1165\u11BC\u110B\u116D\u1109\u1169-\u110C\u1161\u11A8\u1109\u1165\u11BC\u110C\u116E\u11BC" aria-hidden="true">#</a> 02 - GraphQL\uC758 \uAD6C\uC131\uC694\uC18C (\uC791\uC131\uC911)</h1><p>GraphQL\uC740 \uD06C\uAC8C 3\uAC00\uC9C0 \uC694\uC18C\uB85C \uC774\uB8E8\uC5B4\uC838 \uC788\uB2E4.</p><ol><li><strong>\uCFFC\uB9AC &amp; \uBBA4\uD14C\uC774\uC158 (Query &amp; Mutation)</strong></li><li><strong>\uC2A4\uD0A4\uB9C8 &amp; \uD0C0\uC785 (Schema &amp; Type)</strong></li><li><strong>\uB9AC\uC878\uBC84 (Resolver)</strong></li></ol><br><h2 id="\u110F\u116F\u1105\u1175-\u1106\u1172\u1110\u1166\u110B\u1175\u1109\u1167\u11AB-query-mutation" tabindex="-1"><a class="header-anchor" href="#\u110F\u116F\u1105\u1175-\u1106\u1172\u1110\u1166\u110B\u1175\u1109\u1167\u11AB-query-mutation" aria-hidden="true">#</a> \uCFFC\uB9AC &amp; \uBBA4\uD14C\uC774\uC158 (query &amp; mutation)</h2><p>GraphQL\uC5D0\uC11C\uB294 \uC694\uCCAD\uC744 \uBCF4\uB0B4\uB294 \uBC29\uBC95\uC744 2\uAC00\uC9C0\uB85C \uC815\uC758\uD558\uB294\uB370, <strong>\uCFFC\uB9AC</strong>\uC640 <strong>\uBBA4\uD14C\uC774\uC158</strong> \uC774\uB2E4.<br> \uC774 \uB458\uC740 \uD070 \uCC28\uC774\uB294 \uC5C6\uB2E4. \uCFFC\uB9AC\uB294 \uC870\uD68C(R)\uC5D0 \uC0AC\uC6A9\uB418\uACE0, \uBBA4\uD14C\uC774\uC158\uC740 \uBCC0\uC870(CUD)\uC5D0 \uC0AC\uC6A9\uB418\uB294 <strong>\uAC1C\uB150\uC801\uC778 \uADDC\uC57D</strong>\uC77C \uBFD0\uC774\uB2E4.</p><br><h2 id="\u1103\u1166\u110B\u1175\u1110\u1165-\u110C\u1169\u1112\u116C" tabindex="-1"><a class="header-anchor" href="#\u1103\u1166\u110B\u1175\u1110\u1165-\u110C\u1169\u1112\u116C" aria-hidden="true">#</a> \uB370\uC774\uD130 \uC870\uD68C</h2><h3 id="\u110F\u116F\u1105\u1175-\u1103\u1161\u11AB\u1109\u116E\u11AB-\u110C\u1169\u1112\u116C" tabindex="-1"><a class="header-anchor" href="#\u110F\u116F\u1105\u1175-\u1103\u1161\u11AB\u1109\u116E\u11AB-\u110C\u1169\u1112\u116C" aria-hidden="true">#</a> \uCFFC\uB9AC - \uB2E8\uC21C \uC870\uD68C</h3><p><img src="https://github.com/l4279625/TIL/blob/main/images/TIL/GraphQL-02_01.PNG" alt="\uB2E8\uC21C \uC870\uD68C"></p><h3 id="\u110F\u116F\u1105\u1175-\u110B\u1175\u11AB\u110C\u1161\u1105\u1173\u11AF-\u1110\u1169\u11BC\u1112\u1161\u11AB-\u110C\u1169\u1112\u116C" tabindex="-1"><a class="header-anchor" href="#\u110F\u116F\u1105\u1175-\u110B\u1175\u11AB\u110C\u1161\u1105\u1173\u11AF-\u1110\u1169\u11BC\u1112\u1161\u11AB-\u110C\u1169\u1112\u116C" aria-hidden="true">#</a> \uCFFC\uB9AC - \uC778\uC790\uB97C \uD1B5\uD55C \uC870\uD68C</h3><p><img src="https://github.com/l4279625/TIL/blob/main/images/TIL/GraphQL-02_02.PNG" alt="\uC778\uC790\uB97C \uD1B5\uD55C \uC870\uD68C"></p><h3 id="\u1106\u1172\u1110\u1166\u110B\u1175\u1109\u1167\u11AB-\u1103\u1166\u110B\u1175\u1110\u1165-\u1107\u1167\u11AB\u110C\u1169" tabindex="-1"><a class="header-anchor" href="#\u1106\u1172\u1110\u1166\u110B\u1175\u1109\u1167\u11AB-\u1103\u1166\u110B\u1175\u1110\u1165-\u1107\u1167\u11AB\u110C\u1169" aria-hidden="true">#</a> \uBBA4\uD14C\uC774\uC158 - \uB370\uC774\uD130 \uBCC0\uC870</h3><div class="language-graphql ext-graphql line-numbers-mode"><pre class="language-graphql"><code>mutation CreateReviewForEpisode($ep: Episode!, $review: ReviewInput!) {
  createReview(episode: $ep, review: $review) {
    stars
    commentary
  }
}
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><br><h2 id="\u1109\u1173\u110F\u1175\u1106\u1161-\u1110\u1161\u110B\u1175\u11B8" tabindex="-1"><a class="header-anchor" href="#\u1109\u1173\u110F\u1175\u1106\u1161-\u1110\u1161\u110B\u1175\u11B8" aria-hidden="true">#</a> \uC2A4\uD0A4\uB9C8 &amp; \uD0C0\uC785</h2><p><a href="https://tech.kakao.com/2019/08/01/graphql-basic/" target="_blank" rel="noopener noreferrer">\uCE74\uCE74\uC624 \uAE30\uC220\uBE14\uB85C\uADF8</a>\uC5D0\uB294 \uB2E4\uC74C\uACFC \uAC19\uC774 \uC791\uC131\uB418\uC5B4 \uC788\uB2E4.</p><blockquote><p>\uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC2A4\uD0A4\uB9C8(Schema)\uB97C \uC791\uC131\uD560 \uB54C\uC758 \uACBD\uD5D8\uC744 SQL \uCFFC\uB9AC \uC791\uC131\uC73C\uB85C \uBE44\uC720\uD55C\uB2E4\uBA74, gql \uC2A4\uD0A4\uB9C8\uB97C \uC791\uC131\uD560 \uB54C\uC758 \uACBD\uD5D8\uC740 C, C++\uC758 \uD5E4\uB354\uD30C\uC77C \uC791\uC131\uC5D0 \uBE44\uC720\uAC00 \uB429\uB2C8\uB2E4.</p></blockquote><p>\uC989, <strong>\uC2A4\uD0A4\uB9C8</strong>\uB77C\uB294 \uAC83\uC740 GraphQL\uC5D0\uC11C \uC0AC\uC6A9\uB420 \uAC1D\uCCB4 \uD0C0\uC785\uC744 \uBBF8\uB9AC \uC9C0\uC815\uD574\uB193\uB294 \uC791\uC5C5\uC774\uB2E4. ex)</p><div class="language-graphQL ext-graphQL line-numbers-mode"><pre class="language-graphQL"><code>type Character {
    name: String!
    appearsIn: [Episode]!
}
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><ul><li>\uC624\uBE0C\uC81D\uD2B8 \uD0C0\uC785 : Character</li><li>\uD544\uB4DC : name, appearsIn</li><li>\uC2A4\uCE7C\uB77C \uD0C0\uC785 : String, ID, Int \uB4F1</li><li>\uB290\uB08C\uD45C(!) : \uD544\uC218 \uAC12\uC744 \uC758\uBBF8(non-nulalble)</li><li>\uB300\uAD04\uD638([, ]): \uBC30\uC5F4\uC744 \uC758\uBBF8</li></ul><br><h2 id="\u1105\u1175\u110C\u1169\u11AF\u1107\u1165-resolver" tabindex="-1"><a class="header-anchor" href="#\u1105\u1175\u110C\u1169\u11AF\u1107\u1165-resolver" aria-hidden="true">#</a> \uB9AC\uC878\uBC84 (Resolver)</h2><p>GraphQL\uC740 <strong>\uB370\uC774\uD130\uB97C \uAC00\uC838\uC624\uB294 \uAD6C\uCCB4\uC801\uC778 \uACFC\uC815\uC744 \uC9C1\uC811 \uAD6C\uD604\uD574\uC57C\uD55C\uB2E4.</strong><br> \uC774\uB97C <strong>\uB9AC\uC878\uBC84</strong>\uAC00 \uB2F4\uB2F9\uD558\uBA70 \uB9AC\uC878\uBC84\uB97C \uD1B5\uD574 \uB370\uC774\uD130\uBCA0\uC774\uC2A4, \uD30C\uC77C \uB4F1 <strong>\uB370\uC774\uD130 \uC885\uB958\uC5D0 \uC0C1\uAD00 \uC5C6\uC774 \uAD6C\uD604 \uAC00\uB2A5\uD558\uB2E4</strong>.<br> \uC2EC\uC9C0\uC5B4 http, SOAP \uAC19\uC740 \uD504\uB85C\uD1A0\uCF5C\uC744 \uD65C\uC6A9\uD558\uC5EC \uC6D0\uACA9 \uB370\uC774\uD130\uB97C \uAC00\uC838\uC62C \uC218\uB3C4 \uC788\uB2E4.</p><p>GraphQL\uC5D0\uC11C\uB294 \uAC01 \uD544\uB4DC\uB9C8\uB2E4 \uD558\uB098\uC758 \uD568\uC218\uAC00 \uC874\uC7AC\uD558\uBA70, \uC774 \uD568\uC218\uB294 \uB2E4\uC74C \uD0C0\uC785\uC744 \uBC18\uD658\uD558\uAC8C \uB418\uBA70 \uB9AC\uC878\uBC84\uB77C \uBD80\uB978\uB2E4. \uD544\uB4DC\uAC00 \uC2A4\uCE7C\uB77C\uAC12(primitive \uD0C0\uC785)\uC778 \uACBD\uC6B0\uC5D0\uB294 \uC5F0\uC1C4 \uD638\uCD9C\uC774 \uC911\uC9C0\uB418\uACE0, \uC885\uB8CC\uB41C\uB2E4.<br> (\uB9AC\uC878\uBC84 \uD638\uCD9C\uC740 DFS\uB85C \uAD6C\uD604\uB418\uC5B4\uC788\uC744\uAC83\uC73C\uB85C \uCD94\uCE21\uB41C\uB2E4.)</p><p>\uB3D9\uC77C\uD55C \uCFFC\uB9AC\uBA85\uC744 \uAC00\uC9C4 2\uAC00\uC9C0 \uCFFC\uB9AC\uB97C \uC0B4\uD3B4\uBCF4\uC790.</p><div class="language-graphql ext-graphql line-numbers-mode"><pre class="language-graphql"><code>{
  paymentsByUser(userId: 10) {
    id
    amount
  }
}

{
  paymentsByUser(userId: 10) {
    id
    amount
    user {
      name
      phoneNumber
    }
  }
}
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>\uD544\uB4DC 1\uAC1C\uB2F9 \uB9AC\uC878\uBC84 \uD568\uC218 1\uAC1C\uC774\uBBC0\uB85C \uBC11\uC758 \uCFFC\uB9AC\uAC00 \uB354 \uB9CE\uC740 \uD568\uC218\uB97C \uD638\uCD9C\uD558\uAC8C \uB41C\uB2E4. \uB610\uD55C \uAC01\uAC01\uC758 \uB9AC\uC878\uBC84 \uD568\uC218\uB294 \uB0B4\uBD80\uC801\uC73C\uB85C \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uCFFC\uB9AC\uAC00 \uC874\uC7AC\uD558\uB294\uB370, \uC774 \uB450\uAC00\uC9C0\uB97C \uC870\uD569\uD558\uBA74 <strong>\uCFFC\uB9AC\uC5D0 \uB9DE\uAC8C \uD544\uC694\uD55C\uB9CC\uD07C\uB9CC \uCD5C\uC801\uD654\uD558\uC5EC \uD638\uCD9C\uC774 \uAC00\uB2A5\uD558\uB2E4</strong>.</p><p>\uC989, \uB9AC\uC878\uBC84 \uCCB4\uC778\uC744 \uC798 \uD65C\uC6A9\uD558\uBA74 \uD6A8\uC728\uC801\uC778 \uC124\uACC4\uB97C \uD560 \uC218 \uC788\uB2E4\uB294 \uB73B\uC774\uB2E4.</p><br><h2 id="graphql-\u1107\u1175\u110C\u1173\u1102\u1175\u1109\u1173-\u1105\u1169\u110C\u1175\u11A8" tabindex="-1"><a class="header-anchor" href="#graphql-\u1107\u1175\u110C\u1173\u1102\u1175\u1109\u1173-\u1105\u1169\u110C\u1175\u11A8" aria-hidden="true">#</a> GraphQL \uBE44\uC988\uB2C8\uC2A4 \uB85C\uC9C1</h2><p><img src="https://github.com/l4279625/TIL/blob/main/images/TIL/GraphQL-02_03.png" alt="GraphQL \uBE44\uC988\uB2C8\uC2A4 \uB85C\uC9C1"></p><p>\uAD6C\uD604\uC2DC, \uBE44\uC988\uB2C8\uC2A4 \uB85C\uC9C1\uC740 \uC2E4\uC81C \uB9AC\uC878\uBC84 \uD568\uC218\uC5D0 \uB2F4\uC9C0 \uC54A\uB294\uB2E4.<br> \uADF8\uB7EC\uBBC0\uB85C \uC2E4\uC81C \uB85C\uC9C1\uC740 <strong>\uBE44\uC988\uB2C8\uC2A4 \uB85C\uC9C1 \uB808\uC774\uC5B4</strong>(\uB2E4\uB978 \uD30C\uC77C\uC758 \uB2E4\uB978 \uD568\uC218)\uC5D0 \uB2F4\uB294 \uAC83\uC774 \uAD8C\uC7A5\uB41C\uB2E4.</p><ul><li>\uC2E4\uC81C \uAD6C\uD604\uD55C \uBE44\uC988\uB2C8\uC2A4 \uB85C\uC9C1 \uAD00\uB828 \uB9AC\uC878\uBC84</li></ul><div class="language-graphql ext-graphql line-numbers-mode"><pre class="language-graphql"><code>requestPaymentSession: async (parent, { pgId, name, sex, birthDay, phoneNumber, amount, productName, ref 
}, context, info) =&gt; {
    # \uC2E4\uC81C \uB85C\uC9C1\uC740 \uBE44\uC988\uB2C8\uC2A4 \uB808\uC774\uC5B4\uB85C \uBCF4\uB0C4
    const ret = await requestPaymentSession({ pgId, name, birthDay, phoneNumber, sex, amount, productName, ref })

    return removeSymbol(ret)
},
requestPaymentApprove: async (parent, { sessionKey, authNumber
}, context, info) =&gt; {
    # \uC2E4\uC81C \uB85C\uC9C1\uC740 \uBE44\uC988\uB2C8\uC2A4 \uB808\uC774\uC5B4\uB85C \uBCF4\uB0C4
    const ret = await requestApprovePayment({ sessionKey, authNumber })

    return removeSymbol(ret)
}
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><br><h2 id="reference" tabindex="-1"><a class="header-anchor" href="#reference" aria-hidden="true">#</a> Reference</h2><p><a href="https://owin2828.github.io/devlog/2020/11/13/GraphQL-2.html" target="_blank" rel="noopener noreferrer">https://owin2828.github.io/devlog/2020/11/13/GraphQL-2.html</a><br><a href="https://graphql-kr.github.io/learn/queries/" target="_blank" rel="noopener noreferrer">https://graphql-kr.github.io/learn/queries/</a></p>`,38),l=[s];function d(t,h){return r(),n("div",null,l)}var c=e(i,[["render",d],["__file","02 - GraphQL\uC758 \uAD6C\uC131\uC694\uC18C.html.vue"]]);export{c as default};
