import{r as a,o,c as n,a as e,b as t,F as s,e as c,d as i}from"./app.512249a8.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";var p="/images/TIL/ETC-Docker/container-layer.png",l="/images/TIL/ETC-Docker/volume-container.png";const h={},_=c('<h1 id="_2-2-\u1103\u1169\u110F\u1165-\u1107\u1169\u11AF\u1105\u1172\u11B7" tabindex="-1"><a class="header-anchor" href="#_2-2-\u1103\u1169\u110F\u1165-\u1107\u1169\u11AF\u1105\u1172\u11B7" aria-hidden="true">#</a> 2-2. \uB3C4\uCEE4 \uBCFC\uB968</h1><p>\uCEE8\uD14C\uC774\uB108 \uB0B4\uBD80\uC758 \uBCC0\uACBD\uC0AC\uD56D\uC740 \uCEE8\uD14C\uC774\uB108 \uB0B4\uBD80\uC5D0 \uC800\uC7A5\uC774 \uB41C\uB2E4.</p><p><img src="'+p+'" alt="container-layer"></p><p>\uC704\uC758 \uADF8\uB9BC\uCC98\uB7FC <strong>\uCEE8\uD14C\uC774\uB108\uB97C \uC0DD\uC131\uD558\uBA74 \uC774\uBBF8\uC9C0\uB294 \uC77D\uAE30 \uC804\uC6A9\uC774 \uB418\uBA70</strong>,</p><p><strong>\uC774\uBBF8\uC9C0 \uB808\uC774\uC5B4 \uC704\uC5D0 \uCEE8\uD14C\uC774\uB108 \uB808\uC774\uC5B4\uAC00 \uCD94\uAC00\uB418\uB294 \uAD6C\uC870</strong>\uB2E4.</p><p>\uC800 \uCEE8\uD14C\uC774\uB108 \uB808\uC774\uC5B4\uC5D0 \uBCC0\uACBD\uB41C \uC815\uBCF4\uB97C \uC800\uC7A5\uD558\uB294\uB370, \uCEE8\uD14C\uC774\uB108\uAC00 \uC0AD\uC81C\uB41C\uB2E4\uBA74 \uC800 \uCEE8\uD14C\uC774\uB108 \uB808\uC774\uC5B4\uAC00 \uC0AD\uC81C\uB418\uB294 \uAC83\uC774\uB2E4.</p><p>\uB9CC\uC57D mysql \uCEE8\uD14C\uC774\uB108\uB97C \uB9CC\uB4E4\uC5B4\uC11C DB\uC5D0 \uC815\uBCF4\uB97C \uC800\uC7A5\uD588\uB2E4\uBA74,</p><p>\uADF8 \uC815\uBCF4 \uB610\uD55C \uCEE8\uD14C\uC774\uB108 \uB808\uC774\uC5B4\uC5D0 \uC800\uC7A5\uB420\uD14C\uB2C8, \uCEE8\uD14C\uC774\uB108\uAC00 \uC0AD\uC81C\uB418\uBA74 \uAC19\uC774 \uC0AD\uC81C\uB41C\uB2E4.</p><p>\uC774\uB97C \uBC29\uC9C0\uD558\uAE30 \uC704\uD574 \uCEE8\uD14C\uC774\uB108\uC758 \uB370\uC774\uD130\uB97C **\uC601\uC18D\uC801(Persistent)**\uC73C\uB85C \uD65C\uC6A9\uD560 \uC218 \uC788\uB294 \uBC29\uBC95\uC774 \uBA87 \uAC00\uC9C0 \uC788\uB2E4.</p><p>\uADF8 \uC911\uC5D0 \uAC00\uC7A5 \uD65C\uC6A9\uD558\uAE30 \uC26C\uC6B4 \uBC29\uBC95\uC774 <strong>\uBCFC\uB968</strong>\uC744 \uD65C\uC6A9\uD558\uB294 \uBC29\uBC95\uC774\uB2E4.</p><p>\uBCFC\uB968\uC744 \uD65C\uC6A9\uD558\uB294 \uBC29\uBC95\uC740 \uC5EC\uB7EC\uAC00\uC9C0\uAC00 \uC788\uB2E4.</p><ol><li><strong>\uD638\uC2A4\uD2B8\uC640 \uBCFC\uB968\uC744 \uACF5\uC720</strong></li><li><strong>\uBCFC\uB968 \uCEE8\uD14C\uC774\uB108\uB97C \uD65C\uC6A9</strong></li><li><strong>\uB3C4\uCEE4\uAC00 \uAD00\uB9AC\uD558\uB294 \uBCFC\uB968\uC744 \uC0DD\uC131</strong></li></ol><p>\uC774 \uC911\uC5D0\uC11C \uBA3C\uC800 \uD638\uC2A4\uD2B8\uC640 \uBCFC\uB968\uC744 \uACF5\uC720\uD558\uB294 \uBC29\uBC95\uC744 \uC54C\uC544\uBCF4\uC790.</p><h2 id="_1-\u1112\u1169\u1109\u1173\u1110\u1173-\u1107\u1169\u11AF\u1105\u1172\u11B7-\u1100\u1169\u11BC\u110B\u1172" tabindex="-1"><a class="header-anchor" href="#_1-\u1112\u1169\u1109\u1173\u1110\u1173-\u1107\u1169\u11AF\u1105\u1172\u11B7-\u1100\u1169\u11BC\u110B\u1172" aria-hidden="true">#</a> 1. \uD638\uC2A4\uD2B8 \uBCFC\uB968 \uACF5\uC720</h2><p>\uD638\uC2A4\uD2B8 \uBCFC\uB968 \uACF5\uC720\uB294 \uD638\uC2A4\uD2B8\uC640 \uCEE8\uD14C\uC774\uB108\uAC00 \uB514\uB809\uD1A0\uB9AC\uB97C \uACF5\uC720\uD558\uB294 \uBC29\uBC95\uC774\uB2E4.<br><strong>\uB450 \uB514\uB809\uD1A0\uB9AC\uB294 \uB3D9\uAE30\uD654 \uB418\uB294 \uAC83\uC774 \uC544\uB2C8\uB77C \uC644\uC804\uD788 \uAC19\uC740 \uB514\uB809\uD1A0\uB9AC\uB2E4.</strong></p><h3 id="\u110B\u1169\u11B8\u1109\u1167\u11AB-\u1109\u1161\u110B\u116D\u11BC\u1107\u1165\u11B8" tabindex="-1"><a class="header-anchor" href="#\u110B\u1169\u11B8\u1109\u1167\u11AB-\u1109\u1161\u110B\u116D\u11BC\u1107\u1165\u11B8" aria-hidden="true">#</a> \uC635\uC158 \uC0AC\uC6A9\uBC95</h3><ul><li><p><strong>windows</strong> : <code>-v C:/[\uD638\uC2A4\uD2B8\uC758 \uACF5\uC720 \uB514\uB809\uD1A0\uB9AC]:[\uCEE8\uD14C\uC774\uB108\uC758 \uACF5\uC720 \uB514\uB809\uD1A0\uB9AC]</code></p></li><li><p><strong>Linux</strong> : <code> -v /[\uD638\uC2A4\uD2B8\uC758 \uACF5\uC720 \uB514\uB809\uD1A0\uB9AC]:[\uCEE8\uD14C\uC774\uB108\uC758 \uACF5\uC720 \uB514\uB809\uD1A0\uB9AC]</code></p></li></ul><br><p>\uCEE8\uD14C\uC774\uB108 \uC0DD\uC131\uC2DC \uC704\uC758 \uC635\uC158\uC744 \uCD94\uAC00\uD574\uC8FC\uBA74 \uB41C\uB2E4.<br> windows\uC758 \uACBD\uC6B0\uC5D0\uB294 default\uAC00 C\uB4DC\uB77C\uC774\uBE0C\uB77C\uBA74 \uCD5C\uC0C1\uC704 \uB514\uB809\uD1A0\uB9AC\uB97C C\uB4DC\uB77C\uC774\uBE0C\uB85C \uC2DC\uC791\uD558\uBA74 \uB41C\uB2E4.</p><p>\uAE30\uBCF8\uC801\uC778 \uCEE8\uC149\uC740 <code>\uD638\uC2A4\uD2B8\uC640 \uCEE8\uD14C\uC774\uB108\uC758 \uB514\uB809\uD1A0\uB9AC\uB97C \uACF5\uC720\uD55C\uB2E4.</code> \uB77C\uACE0 \uC0DD\uAC01\uD558\uBA74 \uB418\uB294\uB370,<br> \uD638\uC2A4\uD2B8\uC5D0 \uACF5\uC720 \uB514\uB809\uD1A0\uB9AC\uAC00 \uC874\uC7AC\uD558\uC9C0 \uC54A\uB294\uB2E4\uBA74 \uC6B0\uB9AC\uAC00 \uC791\uC131\uD574\uC900 \uC635\uC158\uB300\uB85C \uB514\uB809\uD1A0\uB9AC\uB97C \uC0DD\uC131\uD574\uC900\uB2E4.</p><div class="custom-container tip"><p class="custom-container-title">\uC774\uBBF8 \uC874\uC7AC\uD558\uB294 \uD638\uC2A4\uD2B8 \uB514\uB809\uD1A0\uB9AC\uC5D0 \uCEE8\uD14C\uC774\uB108\uC758 \uB514\uB809\uD1A0\uB9AC\uB97C \uACF5\uC720\uD55C\uB2E4\uBA74?</p><p>\uD638\uC2A4\uD2B8 \uB514\uB809\uD1A0\uB9AC\uC5D0 \uC788\uB358 \uB370\uC774\uD130\uB294 \uC9C0\uC6CC\uC9C0\uACE0 \uCEE8\uD14C\uC774\uB108\uC758 \uB514\uB809\uD1A0\uB9AC \uC790\uCCB4\uAC00 \uB36E\uC5B4\uC50C\uC6CC\uC9C4\uB2E4.</p></div><p><strong>\uC989, \uD638\uC2A4\uD2B8 \uBCFC\uB968 \uACF5\uC720\uB294 \uD638\uC2A4\uD2B8\uC758 \uB514\uB809\uD1A0\uB9AC\uB97C \uCEE8\uD14C\uC774\uB108\uC758 \uB514\uB809\uD1A0\uB9AC\uC5D0 \uB9C8\uC6B4\uD2B8 \uD558\uB294\uAC83\uC774\uB2E4.</strong></p><p>\uB9CC\uC57D \uB3C4\uCEE4 \uBCFC\uB968\uC774 \uC2E4\uC81C \uD638\uC2A4\uD2B8\uC758 \uC5B4\uB290 \uACBD\uB85C\uC5D0 \uB9CC\uB4E4\uC5B4 \uC84C\uB294\uC9C0 \uBAA8\uB974\uACA0\uB2E4\uBA74<br><code>docker volume inspect [\uCEE8\uD14C\uC774\uB108 \uC774\uB984]</code> \uBA85\uB839\uC5B4\uB97C \uC774\uC6A9\uD558\uBA74 \uB41C\uB2E4.</p><h2 id="_2-\u1107\u1169\u11AF\u1105\u1172\u11B7-\u110F\u1165\u11AB\u1110\u1166\u110B\u1175\u1102\u1165" tabindex="-1"><a class="header-anchor" href="#_2-\u1107\u1169\u11AF\u1105\u1172\u11B7-\u110F\u1165\u11AB\u1110\u1166\u110B\u1175\u1102\u1165" aria-hidden="true">#</a> 2. \uBCFC\uB968 \uCEE8\uD14C\uC774\uB108</h2><p>\uBCFC\uB968 \uCEE8\uD14C\uC774\uB108\uB294 <strong>\uD638\uC2A4\uD2B8 \uBCFC\uB968\uC744 \uC801\uC6A9\uD55C \uCEE8\uD14C\uC774\uB108\uC758 \uBCFC\uB968 \uB514\uB809\uD1A0\uB9AC\uB97C \uACF5\uC720\uD558\uB294 \uBC29\uBC95</strong>\uC774\uB2E4.</p><h3 id="\u110B\u1169\u11B8\u1109\u1167\u11AB-\u1109\u1161\u110B\u116D\u11BC\u1107\u1165\u11B8-1" tabindex="-1"><a class="header-anchor" href="#\u110B\u1169\u11B8\u1109\u1167\u11AB-\u1109\u1161\u110B\u116D\u11BC\u1107\u1165\u11B8-1" aria-hidden="true">#</a> \uC635\uC158 \uC0AC\uC6A9\uBC95</h3><p><code>--volumes-from [\uBCFC\uB968\uC774 \uACF5\uC720\uB41C \uCEE8\uD14C\uC774\uB108 \uC774\uB984]</code><img src="'+l+'" alt="volume-container"></p><p>\uBCFC\uB968\uC758 \uC5ED\uD560\uB9CC \uD558\uB294 <code>\uBCFC\uB968 \uCEE8\uD14C\uC774\uB108</code>\uB97C \uC911\uAC04\uC5D0 \uB450\uACE0 \uC5EC\uB7EC \uCEE8\uD14C\uC774\uB108\uAC00 \uB370\uC774\uD130\uB97C \uAC04\uC811\uC801\uC73C\uB85C \uACF5\uC720\uBC1B\uB294 \uBC29\uC2DD\uC774\uB2E4.</p><br><h2 id="_3-\u1103\u1169\u110F\u1165-\u1107\u1169\u11AF\u1105\u1172\u11B7" tabindex="-1"><a class="header-anchor" href="#_3-\u1103\u1169\u110F\u1165-\u1107\u1169\u11AF\u1105\u1172\u11B7" aria-hidden="true">#</a> 3. \uB3C4\uCEE4 \uBCFC\uB968</h2><p>\uB3C4\uCEE4 \uBCFC\uB968\uC740 \uB3C4\uCEE4 \uC790\uCCB4\uC5D0\uC11C \uC81C\uACF5\uD558\uB294 \uBCFC\uB968 \uAE30\uB2A5\uC774\uB2E4.</p><h3 id="\u1106\u1167\u11BC\u1105\u1167\u11BC\u110B\u1165-\u1109\u1161\u110B\u116D\u11BC\u1107\u1165\u11B8" tabindex="-1"><a class="header-anchor" href="#\u1106\u1167\u11BC\u1105\u1167\u11BC\u110B\u1165-\u1109\u1161\u110B\u116D\u11BC\u1107\u1165\u11B8" aria-hidden="true">#</a> \uBA85\uB839\uC5B4 \uC0AC\uC6A9\uBC95</h3><ul><li><strong>\uAE30\uBCF8 \uBA85\uB839\uC5B4</strong> : <code>docker volume</code></li><li><strong>\uB3C4\uCEE4 \uBCFC\uB968 \uC0DD\uC131</strong> : <code>docker volume create</code></li></ul><p>\uC791\uC131\uC911...</p><h3 id="reference" tabindex="-1"><a class="header-anchor" href="#reference" aria-hidden="true">#</a> Reference</h3>',35),g={href:"https://eqfwcev123.github.io/2020/01/30/%EB%8F%84%EC%BB%A4/docker-image-layer/",target:"_blank",rel:"noopener noreferrer"},u=i("https://eqfwcev123.github.io/2020/01/30/\uB3C4\uCEE4/docker-image-layer/");function m(f,b){const r=a("ExternalLinkIcon");return o(),n(s,null,[_,e("p",null,[e("a",g,[u,t(r)])])],64)}var k=d(h,[["render",m]]);export{k as default};
