import{e as t}from"./app.c837ccd9.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";var r="/images/TIL/CS-OS/3\uC7A5/\uC2A4\uCF00\uC904\uB9C1\uC131\uB2A5\uD3C9\uAC00\uAE30\uC900.jpg",n="/images/TIL/CS-OS/3\uC7A5/\uC751\uB2F5\uBE44\uC728.jpg";const s={},i=t('<h1 id="\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC-\u110B\u1161\u11AF\u1100\u1169\u1105\u1175\u110C\u1173\u11B7" tabindex="-1"><a class="header-anchor" href="#\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC-\u110B\u1161\u11AF\u1100\u1169\u1105\u1175\u110C\u1173\u11B7" aria-hidden="true">#</a> \uC2A4\uCF00\uC904\uB9C1 \uC54C\uACE0\uB9AC\uC998</h1><h2 id="\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC-\u1109\u1165\u11BC\u1102\u1173\u11BC-\u1111\u1167\u11BC\u1100\u1161-\u1100\u1175\u110C\u116E\u11AB" tabindex="-1"><a class="header-anchor" href="#\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC-\u1109\u1165\u11BC\u1102\u1173\u11BC-\u1111\u1167\u11BC\u1100\u1161-\u1100\u1175\u110C\u116E\u11AB" aria-hidden="true">#</a> \uC2A4\uCF00\uC904\uB9C1 \uC131\uB2A5 \uD3C9\uAC00 \uAE30\uC900</h2><p><img src="'+r+'" alt="\uC2A4\uCF00\uC904\uB9C1 \uC131\uB2A5 \uD3C9\uAC00 \uAE30\uC900"></p><ul><li><p><strong>\uD3C9\uADE0 \uB300\uAE30\uC2DC\uAC04</strong>: <strong>\uAC01 \uD504\uB85C\uC138\uC2A4\uAC00 \uC0DD\uC131\uB41C(\uC900\uBE44 \uD050\uC5D0 \uB4E4\uC5B4\uC628) \uC2DC\uC810\uBD80\uD130</strong> \uC900\uBE44 \uD050\uC5D0\uC11C <strong>\uAE30\uB2E4\uB9AC\uB294 \uC2DC\uAC04\uC758 \uD569\uC758 \uD3C9\uADE0\uAC12</strong></p></li><li><p><strong>\uD3C9\uADE0 \uBC18\uD658\uC2DC\uAC04</strong>: <strong>\uAC01 \uD504\uB85C\uC138\uC2A4\uAC00 \uC0DD\uC131\uB41C(\uC900\uBE44 \uD050\uC5D0 \uB4E4\uC5B4\uC628) \uC2DC\uC810\uBD80\uD130</strong> \uC218\uD589\uC774 <strong>\uC644\uB8CC\uB41C \uC2DC\uC810\uAE4C\uC9C0\uC758 \uC18C\uC694\uC2DC\uAC04\uC758 \uD3C9\uADE0\uAC12</strong></p></li></ul><div class="custom-container tip"><p class="custom-container-title">TIP</p><p>\uBC18\uD658\uC2DC\uAC04 = \uB300\uAE30\uC2DC\uAC04 + \uC218\uD589\uC2DC\uAC04</p></div><h2 id="\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC-\u110B\u1161\u11AF\u1100\u1169\u1105\u1175\u110C\u1173\u11B7-\u1106\u1169\u11A8\u1105\u1169\u11A8" tabindex="-1"><a class="header-anchor" href="#\u1109\u1173\u110F\u1166\u110C\u116E\u11AF\u1105\u1175\u11BC-\u110B\u1161\u11AF\u1100\u1169\u1105\u1175\u110C\u1173\u11B7-\u1106\u1169\u11A8\u1105\u1169\u11A8" aria-hidden="true">#</a> \uC2A4\uCF00\uC904\uB9C1 \uC54C\uACE0\uB9AC\uC998 \uBAA9\uB85D</h2><div class="custom-container tip"><p class="custom-container-title">\uC120\uC810, \uBE44\uC120\uC810\uC758 \uCC28\uC774</p><p><strong>\uC120\uC810 \uAE30\uBC95</strong>: \uC2E4\uD589 \uC911\uC778 \uD504\uB85C\uC138\uC2A4\uB97C \uC911\uC9C0\uC2DC\uD0A4\uACE0 CPU\uB97C \uAC15\uC81C\uB85C \uBE8F\uC5B4\uC62C \uC218 \uC788\uB294 \uBC29\uC2DD (\uBB38\uB9E5\uAD50\uD658 O)<br><strong>\uBE44\uC120\uC810 \uAE30\uBC95</strong>: \uC9C0\uAE08 \uC2E4\uD589\uC911\uC778 \uD504\uB85C\uC138\uC2A4\uB97C \uAC15\uC81C\uB85C \uC911\uC9C0\uC2DC\uD0AC \uC218 \uC5C6\uB294 \uBC29\uC2DD (\uBB38\uB9E5\uAD50\uD658 X)</p></div><div class="custom-container tip"><p class="custom-container-title">\uBB38\uB9E5\uAD50\uD658\uC774\uB780?</p><p>\uD504\uB85C\uC138\uC2A4\uB97C \uBC14\uAFD4\uC8FC\uB294 \uD589\uC704<br> (SRT \uC2A4\uCF00\uC904\uB9C1\uC5D0\uC11C \uB0A8\uC740 \uC2DC\uAC04\uC774 \uB354 \uC801\uC740 \uD504\uB85C\uC138\uC2A4\uB97C CPU\uC5D0\uAC8C \uD560\uB2F9\uD558\uAE30 \uC704\uD574 \uC2E4\uD589\uC911\uC778 \uD504\uB85C\uC138\uC2A4\uC640 \uAD50\uD658\uD558\uB294 \uAC83\uC744 \uB73B\uD568)</p></div><table><thead><tr><th style="text-align:center;"></th><th style="text-align:center;">\uD2B9\uC9D5</th><th style="text-align:center;">\uC7A5\uC810</th><th style="text-align:center;">\uB2E8\uC810</th></tr></thead><tbody><tr><td style="text-align:center;"><strong>FCFS \uC2A4\uCF00\uC904\uB9C1</strong><br><strong>(First-Come First-Served)</strong></td><td style="text-align:center;"><strong>\uBE44\uC120\uC810 \uC2A4\uCF00\uC904\uB9C1 \uC54C\uACE0\uB9AC\uC998</strong></td><td style="text-align:center;">\uAC00\uC7A5 \uAC04\uB2E8\uD55C \uC2A4\uCF00\uC904\uB9C1 \uAE30\uBC95</td><td style="text-align:center;">- \uC911\uC694\uD55C \uD504\uB85C\uC138\uC2A4\uAC00 \uC788\uB354\uB77C\uB3C4 \uB098\uC911\uC5D0 \uC218\uD589\uB420 \uC218 \uC788\uC74C<br>- \uD504\uB85C\uC138\uC2A4\uB4E4\uC758 \uB3C4\uCC29 \uC21C\uC11C\uC5D0 \uB530\uB77C \uBC18\uD658\uC2DC\uAC04\uC774 \uD06C\uAC8C \uBCC0\uD568</td></tr><tr><td style="text-align:center;"><strong>SJF \uC2A4\uCF00\uC904\uB9C1</strong><br><strong>(Shortest Job First)</strong></td><td style="text-align:center;"><strong>\uBE44\uC120\uC810 \uC2A4\uCF00\uC904\uB9C1 \uC54C\uACE0\uB9AC\uC998</strong><br>(\uB300\uAE30\uC911\uC778 \uD504\uB85C\uC138\uC2A4 \uC911\uC5D0\uC11C<br>\uC2E4\uD589\uC2DC\uAC04\uC774 \uAC00\uC7A5 \uC9E7\uB2E4\uACE0 \uC608\uC0C1\uB41C \uAC83\uC744 \uBA3C\uC800 \uB514\uC2A4\uD328\uCE58\uD568)</td><td style="text-align:center;">\uC77C\uAD04\uCC98\uB9AC \uD658\uACBD\uC5D0\uC11C \uAD6C\uD604\uD558\uAE30 \uC26C\uC6C0</td><td style="text-align:center;">\uC2E4\uD589 \uC608\uC815 \uC2DC\uAC04 \uAE38\uC774\uB97C \uC0AC\uC6A9\uC790\uC758 \uCD94\uC815\uCE58\uC5D0 \uC758\uC874\uD558\uAE30 \uB54C\uBB38\uC5D0 \uC2E4\uC81C\uB85C \uBA3C\uC800 \uCC98\uB9AC\uD574\uC8FC\uC5B4\uC57C \uD560 \uC791\uC5C5\uC744 \uC54C \uC218 \uC5C6\uC74C</td></tr><tr><td style="text-align:center;"><strong>SRT \uC2A4\uCF00\uC904\uB9C1</strong><br><strong>(Shortest Remaining Time)</strong></td><td style="text-align:center;"><strong>\uC120\uC810 \uC2A4\uCF00\uC904\uB9C1 \uC54C\uACE0\uB9AC\uC998</strong><br>(\uB9E4 \uC2DC\uAC04\uB9C8\uB2E4 CPU\uC5D0\uAC8C \uD560\uB2F9\uD560 \uD504\uB85C\uC138\uC2A4\uB97C \uBC14\uAFD4\uC904 \uC218 \uC788\uC74C)<br>\uC2E4\uD589\uC774 \uB05D\uB0A0 \uB54C\uAE4C\uC9C0 \uB0A8\uC740 \uC2DC\uAC04 \uCD94\uC815\uCE58\uAC00 \uAC00\uC7A5 \uC9E7\uC740 \uD504\uB85C\uC138\uC2A4\uB97C \uBA3C\uC800 \uB514\uC2A4\uD328\uCE58</td><td style="text-align:center;">- SJF\uBCF4\uB2E4 \uD3C9\uADE0 \uB300\uAE30\uC2DC\uAC04\uC774\uB098 \uD3C9\uADE0 \uBC18\uD658\uC2DC\uAC04\uC5D0\uC11C \uD6A8\uC728\uC801<br>- \uB300\uD654\uD615 \uC6B4\uC601\uCCB4\uC81C\uC5D0 \uC720\uC6A9</td><td style="text-align:center;">- \uAC01 \uD504\uB85C\uC138\uC2A4\uC758 \uC2E4\uD589\uC2DC\uAC04 \uCD94\uC801, \uC120\uC810\uC744 \uC704\uD55C \uBB38\uB9E5 \uAD50\uD658 \uB4F1 SJF\uBCF4\uB2E4 \uC624\uBC84\uD5E4\uB4DC\uAC00 \uD07C</td></tr><tr><td style="text-align:center;"><strong>RR \uC2A4\uCF00\uC904\uB9C1</strong><br><strong>(Round Robin)</strong></td><td style="text-align:center;"><strong>\uC120\uC810 \uC2A4\uCF00\uC904\uB9C1 \uC54C\uACE0\uB9AC\uC998</strong><br>- \uC900\uBE44 \uD050\uC5D0 \uB3C4\uCC29\uD55C \uC21C\uC11C\uC5D0 \uB530\uB77C \uB514\uC2A4\uD328\uCE58\uD558\uC9C0\uB9CC \uC815\uD574\uC9C4 \uC2DC\uAC04 \uD560\uB2F9\uB7C9\uC5D0 \uC758\uD574 \uC2E4\uD589\uC744 \uC81C\uD55C\uD568<br>- \uC2DC\uAC04 \uD560\uB2F9\uB7C9 \uC548\uC5D0 \uC644\uB8CC\uB418\uC9C0 \uBABB\uD55C \uD504\uB85C\uC138\uC2A4\uB294 \uC900\uBE44 \uD050\uC758 \uB9E8 \uB4A4\uC5D0 \uBC30\uCE58<br></td><td style="text-align:center;">- CPU\uB97C \uB3C5\uC810\uD558\uC9C0 \uC54A\uACE0 \uACF5\uD3C9\uD558\uAC8C \uC774\uC6A9<br>- \uB300\uD654\uD615 \uC6B4\uC601\uCCB4\uC81C\uC5D0 \uC720\uC6A9</td><td style="text-align:center;">- \uC2DC\uAC04 \uD560\uB2F9\uB7C9\uC774 \uB108\uBB34 \uD06C\uBA74 FCFS \uC2A4\uCF00\uC904\uB9C1\uACFC \uAC19\uC544\uC9D0<br>- \uC2DC\uAC04 \uD560\uB2F9\uB7C9\uC774 \uB108\uBB34 \uC791\uC73C\uBA74 \uBB38\uB9E5 \uAD50\uD658\uC5D0 \uB530\uB978 \uC624\uBC84\uD5E4\uB4DC\uAC00 \uD06C\uAC8C \uC99D\uAC00\uD568</td></tr><tr><td style="text-align:center;"><strong>HRN \uC2A4\uCF00\uC904\uB9C1</strong><br><strong>(Highest Response Ratio Next)</strong></td><td style="text-align:center;"><strong>\uBE44\uC120\uC810 \uC2A4\uCF00\uC904\uB9C1 \uC54C\uACE0\uB9AC\uC998</strong><br>- \uC900\uBE44 \uD050\uC5D0\uC11C \uAE30\uB2E4\uB9AC\uB294 \uD504\uB85C\uC138\uC2A4 \uC911 \uC751\uB2F5\uBE44\uC728\uC774 \uAC00\uC7A5 \uD070 \uAC83\uC744 \uBA3C\uC800 \uB514\uC2A4\uD328\uCE58<br>- \uC608\uC0C1 \uC2E4\uD589\uC2DC\uAC04\uC774 \uC9E7\uC744\uC218\uB85D, \uB300\uAE30\uC2DC\uAC04\uC774 \uAE38\uC218\uB85D \uC751\uB2F5\uBE44\uC728\uC774 \uCEE4\uC9D0</td><td style="text-align:center;">SJF\uC758 \uB2E8\uC810\uC744 \uBCF4\uC644</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;"><strong>\uB2E4\uB2E8\uACC4 \uD53C\uB4DC\uBC31 \uD050 \uC2A4\uCF00\uC904\uB9C1</strong></td><td style="text-align:center;"><strong>\uC120\uC810 \uC2A4\uCF00\uC904\uB9C1 \uC54C\uACE0\uB9AC\uC998</strong><br>- <code>I/O</code> or <code>CPU</code> \uC911\uC2EC \uD504\uB85C\uC138\uC2A4 \uD2B9\uC131\uC5D0 \uB530\uB77C \uC11C\uB85C \uB2E4\uB978 \uC2DC\uAC04 \uD560\uB2F9\uB7C9 \uBD80\uC5EC<br>- n\uAC1C\uC758 \uB2E8\uACC4\uAC00 \uC788\uACE0 \uAC01 \uB2E8\uACC4\uB9C8\uB2E4 \uD050\uAC00 \uD558\uB098\uC529 \uC874\uC7AC<br>- \uB2E8\uACC4\uAC00 \uCEE4\uC9C8\uC218\uB85D \uC2DC\uAC04 \uD560\uB2F9\uB7C9\uB3C4 \uCEE4\uC9D0</td><td style="text-align:center;">- I/O \uC704\uC8FC\uC758 \uD504\uB85C\uC138\uC2A4(\uB300\uD654\uD615)\uB294 \uB192\uC740 \uC6B0\uC120\uAD8C \uC720\uC9C0<br></td><td style="text-align:center;">- \uC5F0\uC0B0 \uC704\uC8FC\uC758 CPU \uC911\uC2EC \uD504\uB85C\uC138\uC2A4\uB294 \uB0AE\uC740 \uC6B0\uC120\uAD8C\uC774\uB77C\uC11C \uB2E8\uACC4\uAC00 \uB192\uC544\uC9C0\uB294 \uB300\uC2E0 \uAE34 \uC2DC\uAC04 \uD560\uB2F9\uB7C9\uC744 \uAC00\uC9C0\uAC8C \uB428</td></tr><tr><td style="text-align:center;"><strong>\uC801\uC751\uD615 \uB2E4\uB2E8\uACC4 \uD53C\uB4DC\uBC31 \uD050 \uC2A4\uCF00\uC904\uB9C1</strong></td><td style="text-align:center;"><strong>\uC120\uC810 \uC2A4\uCF00\uC904\uB9C1 \uC54C\uACE0\uB9AC\uC998</strong><br>- \uC2DC\uAC04 \uD560\uB2F9\uB7C9\uC744 \uB2E4 \uC4F0\uAE30 \uC804\uC5D0 CPU\uB97C \uBC18\uB0A9\uD558\uB294 \uACBD\uC6B0 \uD558\uB098 \uC791\uC740 \uB2E8\uACC4\uC758 \uD050\uB85C \uC774\uB3D9 \uBC30\uCE58<br>- \uC5F0\uC0B0 \uC704\uC8FC\uC758 \uD504\uB85C\uC138\uC2A4\uAC00 I/O \uC704\uC8FC\uB85C \uBC14\uB010\uB2E4\uBA74 \uC810\uC810 \uC791\uC740 \uB2E8\uACC4\uB85C \uBC30\uCE58 \uAC00\uB2A5</td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr></tbody></table><div class="custom-container tip"><p class="custom-container-title">\uC751\uB2F5\uBE44\uC728 \uACF5\uC2DD</p><p><img src="'+n+'" alt="\uC751\uB2F5\uBE44\uC728"></p></div><div class="custom-container tip"><p class="custom-container-title">\uB2E4\uB2E8\uACC4 \uD53C\uB4DC\uBC31 \uD050 \uC2A4\uCF00\uC904\uB9C1 \uBC29\uBC95</p><ol><li>\uC2E0\uADDC \uD504\uB85C\uC138\uC2A4\uB294 \uB2E8\uACC4 1\uC758 \uD050\uC5D0\uC11C FIFO \uC21C\uC11C\uC5D0 \uB530\uB77C CPU \uC810\uC720</li><li>\uC785\uCD9C\uB825 \uAC19\uC740 \uC774\uBCA4\uD2B8\uAC00 \uBC1C\uC0DD\uD558\uBA74 CPU\uB97C \uC591\uBCF4\uD558\uACE0 \uB300\uAE30\uC0C1\uD0DC\uB85C \uAC14\uB2E4\uAC00 \uB2E4\uC2DC \uC900\uBE44\uC0C1\uD0DC\uAC00 \uB420 \uB54C\uC5D0\uB294 \uB3D9\uC77C\uD55C \uB2E8\uACC4\uC758 \uD050\uC5D0 \uBC30\uCE58</li><li>\uC2DC\uAC04 \uD560\uB2F9\uB7C9\uC744 \uB2E4 \uC37C\uB294\uB370\uB3C4 \uD504\uB85C\uC138\uC2A4\uAC00 \uC885\uB8CC\uB418\uC9C0 \uC54A\uC558\uB2E4\uBA74 \uB2E4\uC74C \uB2E8\uACC4\uB85C \uC774\uB3D9</li><li>\uB9C8\uC9C0\uB9C9 \uB2E8\uACC4 n\uC5D0\uC11C\uB294 RR \uC2A4\uCF00\uC904\uB9C1 \uBC29\uC2DD\uC73C\uB85C \uB3D9\uC791</li><li>\uB2E8\uACC4 k\uC758 \uD050\uC5D0 \uC788\uB294 \uD504\uB85C\uC138\uC2A4\uAC00 CPU\uB97C \uD560\uB2F9 \uBC1B\uC73C\uB824\uBA74 1\uBD80\uD130 k-1\uAE4C\uC9C0 \uBAA8\uB4E0 \uD050\uAC00 \uBE44\uC5B4\uC788\uC5B4\uC57C\uB9CC \uD568</li></ol></div>',11);function l(o,d){return i}var c=e(s,[["render",l]]);export{c as default};
