import{_ as l,o as i,c as a,e}from"./app.96321e0f.js";const r={},t=e('<h1 id="\u1103\u1166\u110B\u1175\u1110\u1165-\u110C\u1165\u110C\u1161\u11BC\u1100\u116A-\u1111\u1161\u110B\u1175\u11AF" tabindex="-1"><a class="header-anchor" href="#\u1103\u1166\u110B\u1175\u1110\u1165-\u110C\u1165\u110C\u1161\u11BC\u1100\u116A-\u1111\u1161\u110B\u1175\u11AF" aria-hidden="true">#</a> \uB370\uC774\uD130 \uC800\uC7A5\uACFC \uD30C\uC77C</h1><h2 id="\u1106\u116E\u11AF\u1105\u1175\u110C\u1165\u11A8-\u110C\u1165\u110C\u1161\u11BC-\u110C\u1161\u11BC\u110E\u1175" tabindex="-1"><a class="header-anchor" href="#\u1106\u116E\u11AF\u1105\u1175\u110C\u1165\u11A8-\u110C\u1165\u110C\u1161\u11BC-\u110C\u1161\u11BC\u110E\u1175" aria-hidden="true">#</a> \uBB3C\uB9AC\uC801 \uC800\uC7A5 \uC7A5\uCE58</h2><p>\uBB3C\uB9AC\uC801 \uC800\uC7A5\uC7A5\uCE58\uB294 \uB370\uC774\uD130 \uC811\uADFC \uC18D\uB3C4, \uC6A9\uB7C9\uC744 \uAE30\uC900\uC73C\uB85C \uB2E4\uC591\uD55C \uC7A5\uCE58\uB85C \uAD6C\uC131\uB428</p><h3 id="\u1106\u116E\u11AF\u1105\u1175\u110C\u1165\u11A8-\u110C\u1165\u110C\u1161\u11BC\u110C\u1161\u11BC\u110E\u1175\u1107\u1167\u11AF-\u1110\u1173\u11A8\u110C\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#\u1106\u116E\u11AF\u1105\u1175\u110C\u1165\u11A8-\u110C\u1165\u110C\u1161\u11BC\u110C\u1161\u11BC\u110E\u1175\u1107\u1167\u11AF-\u1110\u1173\u11A8\u110C\u1175\u11BC" aria-hidden="true">#</a> \uBB3C\uB9AC\uC801 \uC800\uC7A5\uC7A5\uCE58\uBCC4 \uD2B9\uC9D5</h3><ol><li>\uD718\uBC1C\uC131 <ul><li>\uCE90\uC2DC: \uACE0\uBE44\uC6A9 \uC800\uC7A5\uC7A5\uCE58\uB85C \uBE60\uB978 \uC811\uADFC \uC18D\uB3C4\uB97C \uBCF4\uC7A5</li><li>\uBA54\uC778 \uBA54\uBAA8\uB9AC: \uC2E4\uC81C \uD504\uB85C\uADF8\uB7A8\uACFC \uB370\uC774\uD130 \uC801\uC7AC \uACF5\uAC04</li></ul></li><li>\uBE44\uD718\uBC1C\uC131 <ul><li>\uD50C\uB798\uC26C \uBA54\uBAA8\uB9AC: \uBA54\uC778 \uBA54\uBAA8\uB9AC\uC640 \uC720\uC0AC\uD558\uB098 \uBE44\uD718\uBC1C\uC131</li><li>\uC790\uAE30 \uB514\uC2A4\uD06C: \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC804\uCCB4\uB97C \uC548\uC815\uC801\uC73C\uB85C \uC800\uC7A5</li><li>\uAD11\uD559 \uB514\uC2A4\uD06C \uB4DC\uB77C\uC774\uBE0C: CD, DVD, Blue-ray \uB4F1</li><li>\uD14C\uC774\uD504 \uC7A5\uCE58: \uC6A9\uB7C9\uC774 \uD06C\uACE0 \uC800\uB834\uD558\uB098 \uC21C\uCC28 \uC811\uADFC \uBC29\uC2DD\uC73C\uB85C \uC811\uADFC \uC18D\uB3C4\uAC00 \uB9E4\uC6B0 \uB290\uB9BC</li></ul></li></ol><h2 id="\u1103\u1166\u110B\u1175\u1110\u1165\u1107\u1166\u110B\u1175\u1109\u1173-\u1100\u116E\u1109\u1165\u11BC" tabindex="-1"><a class="header-anchor" href="#\u1103\u1166\u110B\u1175\u1110\u1165\u1107\u1166\u110B\u1175\u1109\u1173-\u1100\u116E\u1109\u1165\u11BC" aria-hidden="true">#</a> \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uAD6C\uC131</h2><ol><li><strong>\uD30C\uC77C</strong><ul><li>\uB370\uC774\uD130\uB97C \uC601\uAD6C\uC801\uC73C\uB85C \uC800\uC7A5\uD558\uAE30 \uC704\uD574 \uC0AC\uC6A9\uB418\uB294 \uAC00\uC7A5 \uAE30\uCD08\uC801\uC778 \uB17C\uB9AC\uC801 \uAD6C\uC870</li></ul></li><li><strong>\uBE14\uB7ED</strong><ul><li>\uD30C\uC77C\uC744 \uACE0\uC815\uC801\uC778 \uAE38\uC774\uB85C \uBD84\uD560\uD558\uC5EC \uC0DD\uAE30\uB294 \uADE0\uB4F1\uD55C \uD06C\uAE30\uC758 \uB370\uC774\uD130 \uBB36\uC74C</li><li>\uC77C\uBC18\uC801\uC73C\uB85C \uBA54\uBAA8\uB9AC\uC640 \uB514\uC2A4\uD06C\uAC04 \uB370\uC774\uD130 \uC804\uC1A1 \uB2E8\uC704\uB85C \uACB0\uC815</li></ul></li><li><strong>\uB808\uCF54\uB4DC</strong><ul><li>\uBE14\uB7ED\uC744 \uAD6C\uC131\uD558\uB294 \uC694\uC18C</li><li>\uB354 \uC774\uC0C1 \uBD84\uB9AC\uB420 \uC218 \uC5C6\uB294 \uCD5C\uC18C \uB370\uC774\uD130 \uC800\uC7A5 \uB2E8\uC704</li></ul></li></ol><ul><li><p><strong>\uACE0\uC815 \uAE38\uC774 \uB808\uCF54\uB4DC</strong> \uACE0\uC815\uC801\uC778 \uBC14\uC774\uD2B8 \uC218\uB97C \uAC16\uB294 \uB808\uCF54\uB4DC\uB97C \uC800\uC7A5\uD558\uB294 \uAE30\uBC95</p></li><li><p><strong>\uC794\uC5EC \uACE0\uC815 \uAE38\uC774 \uB808\uCF54\uB4DC \uD560\uB2F9</strong> \uBE14\uB7ED\uC758 \uAE38\uC774\uAC00 \uB808\uCF54\uB4DC \uAE38\uC774\uB85C \uC815\uD655\uD788 \uB098\uB220\uC9C0\uC9C0 \uC54A\uC544 \uC794\uC5EC \uACF5\uAC04\uC744 \uBE44\uC6CC\uB450\uB294 \uBC29\uBC95<br> \uBE14\uB7ED\uB9C8\uB2E4 \uB0A8\uB294 \uACF5\uAC04\uC774 \uC0DD\uAE30\uBA74 \uC544\uAE5D\uAE30 \uB54C\uBB38\uC5D0 <u>\uACE0\uC815 \uAE38\uC774 \uB808\uCF54\uB4DC \uD560\uB2F9</u>\uC744 \uD1B5\uD574\uC11C \uC794\uC5EC\uACF5\uAC04\uC744 \uC0AC\uC6A9\uD558\uAE30\uB3C4 \uD568</p></li><li><p><strong>\uACE0\uC815 \uAE38\uC774 \uB808\uCF54\uB4DC \uD560\uB2F9</strong> \uACE0\uC815 \uAE38\uC774 \uB808\uCF54\uB4DC \uB9CC\uD07C \uD560\uB2F9\uD558\uACE0\uB098\uC11C \uB0A8\uC740 \uACF5\uAC04\uC774 \uACE0\uC815 \uAE38\uC774\uBCF4\uB2E4 \uC791\uC744 \uACBD\uC6B0,<br> \uB0A8\uC740 \uACF5\uAC04\uC5D0 \uB370\uC774\uD130\uC758 \uC77C\uBD80\uB97C \uD560\uB2F9\uD558\uACE0 \uB098\uBA38\uC9C0\uB294 \uB2E4\uC74C \uBE14\uB7ED\uC5D0 \uD560\uB2F9\uD568 <br></p></li></ul><div class="custom-container warning"><p class="custom-container-title">\uB2E8\uC810</p><p>\uB808\uCF54\uB4DC \uC811\uADFC \uC2DC \uB450 \uBE14\uB7ED\uC744 \uC811\uADFC\uD574\uC57C \uD568</p><ol><li><strong>\uB808\uCF54\uB4DC \uC0AD\uC81C \uC2DC</strong><ul><li>\uD574\uB2F9 \uB808\uCF54\uB4DC\uAC00 \uC800\uC7A5\uB41C \uC704\uCE58\uC5D0 \uBE48\uACF5\uAC04\uC774 \uC0DD\uC131</li><li>\uC7A5\uC2DC\uAC04 \uB808\uCF54\uB4DC\uC758 \uC0BD\uC785 \uBC0F \uC0AD\uC81C \uBC1C\uC0DD \uC2DC, \uC800\uC7A5 \uACF5\uAC04\uC5D0 \uB9CE\uC740 \uB0AD\uBE44\uAC00 \uBC1C\uC0DD</li></ul></li><li><strong>\uB808\uCF54\uB4DC \uC0AD\uC81C \uC2DC \uB300\uCC98 \uBC29\uC548</strong><ul><li>\uB9C8\uC9C0\uB9C9 \uB808\uCF54\uB4DC\uB85C \uACF5\uBC31 \uB300\uCCB4</li><li>\uC0AD\uC81C \uB808\uCF54\uB4DC \uC774\uD6C4\uC758 \uB808\uCF54\uB4DC\uB97C \uC774\uB3D9</li><li>\uAC00\uC6A9 \uB9AC\uC2A4\uD2B8 \uAD00\uB9AC</li></ul></li></ol></div><h3 id="\u1105\u1166\u110F\u1169\u1103\u1173-\u1109\u1161\u11A8\u110C\u1166-\u1103\u1162\u110E\u1165" tabindex="-1"><a class="header-anchor" href="#\u1105\u1166\u110F\u1169\u1103\u1173-\u1109\u1161\u11A8\u110C\u1166-\u1103\u1162\u110E\u1165" aria-hidden="true">#</a> \uB808\uCF54\uB4DC \uC0AD\uC81C \uB300\uCC98</h3><ol><li>\uB9C8\uC9C0\uB9C9 \uB808\uCF54\uB4DC\uB85C \uACF5\uBC31 \uB300\uCCB4 =&gt; \uB808\uCF54\uB4DC\uC758 \uC21C\uC11C\uAC00 \uD2C0\uC5B4\uC9D0</li><li>\uC0AD\uC81C \uB808\uCF54\uB4DC \uC774\uD6C4\uC758 \uB808\uCF54\uB4DC\uB97C \uC774\uB3D9 =&gt; \uC55E\uCABD \uB808\uCF54\uB4DC\uB97C \uC0AD\uC81C\uD560 \uACBD\uC6B0 \uB4A4\uCABD\uC758 \uBAA8\uB4E0 \uB370\uC774\uD130\uAC00 \uC6C0\uC9C1\uC774\uBBC0\uB85C \uC624\uBC84\uD5E4\uB4DC\uAC00 \uC77C\uC5B4\uB0A0 \uC218 \uC788\uC74C</li><li>\uAC00\uC6A9 \uB9AC\uC2A4\uD2B8 \uAD00\uB9AC =&gt; \uD30C\uC77C\uD5E4\uB354\uC5D0 \uACF5\uBC31 \uB808\uCF54\uB4DC \uD3EC\uC778\uD130(\uC5F0\uACB0\uB9AC\uC2A4\uD2B8)\uB97C \uCD94\uAC00\uD574\uC11C \uC0AD\uC81C\uB41C \uB808\uCF54\uB4DC \uACF5\uAC04\uC744 \uAD00\uB9AC\uD574\uC90C</li></ol><h3 id="\u1100\u1161\u1107\u1167\u11AB-\u1100\u1175\u11AF\u110B\u1175-\u1105\u1166\u110F\u1169\u1103\u1173" tabindex="-1"><a class="header-anchor" href="#\u1100\u1161\u1107\u1167\u11AB-\u1100\u1175\u11AF\u110B\u1175-\u1105\u1166\u110F\u1169\u1103\u1173" aria-hidden="true">#</a> \uAC00\uBCC0 \uAE38\uC774 \uB808\uCF54\uB4DC</h3><ol><li><p>\uBE14\uB7ED\uC5D0 \uC800\uC7A5\uB418\uB294 \uB808\uCF54\uB4DC\uC758 \uAE38\uC774\uAC00 \uC11C\uB85C \uB2E4\uB978 \uB808\uCF54\uB4DC\uB97C \uD560\uB2F9\uD558\uB294 \uBC29\uBC95</p></li><li><p>\uAC00\uBCC0 \uAE38\uC774 \uB808\uCF54\uB4DC\uAC00 \uC0AC\uC6A9\uB418\uB294 \uC0C1\uD669</p><ul><li>\uD55C \uBE14\uB7ED \uB0B4\uC5D0 \uC800\uC7A5\uB418\uB294 \uB808\uCF54\uB4DC \uC720\uD615\uC774 \uB458 \uC774\uC0C1</li><li>\uAE38\uC774\uAC00 \uACE0\uC815\uB418\uB2C8 \uC54A\uC740 \uCEEC\uB7FC\uC758 \uAC1C\uC218\uAC00 \uD558\uB098 \uC774\uC0C1</li><li>\uB808\uCF54\uB4DC\uAC00 \uBA40\uD2F0\uC14B\uC744 \uD5C8\uC6A9\uD55C \uCEEC\uB7FC\uC744 \uAC00\uC9C8 \uB54C</li></ul></li><li><p><strong>0~4\uBC14\uC774\uD2B8\uC5D0 \uAC00\uBCC0 \uB808\uCF54\uB4DC\uC758 \uC2DC\uC791\uACFC \uB05D\uC744 \uC800\uC7A5\uD574\uB193\uC74C</strong></p></li></ol><div class="custom-container tip"><p class="custom-container-title">\uBA40\uD2F0\uC14B</p><p>\uB808\uCF54\uB4DC\uC758 \uCEEC\uB7FC\uAC12\uC774 \uC5EC\uB7EC \uAC1C\uC778 \uCEEC\uB7FC\uC774\uB2E4.<br> RDBMS\uC5D0\uC11C\uB294 \uD5C8\uC6A9\uD558\uC9C0 \uC54A\uC9C0\uB9CC, \uC0C1\uC6A9 \uB808\uCF54\uB4DC\uC5D0\uC11C\uB294 \uC774\uC6A9\uD558\uB294 \uACBD\uC6B0\uAC00 \uC788\uB2E4.</p></div><h3 id="\u1109\u1173\u11AF\u1105\u1169\u11BA\u1111\u1166\u110B\u1175\u110C\u1175-\u1100\u116E\u110C\u1169" tabindex="-1"><a class="header-anchor" href="#\u1109\u1173\u11AF\u1105\u1169\u11BA\u1111\u1166\u110B\u1175\u110C\u1175-\u1100\u116E\u110C\u1169" aria-hidden="true">#</a> \uC2AC\uB86F\uD398\uC774\uC9C0 \uAD6C\uC870</h3><ul><li>\uC2AC\uB86F\uD398\uC774\uC9C0\uC758 \uAC00\uC7A5 \uCC98\uC74C\uC5D0\uB294 \uB808\uCF54\uB4DC\uC758 \uAC1C\uC218\uB97C \uB2F4\uACE0 \uC788\uC74C</li><li>\uBE14\uB7ED \uD5E4\uB354: \uB808\uCF54\uB4DC \uAC1C\uC218 \uB2E4\uC74C\uC5D0\uB294 \uB808\uCF54\uB4DC \uC694\uC545 \uC815\uBCF4\uB97C \uB2F4\uACE0 \uC788\uC74C</li><li>\uB808\uCF54\uB4DC\uB294 \uC2AC\uB86F\uD398\uC774\uC9C0\uC758 \uAC00\uC7A5 \uB05D\uBD80\uD130 \uCC44\uC6CC\uC9D0</li></ul><h3 id="\u1111\u1161\u110B\u1175\u11AF-\u1100\u116E\u110C\u1169\u1112\u116A-\u1107\u1161\u11BC\u1107\u1165\u11B8\u110B\u1174-\u110C\u1169\u11BC\u1105\u1172" tabindex="-1"><a class="header-anchor" href="#\u1111\u1161\u110B\u1175\u11AF-\u1100\u116E\u110C\u1169\u1112\u116A-\u1107\u1161\u11BC\u1107\u1165\u11B8\u110B\u1174-\u110C\u1169\u11BC\u1105\u1172" aria-hidden="true">#</a> \uD30C\uC77C \uAD6C\uC870\uD654 \uBC29\uBC95\uC758 \uC885\uB958</h3><ul><li>\uD799 \uD30C\uC77C \uAD6C\uC870: \uC800\uC7A5\uC21C\uC11C \uACE0\uB824\uC5C6\uC774 \uD30C\uC77C \uB0B4 \uC784\uC758\uC758 \uC704\uCE58\uC5D0 \uBC30\uCE58 (\uC77C\uB2E8 \uBE48\uACF5\uAC04 \uCC3E\uC544\uC11C \uC6B0\uC120 \uC800\uC7A5)</li><li>\uC21C\uCC28 \uD30C\uC77C \uAD6C\uC870: \uB808\uCF54\uB4DC\uB4E4\uC774 \uD0D0\uC0C9 \uD0A4 \uAE30\uC900\uC73C\uB85C \uC815\uB82C\uB418\uC5B4 \uC800\uC7A5 (\uC774\uC9C4 \uD0D0\uC0C9\uC744 \uD1B5\uD574 \uBE60\uB974\uAC8C \uAC80\uC0C9 \uAC00\uB2A5)</li><li>\uD574\uC2DC \uD30C\uC77C \uAD6C\uC870: \uD574\uC2DC \uD568\uC218\uB97C \uC0AC\uC6A9\uD558\uC5EC \uBE14\uB7ED \uC8FC\uC18C\uB97C \uACC4\uC0B0</li></ul><h3 id="\u1109\u116E\u11AB\u110E\u1161-\u1111\u1161\u110B\u1175\u11AF-\u1100\u116E\u110C\u1169" tabindex="-1"><a class="header-anchor" href="#\u1109\u116E\u11AB\u110E\u1161-\u1111\u1161\u110B\u1175\u11AF-\u1100\u116E\u110C\u1169" aria-hidden="true">#</a> \uC21C\uCC28 \uD30C\uC77C \uAD6C\uC870</h3><ol><li><p>\uB808\uCF54\uB4DC\uAC00 \uAC80\uC0C9\uD0A4 \uC21C\uC11C\uB300\uB85C \uC815\uB82C</p></li><li><p>\uB808\uCF54\uB4DC\uAC00 \uD30C\uC77C\uC5D0 \uC0BD\uC785\uB418\uB294 \uC2DC\uC810\uC5D0\uC11C \uD0A4 \uAC12\uC774 \uBD80\uC5EC\uB428</p></li><li><p>\uC7A5\uC810</p><ul><li>\uAC80\uC0C9\uD0A4\uC5D0 \uB300\uD55C \uC815\uB82C \uC5F0\uC0B0\uC774 \uBD88\uD544\uC694, \uD0A4 \uAC12\uB4E4\uC758 \uC21C\uC11C\uB85C \uB808\uCF54\uB4DC\uB97C \uD310\uB3C5\uD558\uB294 \uC5F0\uC0B0\uC5D0 \uD6A8\uC728\uC801</li><li>\uD604\uC7AC \uB808\uCF54\uB4DC\uC5D0\uC11C \uC815\uB82C\uB41C \uD0A4 \uC21C\uC11C\uB85C \uB2E4\uC74C \uB808\uCF54\uB4DC\uB97C \uCC3E\uC744 \uB54C \uBD80\uAC00\uC801\uC778 \uBE14\uB7ED \uC811\uADFC\uC774 \uBD88\uD544\uC694</li><li>\uC774\uC9C4 \uD0D0\uC0C9\uC744 \uC0AC\uC6A9\uD558\uBA74 \uB354 \uBE60\uB974\uAC8C \uB808\uCF54\uB4DC\uB97C \uAC80\uC0C9\uD560 \uC218 \uC788\uC74C</li></ul></li><li><p>\uB2E8\uC810</p><ul><li>\uB808\uCF54\uB4DC \uC0BD\uC785, \uC0AD\uC81C\uC5D0 \uB9CE\uC740 \uBE44\uC6A9 \uC18C\uC694</li></ul></li></ol><h2 id="\u110C\u1165\u110C\u1161\u11BC\u110C\u1161\u11BC\u110E\u1175-\u1100\u116A\u11AB\u1105\u1175" tabindex="-1"><a class="header-anchor" href="#\u110C\u1165\u110C\u1161\u11BC\u110C\u1161\u11BC\u110E\u1175-\u1100\u116A\u11AB\u1105\u1175" aria-hidden="true">#</a> \uC800\uC7A5\uC7A5\uCE58 \uAD00\uB9AC</h2><h3 id="\u110C\u1165\u110C\u1161\u11BC\u110C\u1161\u11BC\u110E\u1175-\u110C\u1165\u11B8\u1100\u1173\u11AB" tabindex="-1"><a class="header-anchor" href="#\u110C\u1165\u110C\u1161\u11BC\u110C\u1161\u11BC\u110E\u1175-\u110C\u1165\u11B8\u1100\u1173\u11AB" aria-hidden="true">#</a> \uC800\uC7A5\uC7A5\uCE58 \uC811\uADFC</h3><ol><li>\uD30C\uC77C\uC740 \uB17C\uB9AC\uC801 \uAD00\uC810\uC5D0\uC11C \uC800\uC7A5 \uAC1D\uCCB4\uB77C\uACE0 \uBCFC \uC218 \uC788\uC74C</li><li>\uC2E4\uC81C \uC800\uC7A5\uB420 \uB54C\uC5D0\uB294 \uC5EC\uB7EC \uAC1C\uC758 \uBB3C\uB9AC\uC801 \uB2E8\uC704\uC778 \uBE14\uB7ED\uC73C\uB85C \uC800\uC7A5 <ul><li>\uBE14\uB7ED\uC740 \uBA54\uBAA8\uB9AC\uC640 \uB514\uC2A4\uD06C \uAC04 \uB370\uC774\uD130\uC758 \uC804\uC1A1 \uB2E8\uC704</li><li>\uC77C\uBC18\uC801\uC73C\uB85C 2kb ~ 32kb \uC0AC\uC6A9</li><li>\uBE14\uB7ED \uC804\uC1A1\uC744 \uCD5C\uC18C\uD654\uD560 \uC218\uB85D \uC785\uCD9C\uB825 \uC18C\uC694 \uC2DC\uAC04\uC774 \uB2E8\uCD95</li></ul></li></ol><h3 id="\u1107\u1165\u1111\u1165-\u1100\u116A\u11AB\u1105\u1175\u110C\u1161" tabindex="-1"><a class="header-anchor" href="#\u1107\u1165\u1111\u1165-\u1100\u116A\u11AB\u1105\u1175\u110C\u1161" aria-hidden="true">#</a> \uBC84\uD37C \uAD00\uB9AC\uC790</h3><p>DBMS\uC0C1\uC758 \uC18C\uD504\uD2B8\uC6E8\uC5B4\uB294 \uD544\uC694\uD55C \uBE14\uB7ED\uC774 \uC788\uC744 \uB54C \uBC84\uD37C \uAD00\uB9AC\uC790\uC5D0\uAC8C \uD574\uB2F9 \uBE14\uB7ED\uC744 \uC694\uCCAD</p><ul><li>\uC694\uCCAD\uB41C \uBE14\uB7ED\uC774 \uBC84\uD37C\uC5D0 \uC788\uB2E4\uBA74, \uBC84\uD37C \uAD00\uB9AC\uC790\uB294 \uBE14\uB7ED\uC774 \uC704\uCE58\uD55C \uBA54\uBAA8\uB9AC \uC8FC\uC18C\uB97C \uD504\uB85C\uADF8\uB7A8\uC5D0\uAC8C \uC804\uB2EC</li><li>\uC694\uCCAD\uB41C \uBE14\uB7ED\uC774 \uC5C6\uB294 \uACBD\uC6B0, \uBC84\uD37C \uAD00\uB9AC\uC790\uB294 \uBC84\uD37C\uB0B4\uC758 \uC0C8\uB85C\uC6B4 \uACF5\uAC04\uC744 \uD560\uB2F9\uD558\uACE0 \uD574\uB2F9 \uBE14\uB7ED\uC744 \uC801\uC7AC</li><li>\uB354 \uC774\uC0C1 \uC801\uC7AC\uD560 \uACF5\uAC04\uC774 \uC5C6\uB2E4\uBA74, \uBC84\uD37C\uC5D0 \uC788\uB294 \uAE30\uC874 \uBE14\uB7ED\uC744 \uC120\uD0DD\uD558\uC5EC \uD560\uB2F9\uC744 \uD574\uC9C0\uD558\uACE0 \uD574\uB2F9 \uBE14\uB7ED\uC744 \uC801\uC7AC</li></ul><h3 id="\u1107\u1165\u1111\u1165-\u1100\u116A\u11AB\u1105\u1175\u110C\u1161\u110B\u1174-\u1100\u1175\u1102\u1173\u11BC" tabindex="-1"><a class="header-anchor" href="#\u1107\u1165\u1111\u1165-\u1100\u116A\u11AB\u1105\u1175\u110C\u1161\u110B\u1174-\u1100\u1175\u1102\u1173\u11BC" aria-hidden="true">#</a> \uBC84\uD37C \uAD00\uB9AC\uC790\uC758 \uAE30\uB2A5</h3><ul><li><p><strong>\uBC84\uD37C \uAD50\uCCB4 \uC804\uB7B5</strong></p><ul><li>\uAC00\uC6A9 \uACF5\uAC04\uC744 \uD655\uBCF4\uD558\uAE30 \uC704\uD574 \uAE30\uC874\uC5D0 \uC801\uC7AC\uB41C \uBE14\uB7ED\uC758 \uD560\uB2F9\uC744 \uD2B9\uC815 \uAE30\uC900\uC5D0 \uC758\uD558\uC5EC \uD574\uC9C0</li><li>\uBBF8\uB798\uC5D0 \uAC00\uC7A5 \uC801\uAC8C \uC0AC\uC6A9\uB420 \uBE14\uB7ED\uC744 \uC120\uD0DD\uD558\uC5EC \uB514\uC2A4\uD06C\uB85C \uB0B4\uBCF4\uB0B4\uB294 \uAC83\uC774 \uC774\uC0C1\uC801\uC778 \uBC84\uD37C \uAD50\uCCB4 \uC804\uB7B5</li><li>\uBC84\uD37C \uAD50\uCCB4 \uC804\uB7B5 \uAE30\uBC95 <ul><li>LRU(Least Recently Used): \uCD5C\uADFC\uC5D0 \uAC00\uC7A5 \uC801\uAC8C \uCC38\uC870\uB41C \uBE14\uB7ED</li><li>MFU(Most Frequently Used): \uD2B9\uC815 \uAE30\uAC04\uB3D9\uC548 \uAC00\uC7A5 \uC5EC\uB7EC\uBC88 \uC0AC\uC6A9\uB41C \uBE14\uB7ED\uC744 \uC120\uD0DD\uD558\uC5EC \uBE14\uB7ED\uC744 \uAD50\uCCB4</li></ul></li></ul></li><li><p><strong>\uACE0\uC815 \uBE14\uB7ED</strong></p><ul><li>\uC7A5\uC560\uB85C \uC778\uD558\uC5EC \uBA54\uBAA8\uB9AC\uC758 \uB370\uC774\uD130\uAC00 \uC190\uC2E4\uB418\uC5B4 \uC791\uC5C5\uC774 \uC911\uB2E8\uB420 \uACBD\uC6B0, \uC911\uB2E8\uB41C \uC791\uC5C5\uC758 \uACB0\uACFC\uBB3C\uC774 \uB514\uC2A4\uD06C\uC5D0 \uAE30\uB85D\uB418\uB294 \uAC83\uC744 \uBC29\uC9C0</li><li>\uB514\uC2A4\uD06C \uBE14\uB7ED\uC774 \uAD50\uCCB4\uB418\uB294 \uAC83\uC744 \uC81C\uD55C</li></ul></li><li><p><strong>\uBE14\uB7ED \uAC15\uC81C \uCD9C\uB825</strong></p><ul><li>\uC2DC\uC2A4\uD15C \uB85C\uADF8\uC640 \uAC19\uC774 \uC911\uC694\uD55C \uB370\uC774\uD130\uB294 \uB514\uC2A4\uD06C\uC5D0 \uC601\uAD6C\uC801\uC73C\uB85C \uAE30\uB85D\uB418\uC5B4\uC57C \uD568</li><li>\uBC84\uD37C \uACF5\uAC04\uC774 \uD544\uC694 \uC5C6\uC5B4\uB3C4 \uAC15\uC81C\uB85C \uB514\uC2A4\uD06C\uC5D0 \uAE30\uB85D</li></ul></li></ul>',28),n=[t];function h(s,d){return i(),a("div",null,n)}var u=l(r,[["render",h],["__file","09. \uB370\uC774\uD130 \uC800\uC7A5\uACFC \uD30C\uC77C.html.vue"]]);export{u as default};
