import{_ as n,o as s,c as a,e as i}from"./app.38bd00bc.js";var e="/images/TIL/Git/git-1-2-1.png",t="/images/TIL/Git/git-1-2-2.png",l="/images/TIL/Git/git-1-2-3.png",c="/images/TIL/Git/git-1-2-4.png";const o={},d=i('<h1 id="_1-2-git-bash\u110B\u1174-\u1100\u1175\u1107\u1169\u11AB-\u1106\u1167\u11BC\u1105\u1167\u11BC\u110B\u1165\u1103\u1173\u11AF" tabindex="-1"><a class="header-anchor" href="#_1-2-git-bash\u110B\u1174-\u1100\u1175\u1107\u1169\u11AB-\u1106\u1167\u11BC\u1105\u1167\u11BC\u110B\u1165\u1103\u1173\u11AF" aria-hidden="true">#</a> 1-2. Git Bash\uC758 \uAE30\uBCF8 \uBA85\uB839\uC5B4\uB4E4</h1><h2 id="git-help" tabindex="-1"><a class="header-anchor" href="#git-help" aria-hidden="true">#</a> git --help</h2><p>\uAE43 \uBA85\uB839\uC5B4 \uAD00\uB828 \uB3C4\uC6C0\uB9D0</p><h2 id="prompt" tabindex="-1"><a class="header-anchor" href="#prompt" aria-hidden="true">#</a> prompt</h2><p>$\uAE30\uD638\uC640 \uC717\uC904\uC5D0 \uD45C\uC2DC\uB418\uB294 \uACBD\uB85C \uB4F1\uC744 \uD569\uCCD0\uC11C \uD504\uB86C\uD504\uD2B8(prompt) \uB77C\uACE0 \uD55C\uB2E4.</p><p><img src="'+e+'" alt="git-1-2-1"></p><h2 id="\u1100\u1175\u1107\u1169\u11AB-\u1106\u1167\u11BC\u1105\u1167\u11BC\u110B\u1165" tabindex="-1"><a class="header-anchor" href="#\u1100\u1175\u1107\u1169\u11AB-\u1106\u1167\u11BC\u1105\u1167\u11BC\u110B\u1165" aria-hidden="true">#</a> \uAE30\uBCF8 \uBA85\uB839\uC5B4</h2><p><img src="'+t+`" alt="git-1-2-2"></p><h2 id="git-\u1105\u1169\u110F\u1165\u11AF\u110C\u1165\u110C\u1161\u11BC\u1109\u1169-\u1109\u1162\u11BC\u1109\u1165\u11BC\u1112\u1161\u1100\u1175" tabindex="-1"><a class="header-anchor" href="#git-\u1105\u1169\u110F\u1165\u11AF\u110C\u1165\u110C\u1161\u11BC\u1109\u1169-\u1109\u1162\u11BC\u1109\u1165\u11BC\u1112\u1161\u1100\u1175" aria-hidden="true">#</a> Git \uB85C\uCEEC\uC800\uC7A5\uC18C \uC0DD\uC131\uD558\uAE30</h2><p>\uC6B0\uC120 \uC790\uAE30\uAC00 \uB9CC\uB4DC\uB824\uB294 \uACBD\uB85C\uB85C \uC774\uB3D9\uD55C\uB2E4.</p><p>\uADF8\uB9AC\uACE0 \uC544\uB798\uC640 \uAC19\uC774 \uBA85\uB839\uC5B4\uB97C \uC791\uC131\uD55C\uB2E4.</p><div class="language-bash ext-sh line-numbers-mode"><pre class="language-bash"><code>$ <span class="token function">mkdir</span> hello-git-cli <span class="token comment"># \uC0C8\uB85C\uC6B4 \uD3F4\uB354 \uC0DD\uC131</span>

$ <span class="token builtin class-name">cd</span> hello-git-cli <span class="token comment"># \uC0DD\uC131\uD55C \uD3F4\uB354\uB85C \uC774\uB3D9</span>

$ <span class="token builtin class-name">pwd</span> <span class="token comment"># \uD604\uC7AC \uACBD\uB85C \uD655\uC778</span>

$ <span class="token function">git</span> status <span class="token comment"># Git \uC800\uC7A5\uC18C\uC758 \uC0C1\uD0DC\uB97C \uC54C\uB824\uC8FC\uB294 \uBA85\uB839\uC5B4</span>

</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>git status \uBA85\uB839\uC744 \uC2E4\uD589\uD558\uBA74 \uC5D0\uB7EC\uAC00 \uBC1C\uC0DD\uD560 \uAC83\uC774\uB2E4.</p><p><img src="`+l+`" alt="git-1-2-3"></p><p>&quot;not a git repository&quot;\uB77C\uACE0 \uD55C\uB2E4.</p><p>\uC989, &quot;\uD604\uC7AC \uB514\uB809\uD1A0\uB9AC\uB294 Git \uC800\uC7A5\uC18C\uAC00 \uC544\uB2C8\uB2E4.&quot; \uB77C\uB294 \uB73B\uC774\uB2E4.</p><ul><li><strong>git status</strong> : git \uC6CC\uD0B9\uD2B8\uB9AC\uC758 \uC0C1\uD0DC\uB97C \uBCF4\uB294 \uBA85\uB839\uC5B4</li><li><strong>git status -s</strong> : git status \uBA85\uB839\uBCF4\uB2E4 \uC9E7\uAC8C \uC694\uC57D\uD574\uC11C \uBCF4\uC5EC\uC8FC\uBA70, \uBCC0\uACBD\uB41C \uD30C\uC77C\uC774 \uB9CE\uC744 \uB54C \uC720\uC6A9\uD558\uB2E4.</li></ul><p>\uB9CC\uC57D \uC0C1\uC704\uC5D0 \uC788\uB294 \uD3F4\uB354\uB4E4 \uC911\uC5D0 Git \uC800\uC7A5\uC18C\uB85C \uC774\uBBF8 \uCD08\uAE30\uD654 \uB418\uC5B4\uC788\uB294 \uD3F4\uB354\uAC00 \uC788\uB2E4\uBA74 git status \uBA85\uB839\uC774 \uC5D0\uB7EC\uC5C6\uC774 \uC2E4\uD589\uB420 \uC218\uB3C4 \uC788\uB2E4!</p><p>git status \uBA85\uB839\uC774 \uC5D0\uB7EC\uC5C6\uC774 \uC2E4\uD589\uB420 \uC218\uB3C4 \uC788\uB2E4.</p><h2 id="git-\u110C\u1165\u110C\u1161\u11BC\u1109\u1169-\u1106\u1161\u11AB\u1103\u1173\u1102\u1173\u11AB-\u1107\u1165\u11B8-git-\u110C\u1165\u110C\u1161\u11BC\u1109\u1169-\u110E\u1169\u1100\u1175\u1112\u116A" tabindex="-1"><a class="header-anchor" href="#git-\u110C\u1165\u110C\u1161\u11BC\u1109\u1169-\u1106\u1161\u11AB\u1103\u1173\u1102\u1173\u11AB-\u1107\u1165\u11B8-git-\u110C\u1165\u110C\u1161\u11BC\u1109\u1169-\u110E\u1169\u1100\u1175\u1112\u116A" aria-hidden="true">#</a> Git \uC800\uC7A5\uC18C \uB9CC\uB4DC\uB294 \uBC95 (=Git \uC800\uC7A5\uC18C \uCD08\uAE30\uD654)</h2><div class="language-bash ext-sh line-numbers-mode"><pre class="language-bash"><code>$ <span class="token function">git</span> init <span class="token comment"># Git \uC800\uC7A5\uC18C \uC0DD\uC131</span>

$ <span class="token function">ls</span> -a <span class="token comment"># \uD30C\uC77C \uBAA9\uB85D \uD655\uC778 (\uC228\uAE40\uD30C\uC77C\uAE4C\uC9C0 \uD655\uC778)</span>

$ <span class="token function">git</span> status
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>&quot;git init&quot;\uC774 \uC798 \uB418\uC5C8\uB2E4\uBA74 \uC544\uB798\uC640 \uAC19\uC774 \uACBD\uB85C \uB05D\uC5D0 (master)\uAC00 \uBD99\uC740 \uAC83\uC744 \uC54C \uC218 \uC788\uB2E4.</p><p><img src="`+c+`" alt="git-1-2-4"></p><ul><li><strong>git init</strong> : Git \uC800\uC7A5\uC18C \uC0DD\uC131</li></ul><h2 id="\u110B\u116D\u11BC\u110B\u1165-\u110C\u1165\u11BC\u1105\u1175" tabindex="-1"><a class="header-anchor" href="#\u110B\u116D\u11BC\u110B\u1165-\u110C\u1165\u11BC\u1105\u1175" aria-hidden="true">#</a> \uC6A9\uC5B4 \uC815\uB9AC</h2><ul><li>\uC6CC\uD0B9\uD2B8\uB9AC : \uC791\uC5C5 \uD3F4\uB354\uC5D0\uC11C [.git]\uD3F4\uB354\uB97C \uBE80 \uB098\uBA38\uC9C0 \uBD80\uBD84. \uC77C\uBC18\uC801\uC778 \uC791\uC5C5\uC774 \uC77C\uC5B4\uB098\uB294 \uACF3\uC744 \uB73B\uD55C\uB2E4.</li><li>\uB85C\uCEEC\uC800\uC7A5\uC18C : \uC228\uAE40 \uCC98\uB9AC\uB418\uB294 .git \uD3F4\uB354</li><li>\uC6D0\uACA9\uC800\uC7A5\uC18C : GitHub\uC640 \uAC19\uC740 \uC800\uC7A5\uC18C\uB97C \uC5C5\uB85C\uB4DC\uD560 \uC218 \uC788\uB294 \uACF3\uC744 \uB73B\uD55C\uB2E4.</li><li>Git \uC800\uC7A5\uC18C : Git \uBA85\uB839\uC73C\uB85C \uAD00\uB9AC\uD560 \uC218 \uC788\uB294 \uD3F4\uB354 \uC804\uCCB4</li></ul><h2 id="\u110B\u1169\u11B8\u1109\u1167\u11AB-\u1109\u1165\u11AF\u110C\u1165\u11BC\u1112\u1161\u1100\u1175-git-config" tabindex="-1"><a class="header-anchor" href="#\u110B\u1169\u11B8\u1109\u1167\u11AB-\u1109\u1165\u11AF\u110C\u1165\u11BC\u1112\u1161\u1100\u1175-git-config" aria-hidden="true">#</a> \uC635\uC158 \uC124\uC815\uD558\uAE30 (git config)</h2><div class="language-bash ext-sh line-numbers-mode"><pre class="language-bash"><code><span class="token comment"># --local : \uC9C0\uC5ED (\uD604\uC7AC Git \uC800\uC7A5\uC18C\uC5D0\uC11C\uB9CC \uC720\uD6A8\uD55C \uC635\uC158)</span>
<span class="token comment"># --global : \uC804\uC5ED (\uD604\uC7AC \uC0AC\uC6A9\uC790\uB97C \uC704\uD55C \uC635\uC158)</span>
<span class="token comment"># --system : \uC2DC\uC2A4\uD15C (PC \uC804\uCCB4\uC758 \uC0AC\uC6A9\uC790\uB97C \uC704\uD55C \uC635\uC158)</span>

<span class="token comment"># \uC9C0\uC815\uD55C \uC804\uC5ED \uC635\uC158\uC758 **\uB0B4\uC6A9\uC744 \uC0B4\uD3B4\uBCF8\uB2E4**.</span>
<span class="token function">git</span> config --global <span class="token operator">&lt;</span>\uC635\uC158\uBA85<span class="token operator">&gt;</span>

<span class="token comment"># \uC9C0\uC815\uD55C \uC804\uC5ED \uC635\uC158\uC758 **\uAC12\uC744 \uC0C8\uB85C \uC124\uC815\uD55C\uB2E4**.</span>
<span class="token function">git</span> config --global <span class="token operator">&lt;</span>\uC635\uC158\uBA85<span class="token operator">&gt;</span> <span class="token operator">&lt;</span>\uC0C8\uB85C\uC6B4 \uAC12<span class="token operator">&gt;</span>

<span class="token comment"># \uC9C0\uC815\uD55C \uC804\uC5ED \uC635\uC158\uC744 **\uC0AD\uC81C\uD55C\uB2E4**.</span>
<span class="token function">git</span> config --global --unset <span class="token operator">&lt;</span>\uC635\uC158\uBA85<span class="token operator">&gt;</span>

<span class="token comment"># \uD604\uC7AC \uD504\uB85C\uC81D\uD2B8\uC758 \uBAA8\uB4E0 \uC635\uC158\uC744 \uC0B4\uD3B4\uBCF8\uB2E4.</span>
<span class="token function">git</span> config --list
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>\uC6B0\uC120\uC21C\uC704\uB294 \uC9C0\uC5ED &gt; \uC804\uC5ED &gt; \uC2DC\uC2A4\uD15C \uC21C\uC73C\uB85C \uC9C0\uC5ED\uC635\uC158\uC774 \uAC00\uC7A5 \uC6B0\uC120\uC21C\uC704\uAC00 \uB192\uB2E4.</p><p>\uACF5\uC6A9 PC\uC5D0\uC11C\uB294 \uC9C0\uC5ED \uC635\uC158\uC744 \uC368\uC57C \uB2E4\uB978\uC0AC\uB78C\uB4E4\uC758 Git\uC5D0 \uC601\uD5A5\uC774 \uAC00\uC9C0 \uC54A\uB294\uB2E4.</p><div class="language-bash ext-sh line-numbers-mode"><pre class="language-bash"><code>$ <span class="token function">git</span> config --global user.name
<span class="token comment"># Giwon Lim</span>

$ <span class="token function">git</span> config --global user.name <span class="token string">&quot;Cat Hanbit&quot;</span>
<span class="token comment"># \uC0AC\uC6A9\uC790 \uC774\uB984\uC774 Cat Hanbit\uC73C\uB85C \uBC14\uB01C</span>

$ <span class="token function">git</span> config --system core.editor
<span class="token comment"># Git\uC5D0\uC11C \uC124\uC815\uB418\uC5B4 \uC788\uB294 default \uD14D\uC2A4\uD2B8 \uC5D0\uB514\uD130\uB97C \uC54C\uB824\uC90C</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>Git\uC5D0\uC11C \uC0AC\uC6A9\uD558\uB294 \uD14D\uC2A4\uD2B8 \uC5D0\uB514\uD130\uB294 default\uAC00 vim\uC774\uB098 nano\uB85C \uB418\uC5B4\uC788\uB294\uB370,</p><p>\uC0AC\uC6A9\uC790 \uD3B8\uC758\uC0C1 Visual Studio Code\uB85C \uBCC0\uACBD\uD558\uB294 \uAC83\uC774 \uC88B\uB2E4.</p><h2 id="\u110B\u116D\u110B\u1163\u11A8" tabindex="-1"><a class="header-anchor" href="#\u110B\u116D\u110B\u1163\u11A8" aria-hidden="true">#</a> \uC694\uC57D</h2><ol><li>\uC790\uC2E0\uC774 \uC6D0\uD558\uB294 \uB514\uB809\uD1A0\uB9AC\uB85C \uC774\uB3D9\uD55C\uB2E4.</li><li>&quot;mkdir \uD3F4\uB354\uC774\uB984&quot;\uC73C\uB85C Git \uC800\uC7A5\uC18C\uB85C \uC4F8 \uD3F4\uB354\uB97C \uC0DD\uC131\uD55C\uB2E4.</li><li>&quot;cd \uD3F4\uB354\uC774\uB984&quot;\uC73C\uB85C \uC0DD\uC131\uD55C \uD3F4\uB354\uB85C \uC774\uB3D9\uD55C\uB2E4.</li><li>&quot;git init&quot; \uC785\uB825 (&quot;git status&quot;\uB85C \uC81C\uB300\uB85C \uC800\uC7A5\uC18C\uAC00 \uB9CC\uB4E4\uC5B4\uC84C\uB294\uC9C0 \uD655\uC778\uD560 \uC218 \uC788\uB2E4.)</li></ol>`,35),r=[d];function p(u,m){return s(),a("div",null,r)}var v=n(o,[["render",p],["__file","1-2. Git Bash\uC758 \uAE30\uBCF8 \uBA85\uB839\uC5B4\uB4E4.html.vue"]]);export{v as default};
