import{e as i}from"./app.9d6573f6.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";var r="/images/TIL/ETC-Database/\uBA54\uBAA8\uB9AC-\uACC4\uCE35\uAD6C\uC870.png";const t={},e=i('<h1 id="\u110B\u1175\u11AB\u1106\u1166\u1106\u1169\u1105\u1175-\u1103\u1166\u110B\u1175\u1110\u1165\u1107\u1166\u110B\u1175\u1109\u1173" tabindex="-1"><a class="header-anchor" href="#\u110B\u1175\u11AB\u1106\u1166\u1106\u1169\u1105\u1175-\u1103\u1166\u110B\u1175\u1110\u1165\u1107\u1166\u110B\u1175\u1109\u1173" aria-hidden="true">#</a> \uC778\uBA54\uBAA8\uB9AC \uB370\uC774\uD130\uBCA0\uC774\uC2A4</h1><p>\uB514\uC2A4\uD06C\uAC00 \uC544\uB2CC \uC8FC \uBA54\uBAA8\uB9AC\uC5D0 \uBAA8\uB4E0 \uB370\uC774\uD130\uB97C \uBCF4\uC720\uD558\uACE0 \uC788\uB294 \uB370\uC774\uD130\uBCA0\uC774\uC2A4</p><h2 id="\u110C\u1161\u11BC\u110C\u1165\u11B7" tabindex="-1"><a class="header-anchor" href="#\u110C\u1161\u11BC\u110C\u1165\u11B7" aria-hidden="true">#</a> \uC7A5\uC810</h2><ul><li>\uB514\uC2A4\uD06C \uAC80\uC0C9\uBCF4\uB2E4 \uC790\uB8CC \uC811\uADFC\uC774 \uD6E8\uC52C \uBE60\uB974\uB2E4</li><li>\uBA54\uBAA8\uB9AC\uC0C1\uC5D0 \uC0C9\uC778(\uC778\uB371\uC2A4)\uB97C \uB123\uC5B4 \uBE60\uB974\uAC8C \uAC80\uC0C9\uD560 \uC218 \uC788\uB2E4.</li></ul><h2 id="\u1103\u1161\u11AB\u110C\u1165\u11B7" tabindex="-1"><a class="header-anchor" href="#\u1103\u1161\u11AB\u110C\u1165\u11B7" aria-hidden="true">#</a> \uB2E8\uC810</h2><ul><li>\uB370\uC774\uD130\uAC00 \uD718\uBC1C\uC131\uC774\uB2E4. (\uC601\uC18D\uC801\uC774\uC9C0 \uC54A\uB2E4)</li><li>\uC0AC\uC6A9\uD560 \uC218 \uC788\uB294 \uC6A9\uB7C9\uC774 RAM\uC758 \uC6A9\uB7C9\uC5D0 \uC88C\uC6B0\uB41C\uB2E4.</li></ul><div class="custom-container tip"><p class="custom-container-title">ACID</p><p>\uB370\uC774\uD130\uC758 \uC720\uD6A8\uC131\uC744 \uBCF4\uC7A5\uD558\uAE30 \uC704\uD55C \uD2B8\uB79C\uC7AD\uC158\uC758 \uC131\uC9C8\uC774\uB2E4.</p><ul><li><strong>Atomicity(\uC6D0\uC790\uC131)</strong> : \uD2B8\uB79C\uC7AD\uC158\uACFC \uAD00\uB828\uB41C \uC791\uC5C5\uB4E4\uC758 \uC5F0\uC0B0\uACB0\uACFC\uAC00 \uC804\uBD80 \uBC18\uC601\uB418\uAC70\uB098 \uBC18\uC601\uB418\uC9C0 \uB9D0\uC544\uC57C \uD55C\uB2E4.</li><li><strong>Consistency(\uC77C\uAD00\uC131)</strong> : \uD2B8\uB79C\uC7AD\uC158\uC774 \uC131\uACF5\uC801\uC73C\uB85C \uC644\uB8CC\uD558\uBA74 \uC5B8\uC81C\uB098 \uC77C\uAD00\uC131 \uC788\uB294 \uB370\uC774\uD130\uBCA0\uC774\uC2A4 \uC0C1\uD0DC\uB85C \uC720\uC9C0\uD55C\uB2E4.</li><li><strong>Isolation(\uB3C5\uB9BD\uC131)</strong> : \uD2B8\uB79C\uC7AD\uC158\uC744 \uC218\uD589 \uC2DC \uB2E4\uB978 \uD2B8\uB79C\uC7AD\uC158\uC758 \uC5F0\uC0B0 \uC791\uC5C5\uC774 \uB07C\uC5B4\uB4E4\uC9C0 \uBABB\uD558\uB3C4\uB85D \uBCF4\uC7A5\uD55C\uB2E4.</li><li><strong>Durability(\uC9C0\uC18D\uC131)</strong> : \uC131\uACF5\uC801\uC73C\uB85C \uC218\uD589\uB41C \uD2B8\uB79C\uC7AD\uC158\uC740 \uC2DC\uC2A4\uD15C\uC774 \uACE0\uC7A5\uB098\uB354\uB77C\uB3C4 \uC601\uAD6C\uC801\uC73C\uB85C \uBC18\uC601\uB418\uC5B4\uC57C \uD55C\uB2E4.</li></ul></div><div class="custom-container warning"><p class="custom-container-title">\uC8FC\uC758</p><p>\uC778\uBA54\uBAA8\uB9AC DB\uB294 \uAF2D Nosql\uC774\uC9C0 \uC54A\uB2E4. \uAC00\uC7A5 \uC720\uBA85\uD55C Redis\uAC00 Nosql\uC774\uB77C\uC11C \uD5F7\uAC08\uB9AC\uB294 \uACBD\uC6B0\uAC00 \uB9CE\uC740\uB370 Mysql \uB610\uD55C \uC778\uBA54\uBAA8\uB9AC \uAE30\uB2A5\uC744 \uC9C0\uC6D0\uD55C\uB2E4.</p></div><h2 id="\u110B\u116B-\u1108\u1161\u1105\u1173\u11AF\u1101\u1161" tabindex="-1"><a class="header-anchor" href="#\u110B\u116B-\u1108\u1161\u1105\u1173\u11AF\u1101\u1161" aria-hidden="true">#</a> \uC65C \uBE60\uB97C\uAE4C?</h2><p><img src="'+r+'" alt="\uBA54\uBAA8\uB9AC \uACC4\uCE35\uAD6C\uC870"> \uC778\uBA54\uBAA8\uB9AC DB\uAC00 \uC704\uCE58\uD558\uB294 \uACF3\uC740 RAM(\uC8FC \uAE30\uC5B5\uC7A5\uCE58)\uC774\uB2E4.</p><p>\uB514\uC2A4\uD06C \uAE30\uBC18 \uC800\uC7A5\uC7A5\uCE58(HDD, SDD)\uB294 <strong>\uBCF4\uC870 \uAE30\uC5B5 \uC7A5\uCE58</strong>\uB77C\uACE0 \uBD88\uB9B0\uB2E4.</p><p>\uC678\uBD80 \uB514\uC2A4\uD06C\uC758 \uB370\uC774\uD130\uC5D0 \uC811\uADFC\uD558\uAE30 \uC704\uD574\uC11C\uB294, \uB370\uC774\uD130\uB97C \uCE90\uC2DC\uB098 RAM\uC73C\uB85C \uC774\uB3D9\uC2DC\uD0A4\uACE0\uB098\uC11C \uC811\uADFC\uD560 \uC218 \uC788\uB2E4.</p><p>\uC544\uBB34\uB798\uB3C4 \uB370\uC774\uD130\uB97C RAM\uC73C\uB85C \uAC00\uC838\uC640\uC11C \uC77D\uC5B4\uC57C\uD55C\uB2E4\uB294 \uC810 \uB54C\uBB38\uC5D0 \uB290\uB9B4 \uC218 \uBC16\uC5D0 \uC5C6\uB294\uB370, <strong>\uC778 \uBA54\uBAA8\uB9AC DB\uB97C \uC0AC\uC6A9\uD558\uBA74 \uB370\uC774\uD130\uB97C \uAC00\uC838\uC624\uB294 \uB2E8\uACC4\uB97C \uC0DD\uB7B5\uD560 \uC218 \uC788\uB2E4.</strong></p><p>\uD558\uC9C0\uB9CC RAM\uC740 \uC601\uC18D\uC801\uC774\uC9C0 \uBABB\uD574\uC11C, \uC778\uBA54\uBAA8\uB9AC DB\uB97C \uC0AC\uC6A9\uD560 \uB54C\uB294 \uC601\uC18D\uC801\uC77C \uD544\uC694\uAC00 \uC5C6\uACE0 \uC8FC\uB85C READ\uD558\uAE30 \uC704\uD55C \uB370\uC774\uD130\uB9CC \uC774\uC6A9\uD558\uBA74 \uD6A8\uC728\uC774 \uC88B\uB2E4.</p><h2 id="register-cache\u110B\u1166\u1109\u1165-db\u1105\u1173\u11AF-\u1109\u1161\u110B\u116D\u11BC\u1112\u1161\u11AF-\u1109\u116E\u1102\u1173\u11AB-\u110B\u1165\u11B9\u110B\u1173\u11AF\u1101\u1161" tabindex="-1"><a class="header-anchor" href="#register-cache\u110B\u1166\u1109\u1165-db\u1105\u1173\u11AF-\u1109\u1161\u110B\u116D\u11BC\u1112\u1161\u11AF-\u1109\u116E\u1102\u1173\u11AB-\u110B\u1165\u11B9\u110B\u1173\u11AF\u1101\u1161" aria-hidden="true">#</a> Register, Cache\uC5D0\uC11C DB\uB97C \uC0AC\uC6A9\uD560 \uC218\uB294 \uC5C6\uC744\uAE4C?</h2><p>\uC774\uC720\uB97C \uC54C\uAE30 \uC704\uD574\uC11C\uB294 \uC77C\uB2E8 \uB808\uC9C0\uC2A4\uD130\uC640 \uCE90\uC2DC\uC5D0 \uB300\uD574 \uC54C\uC544\uC57C \uD55C\uB2E4.</p><div class="custom-container tip"><p class="custom-container-title">TIP</p><ul><li><p><strong>Register</strong> \uCEF4\uD4E8\uD130\uC5D0\uC11C \uC81C\uC77C \uBE60\uB978 \uBA54\uBAA8\uB9AC\uC774\uB2E4. CPU \uB0B4\uBD80\uC5D0 \uC704\uCE58\uD574 \uC788\uC73C\uBA70, \uADF9\uD788 \uC18C\uB7C9\uC758 \uB370\uC774\uD130\uB098 \uD504\uB85C\uC138\uC11C\uAC00 \uBC14\uB85C \uC0AC\uC6A9\uD560 \uC218 \uC788\uB294 \uB370\uC774\uD130\uB97C \uB2F4\uACE0 \uC788\uB294 \uC601\uC5ED\uC774\uB2E4.</p></li><li><p><strong>Cache</strong> \uB808\uC9C0\uC2A4\uD130 \uB2E4\uC74C\uC73C\uB85C \uBE60\uB978 \uBA54\uBAA8\uB9AC\uC774\uBA70 CPU \uB0B4\uBD80\uC5D0 \uC874\uC7AC\uD55C\uB2E4. RAM\uC774 CPU \uC678\uBD80\uC5D0 \uC704\uCE58\uD558\uAE30 \uB54C\uBB38\uC5D0 \uC18D\uB3C4 \uCC28\uC774\uB85C \uC778\uD55C \uC131\uB2A5 \uC800\uD558\uB97C \uB9C9\uAE30 \uC704\uD574 \uC0AC\uC6A9\uB41C\uB2E4. \uC18D\uB3C4\uC5D0 \uB530\uB77C L1, L2, L3 \uCE90\uC2DC \uB4F1\uC73C\uB85C \uBD84\uB958\uB418\uBA70, \uC22B\uC790\uAC00 \uC791\uC744\uC218\uB85D \uC6A9\uB7C9\uC774 \uC791\uACE0 \uBE60\uB974\uB2E4. \uC9C0\uC5ED\uC131\uC5D0 \uC758\uAC70\uD558\uC5EC \uC790\uC8FC \uCC38\uC870\uB420\uAC83 \uAC19\uC740 \uB370\uC774\uD130\uB4E4\uC774 \uC800\uC7A5\uB41C\uB2E4.</p></li></ul></div><p>\uB458 \uB2E4 CPU \uB0B4\uBD80\uC5D0 \uC704\uCE58\uD55C\uB2E4.</p><p>CPU\uAC00 \uC9C1\uC811 \uC0AC\uC6A9\uD558\uB294 \uACF5\uAC04\uC774\uAE30\uB3C4 \uD558\uACE0, \uC6B0\uB9AC\uAC00 \uC9C1\uC811 \uCEE8\uD2B8\uB864\uD558\uAE30 \uD798\uB4E4\uB2E4. \uBB34\uC5C7\uBCF4\uB2E4 <strong>\uC6A9\uB7C9\uC774 \uC791\uB2E4.</strong></p><p>\uC774\uB7EC\uC800\uB7EC\uD55C \uC774\uC720\uB85C RAM\uC5D0\uC11C DB\uB97C \uC0AC\uC6A9\uD55C\uB2E4.</p>',20);function s(n,o){return e}var p=a(t,[["render",s]]);export{p as default};
