import{e as a}from"./app.b0ae6393.js";import{_ as r}from"./plugin-vue_export-helper.21dcd24c.js";const i={},e=a('<h1 id="_1-javascript-\u1107\u1167\u11AB\u110E\u1165\u11AB\u1109\u1161" tabindex="-1"><a class="header-anchor" href="#_1-javascript-\u1107\u1167\u11AB\u110E\u1165\u11AB\u1109\u1161" aria-hidden="true">#</a> 1. Javascript \uBCC0\uCC9C\uC0AC</h1><h3 id="_1-1-javascript-\u1107\u1165\u110C\u1165\u11AB" tabindex="-1"><a class="header-anchor" href="#_1-1-javascript-\u1107\u1165\u110C\u1165\u11AB" aria-hidden="true">#</a> 1-1. Javascript \uBC84\uC804</h3><p><strong>1995\uB144</strong>, \uD30C\uC774\uC5B4\uD3ED\uC2A4 \uBE0C\uB77C\uC6B0\uC800\uC758 \uC804\uC2E0\uC778 NetScape \uBE0C\uB77C\uC6B0\uC800\uC5D0 LiveScript\uB77C\uB294 \uC774\uB984\uC73C\uB85C \uCC98\uC74C \uD0D1\uC7AC\uB428</p><p>(\uCD08\uAE30\uC5D0\uB294 HTML\uC744 \uAC04\uB2E8\uD558\uAC8C \uC870\uC791\uD558\uAE30 \uC704\uD574 \uB9CC\uB4E4\uC5B4\uC9C4 \uD504\uB85C\uADF8\uB7A8\uC774\uC5C8\uB2E4.)</p><p><strong>1996\uB144 8\uC6D4</strong>, Internet Explorer\uC5D0 Jscript\uB77C\uB294 \uBE44\uC2B7\uD55C \uAE30\uB2A5\uC758 \uC2A4\uD06C\uB9BD\uD2B8 \uC5B8\uC5B4\uAC00 \uD0D1\uC7AC\uB428.</p><h3 id="_1-2-ecmascript" tabindex="-1"><a class="header-anchor" href="#_1-2-ecmascript" aria-hidden="true">#</a> 1-2. ECMAScript</h3><p>1997\uB144, EcmaScript\uAC00 \uCC98\uC74C \uB098\uC654\uACE0 2009\uB144\uAE4C\uC9C0 12\uB144\uB3D9\uC548 EcmaScript5\uAE4C\uC9C0 \uBC84\uC804 \uC5C5\uB370\uC774\uD2B8\uB428</p><h1 id="_2-\u110B\u1170\u11B8\u110B\u1162\u11B8\u110B\u1174-\u1100\u116E\u1109\u1165\u11BC\u110B\u116D\u1109\u1169" tabindex="-1"><a class="header-anchor" href="#_2-\u110B\u1170\u11B8\u110B\u1162\u11B8\u110B\u1174-\u1100\u116E\u1109\u1165\u11BC\u110B\u116D\u1109\u1169" aria-hidden="true">#</a> 2. \uC6F9\uC571\uC758 \uAD6C\uC131\uC694\uC18C</h1><h3 id="_2-1-\u110B\u1170\u11B8\u110B\u1162\u11B8\u110B\u1174-\u1100\u1175\u1107\u1169\u11AB-\u1100\u116E\u1109\u1165\u11BC\u110B\u116D\u1109\u1169-html-css-javascript" tabindex="-1"><a class="header-anchor" href="#_2-1-\u110B\u1170\u11B8\u110B\u1162\u11B8\u110B\u1174-\u1100\u1175\u1107\u1169\u11AB-\u1100\u116E\u1109\u1165\u11BC\u110B\u116D\u1109\u1169-html-css-javascript" aria-hidden="true">#</a> 2-1. \uC6F9\uC571\uC758 \uAE30\uBCF8 \uAD6C\uC131\uC694\uC18C - HTML, CSS, Javascript</h3><ul><li>HTML \u2192 \uAE30\uBCF8\uC801\uC778 UI\uB97C \uB9CC\uB4E4\uC5B4\uC90C</li><li>CSS \u2192 \uADF8 UI\uC758 \uBE44\uC8FC\uC5BC\uC801\uC778 \uC694\uC18C\uB4E4\uC744 \uB2F4\uB2F9\uD568</li><li>Javascript \u2192 \uB3D9\uC801\uC778, \uD504\uB85C\uADF8\uB798\uBC0D\uC801\uC778 \uBD80\uBD84\uB4E4\uC744 \uB2F4\uB2F9\uD568</li></ul><p><strong>\uD398\uC774\uC9C0(\uBB38\uC11C)</strong> : Javascript\uAC00 \uC5C6\uC774 HTML, CSS\uB85C\uB9CC \uB9CC\uB4E4\uC5B4\uC9C4 \uC815\uC801 \uC6F9\uD398\uC774\uC9C0</p><p><strong>\uC571(\uC560\uD50C\uB9AC\uCF00\uC774\uC158)</strong> : \uC0C1\uD0DC\uB77C\uB294 \uAC83\uC744 \uAC16\uACE0 \uC788\uACE0, \uC0AC\uC6A9\uC790\uC640 \uC0C1\uD638\uC801\uC73C\uB85C \uC8FC\uACE0\uBC1B\uC744 \uC218 \uC788\uB294 \uB2E4\uC591\uD55C \uAE30\uB2A5\uC744 \uC81C\uACF5\uD574\uC8FC\uB294 \uAC83. \uC571\uC774 \uB418\uAE30 \uC704\uD574\uC11C\uB294 \uBC18\uB4DC\uC2DC Javascript\uAC00 \uC788\uC5B4\uC57C\uD568.</p><h3 id="_2-2-\u1109\u1175\u11AF\u1112\u1162\u11BC-\u1100\u116A\u11AB\u110C\u1165\u11B7\u110B\u1174-\u1100\u116E\u1109\u1165\u11BC\u110B\u116D\u1109\u1169" tabindex="-1"><a class="header-anchor" href="#_2-2-\u1109\u1175\u11AF\u1112\u1162\u11BC-\u1100\u116A\u11AB\u110C\u1165\u11B7\u110B\u1174-\u1100\u116E\u1109\u1165\u11BC\u110B\u116D\u1109\u1169" aria-hidden="true">#</a> 2-2. \uC2E4\uD589 \uAD00\uC810\uC758 \uAD6C\uC131\uC694\uC18C</h3><ul><li><strong>\uC6F9\uC571\uC774 \uC2E4\uD589\uB418\uB294 \uACFC\uC815</strong><ol><li>\uC0AC\uC6A9\uC790\uAC00 \uBE0C\uB77C\uC6B0\uC800\uB97C \uC2E4\uD589\uC2DC\uD0A8 \uB2E4\uC74C\uC5D0 \uC8FC\uC18C\uCC3D\uC5D0 \uD648\uD398\uC774\uC9C0 \uC8FC\uC18C\uB97C \uC785\uB825 \uD558\uBA74</li><li>\uC6F9\uC0AC\uC774\uD2B8\uC5D0 \uC811\uC18D \uD558\uBA74 HTML, CSS, Javascript \uD30C\uC77C\uB4E4\uC774 \uBB3C\uB9AC\uC801\uC73C\uB85C \uC0AC\uC6A9\uC790\uC758 \uBE0C\uB77C\uC6B0\uC800\uC5D0 \uC804\uC1A1\uB428</li><li>\uBE0C\uB77C\uC6B0\uC800\uAC00 \uADF8 \uD30C\uC77C\uB4E4\uC744 \uD574\uC11D\uD574\uC11C \uC2E4\uD589\uC2DC\uD0B4</li></ol></li><li><strong>\uC6F9\uC571\uC5D0\uAC8C \uB7F0\uD0C0\uC784(\uC2E4\uD589\uB418\uB294) \uD658\uACBD\uC744 \uC81C\uACF5\uD558\uB294 \uAC83\uB4E4</strong><ol><li>\uBE0C\uB77C\uC6B0\uC800</li><li>node.js</li></ol></li></ul><h3 id="_2-3-csr-ssr" tabindex="-1"><a class="header-anchor" href="#_2-3-csr-ssr" aria-hidden="true">#</a> 2-3. CSR &amp; SSR</h3><p><strong>CSR(Client Side Rendering)</strong> - \uBE0C\uB77C\uC6B0\uC800\uC5D0\uC11C \uC2E4\uD589\uB418\uB294 Javascri pt\uC758 \uC2E4\uD589 \uACB0\uACFC\uB85C HTML\uC744 \uC8FC\uB3C4\uC801\uC73C\uB85C \uB9CC\uB4E4\uC5B4\uC11C UI\uB97C \uD45C\uD604\uD558\uB294 \uBC29\uC2DD</p><p><strong>SSR(Server Side Rendering)</strong> - \uC6F9 \uC11C\uBC84\uC5D0\uC11C \uC8FC\uB3C4\uC801\uC73C\uB85C HTML\uC744 \uB9CC\uB4E4\uACE0 \uBE0C\uB77C\uC6B0\uC800\uC5D0\uAC8C \uC804\uC1A1\uD558\uB294 \uBC29\uC2DD</p><h3 id="_2-4-\u1100\u1173\u1105\u1162\u1111\u1175\u11A8-\u1109\u1175\u1109\u1173\u1110\u1166\u11B7" tabindex="-1"><a class="header-anchor" href="#_2-4-\u1100\u1173\u1105\u1162\u1111\u1175\u11A8-\u1109\u1175\u1109\u1173\u1110\u1166\u11B7" aria-hidden="true">#</a> 2-4. \uADF8\uB798\uD53D \uC2DC\uC2A4\uD15C</h3><h1 id="_3-\u1106\u1169\u1103\u1165\u11AB-javascript\u110B\u116A-\u1100\u1162\u1107\u1161\u11AF-\u1112\u116A\u11AB\u1100\u1167\u11BC" tabindex="-1"><a class="header-anchor" href="#_3-\u1106\u1169\u1103\u1165\u11AB-javascript\u110B\u116A-\u1100\u1162\u1107\u1161\u11AF-\u1112\u116A\u11AB\u1100\u1167\u11BC" aria-hidden="true">#</a> 3. \uBAA8\uB358 Javascript\uC640 \uAC1C\uBC1C \uD658\uACBD</h1><p>\uBCF4\uD1B5 ES2015 \uBC84\uC804\uBD80\uD130 \uBAA8\uB358 Javascript\uB77C\uACE0 \uD1B5\uCE6D\uD568</p><h3 id="_3-1-\u1106\u1169\u1103\u1172\u11AF" tabindex="-1"><a class="header-anchor" href="#_3-1-\u1106\u1169\u1103\u1172\u11AF" aria-hidden="true">#</a> 3-1. \uBAA8\uB4C8</h3><p>\uD2B9\uC815 \uD30C\uC77C\uC758 \uC5B4\uB5A4 \uAE30\uB2A5\uC744 \uC0AC\uC6A9\uD558\uAE30 \uC704\uD574\uC11C \uBD88\uB7EC\uB4E4\uC5EC\uC11C \uC4F8 \uC218 \uC788\uB294 \uAE30\uB2A5.</p><p>Javascript\uC5D0\uC11C\uB294 js\uD30C\uC77C \uC548\uC5D0\uC11C \uB2E4\uB978 js\uD30C\uC77C\uC744 \uC0AC\uC6A9\uD558\uAE30 \uC704\uD574\uC11C \uC0AC\uC6A9\uD558\uACE0 import, export\uB77C\uB294 \uBB38\uBC95\uC73C\uB85C \uBAA8\uB4C8\uC744 \uC9C0\uC6D0\uD568.</p><h3 id="_3-2-\u1107\u1165\u11AB\u1103\u1173\u11AF\u1105\u1165" tabindex="-1"><a class="header-anchor" href="#_3-2-\u1107\u1165\u11AB\u1103\u1173\u11AF\u1105\u1165" aria-hidden="true">#</a> 3-2. \uBC88\uB4E4\uB7EC</h3><p>\uC2DC\uC791\uB418\uB294 Javascript\uD30C\uC77C\uC744 \uC9C0\uC815\uD558\uBA74 \uADF8 \uD30C\uC77C \uC548\uC5D0\uC11C \uB2E4\uB978 Javascript \uD30C\uC77C\uC744 \uBD88\uB7EC\uC62C \uC218 \uC788\uC74C</p><p>\uADF8\uB807\uAC8C \uBD88\uB7EC\uC640\uC11C \uD558\uB098\uC758 \uD30C\uC77C\uB85C \uB9CC\uB4E4\uC5B4\uC8FC\uB294 \uAC83\uC774 \uBC14\uB85C <strong>\uBC88\uB4E4\uB7EC</strong>!</p><p>js\uD30C\uC77C \uC678\uC5D0\uB3C4 sass, jpg, png \uB4F1\uB4F1 \uC5EC\uB7EC \uD655\uC7A5\uC790\uB3C4 js, css\uB4F1\uC73C\uB85C \uBCC0\uD658 \uAC00\uB2A5\uD558\uB2E4.</p><ul><li><p>\uC7A5\uC810</p><ol><li>HTML\uC5D0\uC11C \uD558\uB098\uC758 js\uD30C\uC77C\uB9CC import \uD558\uBA74 \uB428</li><li>\uBAA8\uB4C8\uC744 \uC9C0\uC6D0\uD558\uC9C0 \uC54A\uB294 \uC0C1\uD669\uC5D0\uC11C\uB3C4 import\uD574\uC11C \uC4F8 \uC218 \uC788\uAC8C \uB410\uB2E4.</li></ol></li><li><p>\uB300\uD45C\uC801\uC778 \uBC88\uB4E4\uB7EC - Webpack</p></li></ul><p><img src="https://user-images.githubusercontent.com/72638829/129926082-ea453c10-5f98-44c0-b7d4-474ee6b0b058.png" alt="Untitled"></p><ul><li>\uD2B8\uB79C\uC2A4\uD30C\uC77C\uB9C1 - \uB2E4\uB978 \uC5B8\uC5B4\uB97C javscript\uB85C \uBCC0\uD658\uD574\uC90C</li><li>\uD2B8\uB79C\uC2A4\uD30C\uC77C\uB7EC\uB97C \uC774\uC6A9\uD558\uBA74 \uCD5C\uC2E0\uBB38\uBC95\uC758 javascript\uB97C \uC608\uC804 \uBC84\uC804\uC758 javascript\uB85C \uBCC0\uD658\uD560 \uC218 \uC788\uC74C</li><li>\uB300\uD45C\uC801\uC778 \uD2B8\uB79C\uC2A4\uD30C\uC77C\uB7EC - Babel</li></ul><p>\uBC88\uB4E4\uB7EC\uB97C \uC774\uC6A9\uD574\uC11C \uC5EC\uB7EC js\uD30C\uC77C\uC744 \uD558\uB098\uC758 js\uD30C\uC77C\uB85C \uB9CC\uB4E4\uC5B4\uC904 \uB54C, Babel\uC744 \uC774\uC6A9\uD558\uC5EC \uBE0C\uB77C\uC6B0\uC800 \uD638\uD658\uC131\uC744 \uB192\uC5EC\uC90C</p><h1 id="_4-nodejs-npm" tabindex="-1"><a class="header-anchor" href="#_4-nodejs-npm" aria-hidden="true">#</a> 4. nodejs &amp; npm</h1><ul><li>nodejs : v8\uC5D4\uC9C4 \uC624\uD508\uC18C\uC2A4\uB97C \uAE30\uBC18\uC73C\uB85C \uC77C\uBC18 \uC6B4\uC601\uCCB4\uC81C \uD658\uACBD\uC5D0\uC11C\uB3C4 javascript\uB97C \uC2E4\uD589 \uD560 \uC218 \uC788\uB294 \uB7F0\uD0C0\uC784 \uD658\uACBD\uC744 \uC9C0\uC6D0\uD558\uB294 \uC5B4\uD50C\uB9AC\uCF00\uC774\uC158</li><li>npm : nodejs\uAC00 \uC81C\uACF5\uD558\uB294 \uC5EC\uB7EC\uAC00\uC9C0 \uD658\uACBD. npm\uC744 \uD1B5\uD574\uC11C nodejs \uAD00\uB828 \uB77C\uC774\uBE0C\uB7EC\uB9AC \uAD00\uB9AC\uAC00 \uAC00\uB2A5\uD568</li></ul><p>npm [install or i] [\uB77C\uC774\uBE0C\uB7EC\uB9AC\uBA85] \u2192 node_modules\uC5D0 \uD574\uB2F9 \uB77C\uC774\uBE0C\uB7EC\uB9AC\uB97C \uC124\uCE58\uD568</p>',34);function s(t,p){return e}var d=r(i,[["render",s]]);export{d as default};
