import{e as i}from"./app.bdfb3c5f.js";import{_ as l}from"./plugin-vue_export-helper.21dcd24c.js";const e={},a=i('<h1 id="tcp-udp-\u1111\u1173\u1105\u1169\u1110\u1169\u110F\u1169\u11AF" tabindex="-1"><a class="header-anchor" href="#tcp-udp-\u1111\u1173\u1105\u1169\u1110\u1169\u110F\u1169\u11AF" aria-hidden="true">#</a> TCP / UDP \uD504\uB85C\uD1A0\uCF5C</h1><p>TCP/IP \uC5D0\uC11C\uC758 \uC804\uC1A1 \uACC4\uCE35\uC5D0 \uD574\uB2F9\uD558\uB294 \uD504\uB85C\uD1A0\uCF5C\uB4E4\uC774\uBA70, \uC804\uC1A1\uACC4\uCE35\uC758 \uD504\uB85C\uD1A0\uCF5C\uB4E4\uC740 <code>process-to-process protocol</code>\uC774\uB2E4.</p><div class="custom-container tip"><p class="custom-container-title">IP \uD504\uB85C\uD1A0\uCF5C\uC758 \uACBD\uC6B0</p><p>host-to-host protocol</p></div><h2 id="udp-user-datagram-protocol" tabindex="-1"><a class="header-anchor" href="#udp-user-datagram-protocol" aria-hidden="true">#</a> UDP (User DataGram Protocol)</h2><ul><li><strong>\uBE44\uC5F0\uACB0\uC131</strong>\uACFC <strong>\uBE44\uC2E0\uB8B0\uC131</strong></li><li>\uAE30\uBCF8\uC801\uC778 IP \uB370\uC774\uD130 \uC804\uB2EC \uC11C\uBE44\uC2A4\uC5D0 \uD504\uB85C\uC138\uC2A4\uAC04 \uD1B5\uC2E0\uC774 \uCD94\uAC00\uB41C \uD615\uD0DC</li><li>\uC5F0\uACB0\uC131 \uBCF4\uC7A5\uACFC \uC2E0\uB8B0\uC801 \uC804\uC1A1\uC5D0 \uC18C\uC694\uB418\uB294 \uC624\uBC84\uD5E4\uB4DC\uAC00 \uC5C6\uC74C</li><li>\uC751\uC6A9 \uACC4\uCE35\uC5D0\uC11C \uB370\uC774\uD130\uB97C \uCC98\uB9AC\uD574\uC904\uB54C \uC624\uBC84\uD5E4\uB4DC\uAC00 \uBC1C\uC0DD\uD560 \uC218\uB3C4 \uC788\uC74C</li><li>\uAC04\uB2E8\uD55C \uBA54\uC2DC\uC9C0\uB97C \uC1A1\uC218\uC2E0\uD560 \uB54C \uC720\uB9AC</li></ul><h3 id="\u1110\u1173\u11A8\u110C\u1175\u11BC" tabindex="-1"><a class="header-anchor" href="#\u1110\u1173\u11A8\u110C\u1175\u11BC" aria-hidden="true">#</a> \uD2B9\uC9D5</h3><ul><li>\uBE44\uC5F0\uACB0\uD615 IP \uC804\uB2EC \uC11C\uBE44\uC2A4 + \uAC80\uC0AC\uD569(checksum) \uAE30\uB2A5</li><li>\uCD5C\uC885 \uBAA9\uC801\uC9C0\uC758 \uB370\uC774\uD130 \uC218\uC2E0 \uC5EC\uBD80\uB97C \uD655\uC778\uD558\uC9C0 \uC54A\uC74C (\uB370\uC774\uD130 \uC190\uC2E4 \uAC00\uB2A5\uC131 O)</li><li>\uBA54\uC2DC\uC9C0\uC758 \uB3C4\uCC29 \uC21C\uC11C\uB97C \uC7AC\uC870\uC815\uD558\uC9C0 \uC54A\uC74C (\uB370\uC774\uD130 \uC21C\uC11C \uD2C0\uB9B4 \uAC00\uB2A5\uC131 O)</li><li>\uD638\uC2A4\uD2B8 \uC0AC\uC774\uC5D0 \uB370\uC774\uD130 \uD750\uB984 \uC81C\uC5B4 \uC5C6\uC74C (\uC218\uC2E0\uC790 \uCC98\uB9AC \uC6A9\uB7C9\uC744 \uCD08\uACFC\uD558\uB294 \uB370\uC774\uD130 \uBC1C\uC0DD \uAC00\uB2A5)</li></ul><h3 id="\u110C\u1161\u11BC\u1103\u1161\u11AB\u110C\u1165\u11B7" tabindex="-1"><a class="header-anchor" href="#\u110C\u1161\u11BC\u1103\u1161\u11AB\u110C\u1165\u11B7" aria-hidden="true">#</a> \uC7A5\uB2E8\uC810</h3><ul><li>\uC7A5\uC810 : TCP\uBCF4\uB2E4 \uB370\uC774\uD130 \uC804\uC1A1\uC18D\uB3C4\uAC00 \uBE60\uB974\uACE0 \uC751\uC6A9 \uD504\uB85C\uADF8\uB7A8\uC774 \uAC04\uB2E8\uD574\uC9D0</li><li>\uB2E8\uC810 : UDP\uB97C \uC0AC\uC6A9\uD558\uB294 \uC751\uC6A9 \uD504\uB85C\uADF8\uB7A8\uC740 <strong>\uBA54\uC2DC\uC9C0 \uC190\uC2E4, \uC911\uBCF5 \uC218\uC2E0, \uC218\uC2E0 \uC9C0\uC5F0, \uC798\uBABB\uB41C \uC21C\uC11C \uB4F1\uC744 \uCC98\uB9AC\uD558\uAE30 \uC704\uD55C \uC2E0\uB8B0\uC131 \uC81C\uC5B4 \uAE30\uB2A5\uC744 \uC81C\uACF5\uD574\uC57C\uD568</strong></li></ul><h3 id="udp-\u1109\u1161\u110B\u116D\u11BC\u110C\u1161-\u1103\u1166\u110B\u1175\u1110\u1165\u1100\u1173\u1105\u1162\u11B7" tabindex="-1"><a class="header-anchor" href="#udp-\u1109\u1161\u110B\u116D\u11BC\u110C\u1161-\u1103\u1166\u110B\u1175\u1110\u1165\u1100\u1173\u1105\u1162\u11B7" aria-hidden="true">#</a> UDP \uC0AC\uC6A9\uC790 \uB370\uC774\uD130\uADF8\uB7A8</h3><ul><li><p>\uAC00\uC9DC \uD5E4\uB354 \uBD80\uBD84 (pseudo header)</p><ol><li>\uBC1C\uC2E0\uC9C0 IP \uC8FC\uC18C (32 bit)</li><li>\uBAA9\uC801\uC9C0 IP \uC8FC\uC18C (32 bit)</li><li>\uC81C\uB85C\uBC14\uC774\uD2B8 (8 bit)</li><li>\uD504\uB85C\uD1A0\uCF5C \uD0C0\uC785 (8 bit)</li><li>\uD5E4\uB354 \uAE38\uC774 (16 bit)</li></ol></li><li><p>UDP \uD5E4\uB354 \uBD80\uBD84</p><ol><li>\uBC1C\uC2E0\uC9C0 \uD3EC\uD2B8(16 bit)</li><li>\uBAA9\uC801\uC9C0 \uD3EC\uD2B8(16 bit)</li><li>\uB370\uC774\uD130\uADF8\uB7A8 \uC804\uCCB4 \uAE38\uC774(16 bit) (=\uD5E4\uB354 \uAE38\uC774 + \uB370\uC774\uD130 \uAE38\uC774)</li><li>UDP \uAC80\uC0AC\uD569(16 bit) : \uB370\uC774\uD130\uADF8\uB7A8 \uC804\uCCB4\uC758 \uC624\uB958 \uAC80\uC0AC\uC6A9</li></ol></li><li><p>\uB370\uC774\uD130 \uBD80\uBD84</p></li></ul><h3 id="process-to-process-\u1110\u1169\u11BC\u1109\u1175\u11AB" tabindex="-1"><a class="header-anchor" href="#process-to-process-\u1110\u1169\u11BC\u1109\u1175\u11AB" aria-hidden="true">#</a> Process-to-process \uD1B5\uC2E0</h3><p>\uD3EC\uD2B8\uBC88\uD638\uB97C \uD1B5\uD574\uC11C Process-to-process\uB97C \uC5F0\uACB0\uD574\uC900\uB2E4.</p><ul><li>\uD3EC\uD2B8 \uBC88\uD638 <ul><li>\uC11C\uBC84 \uD504\uB85C\uC138\uC2A4\uB294 \uC798 \uC54C\uB824\uC9C4 \uD3EC\uD2B8 \uBC88\uD638 \uC0AC\uC6A9</li><li>\uD074\uB77C\uC774\uC5B8\uD2B8 \uD504\uB85C\uC138\uC2A4\uB294 \uC784\uC2DC \uD3EC\uD2B8 \uBC88\uD638 \uC0AC\uC6A9</li></ul></li></ul><h2 id="tcp-transmission-control-protocol" tabindex="-1"><a class="header-anchor" href="#tcp-transmission-control-protocol" aria-hidden="true">#</a> TCP (Transmission Control Protocol)</h2><ul><li>\uC5F0\uACB0\uC9C0\uD5A5(connected-oriented) \uC804\uC1A1 \uC11C\uBE44\uC2A4 (\uC5F0\uACB0 \uC124\uC815 - \uB370\uC774\uD130 \uC804\uC1A1 - \uC5F0\uACB0 \uD574\uC81C)</li><li>\uC2E0\uB8B0\uC131 (\uD750\uB984\uC81C\uC5B4, \uC624\uB958\uC81C\uC5B4)</li><li>\uBC29\uB300\uD55C \uC591\uC758 \uB370\uC774\uD130 \uC804\uC1A1\uD560\uB54C \uC8FC\uB85C \uC0AC\uC6A9</li></ul><h3 id="\u1109\u1175\u11AB\u1105\u116C\u1109\u1165\u11BC-\u110C\u1166\u1100\u1169\u11BC-\u1107\u1161\u11BC\u1107\u1165\u11B8" tabindex="-1"><a class="header-anchor" href="#\u1109\u1175\u11AB\u1105\u116C\u1109\u1165\u11BC-\u110C\u1166\u1100\u1169\u11BC-\u1107\u1161\u11BC\u1107\u1165\u11B8" aria-hidden="true">#</a> \uC2E0\uB8B0\uC131 \uC81C\uACF5 \uBC29\uBC95</h3><ul><li>\uC5F0\uACB0\uC9C0\uD5A5 \uB370\uC774\uD130 \uC804\uC1A1</li><li>Segment \uB2E8\uC704\uC758 \uC804\uC1A1 (MSS: Maximum Segment Size)</li><li>\uD750\uB984 \uC81C\uC5B4 (flow control: sliding window \uC0AC\uC6A9)</li></ul><h3 id="tcp-\u1109\u1166\u1100\u1173\u1106\u1165\u11AB\u1110\u1173-\u1112\u1167\u11BC\u1109\u1175\u11A8" tabindex="-1"><a class="header-anchor" href="#tcp-\u1109\u1166\u1100\u1173\u1106\u1165\u11AB\u1110\u1173-\u1112\u1167\u11BC\u1109\u1175\u11A8" aria-hidden="true">#</a> TCP \uC138\uADF8\uBA3C\uD2B8 \uD615\uC2DD</h3><ol><li>\uBC1C\uC2E0\uC9C0 \uD3EC\uD2B8 (16 bit)</li><li>\uBAA9\uC801\uC9C0 \uD3EC\uD2B8 (16 bit)</li><li>\uC21C\uC11C \uBC88\uD638 (32 bit) : \uB370\uC774\uD130 \uD544\uB4DC \uCCAB \uBC88\uC9F8 \uB370\uC774\uD130 \uBC14\uC774\uD2B8\uC758 \uC21C\uC11C \uBC88\uD638</li><li>\uC751\uB2F5 \uBC88\uD638 (ACK number) (32 bit)</li></ol><ul><li>\uC1A1\uC2E0\uCE21\uC73C\uB85C\uBD80\uD130 \uBC1B\uC740 \uC138\uADF8\uBA3C\uD2B8\uC5D0 \uB300\uD55C \uC751\uB2F5</li><li>\uBC14\uB85C \uB2E4\uC74C\uC5D0 \uBC1B\uAE30\uB97C \uAE30\uB300\uD558\uB294 \uC21C\uC11C \uBC88\uD638\uB97C \uC758\uBBF8\uD568</li></ul><ol start="5"><li>\uD5E4\uB354 \uAE38\uC774 (4 bit) : 5 ~ 15 \uC0AC\uC774\uC758 \uAC12 (20\uBC14\uC774\uD2B8 ~ 60\uBC14\uC774\uD2B8)</li><li>\uC608\uC57D (6 bit)</li><li>\uC81C\uC5B4 (6 bit)</li></ol><ul><li>URG : \uAE34\uAE09 \uD3EC\uC778\uD130(urgent pointer) \uD544\uB4DC\uAC00 \uC720\uD6A8\uD568\uC744 \uD45C\uC2DC\uD568</li><li>ACK : \uC751\uB2F5\uBC88\uD638\uAC00 \uC720\uD6A8\uD568\uC744 \uD45C\uC2DC\uD568</li><li>PSH : \uAC00\uB2A5\uD55C \uBE68\uB9AC \uD604\uC7AC \uC138\uADF8\uBA3C\uD2B8\uB97C \uC0C1\uC704 \uACC4\uCE35\uC5D0 \uC804\uB2EC\uD574\uC57C\uD568</li><li>RST : \uC5F0\uACB0\uC744 \uC7AC\uC124\uC815\uD568</li><li>SYN : \uC5F0\uACB0\uC744 \uCD08\uAE30\uD654\uD558\uAE30 \uC704\uD574 \uC21C\uC11C\uBC88\uD638\uB97C \uB3D9\uAE30\uD654\uC2DC\uD0B4</li><li>FIN : \uC5F0\uACB0\uC744 \uD574\uC81C\uC2DC\uD0A4\uAE30 \uC704\uD574 \uD604\uC7AC \uC138\uADF8\uBA3C\uD2B8\uAC00 \uB9C8\uC9C0\uB9C9 \uB370\uC774\uD130\uC784\uC744 \uD45C\uC2DC\uD568</li></ul><ol start="8"><li>\uC708\uB3C4\uC6B0 \uD06C\uAE30 (16 bit) : \uD750\uB984\uC81C\uC5B4\uB97C \uC704\uD55C \uC708\uB3C4\uC6B0\uC758 \uD06C\uAE30\uB97C \uBA85\uC2DC (\uCD5C\uB300 \uD06C\uAE30 = 2^16 byte)</li><li>\uAC80\uC0AC\uD569 (16 bit) : \uC624\uB958\uC81C\uC5B4\uB97C \uC704\uD55C \uAC80\uC0AC\uD569 \uBD80\uBD84</li><li>\uAE34\uAE09 \uD3EC\uC778\uD130 (16 bit) : URG flag = 1\uC77C \uB54C, \uAE34\uAE09 \uB370\uC774\uD130\uC758 \uB9C8\uC9C0\uB9C9 \uBC14\uC774\uD2B8\uC758 \uC21C\uC11C \uBC88\uD638 = \uAE34\uAE09\uD3EC\uC778\uD130 + \uC21C\uC11C \uBC88\uD638</li><li>\uC635\uC158 (option: 0 ~ 40 byte)</li></ol><ul><li>Maximum Segment Size (MSS)</li><li>\uC708\uB3C4\uC6B0 \uD06C\uAE30\uB97C \uC99D\uAC00\uC2DC\uD0A4\uAE30 \uC704\uD55C \uAC12</li><li>time stamp \uB4F1</li></ul><h3 id="tcp-\u110B\u1167\u11AB\u1100\u1167\u11AF\u1112\u1167\u11BC-\u1109\u1165\u1107\u1175\u1109\u1173" tabindex="-1"><a class="header-anchor" href="#tcp-\u110B\u1167\u11AB\u1100\u1167\u11AF\u1112\u1167\u11BC-\u1109\u1165\u1107\u1175\u1109\u1173" aria-hidden="true">#</a> TCP \uC5F0\uACB0\uD615 \uC11C\uBE44\uC2A4</h3><p>TCP \uC885\uC810(endpoint) \uC2DD\uBCC4\uC790 (=socket address)</p><ul><li>\uD638\uC2A4\uD2B8 IP \uC8FC\uC18C\uC640 \uD3EC\uD2B8 \uBC88\uD638\uB85C \uC815\uC758</li><li>\uC804\uC774\uC911(full-duplex) \uB370\uC774\uD130 \uC804\uC1A1 \uC11C\uBE44\uC2A4</li></ul><h3 id="tcp-\u110B\u1167\u11AB\u1100\u1167\u11AF-\u1109\u1165\u11AF\u110C\u1165\u11BC" tabindex="-1"><a class="header-anchor" href="#tcp-\u110B\u1167\u11AB\u1100\u1167\u11AF-\u1109\u1165\u11AF\u110C\u1165\u11BC" aria-hidden="true">#</a> TCP \uC5F0\uACB0 \uC124\uC815</h3><ul><li>3-way handshaking</li></ul><ol><li>\uC1A1\uC2E0\uCE21\uC5D0\uC11C \uBCF4\uB0B8 SYN\uC744 \uBCF4\uB0B8\uB2E4.</li><li>\uC218\uC2E0\uCE21\uC5D0\uC11C\uB294 \uBC1B\uC740 SYN\uC5D0 1\uC744 \uB354\uD558\uC5EC ACK \uAC12\uC744 \uACB0\uC815\uD558\uACE0 \uC218\uC2E0\uCE21\uB9CC\uC758 SYN\uC744 \uC0DD\uC131\uD558\uC5EC \uBCF4\uB0B8\uB2E4.</li><li>\uC1A1\uC2E0\uCE21\uC5D0\uC11C \uBC1B\uC740 \uB370\uC774\uD130\uC758 ACK\uAC12\uC774 \uCC98\uC74C\uC5D0\uBCF4\uB0B8 SYN\uC5D0 1\uC744 \uB354\uD55C \uAC12\uC774\uBA74 \uB370\uC774\uD130 \uC804\uC1A1\uC774 \uC81C\uB300\uB85C \uC774\uB8E8\uC5B4\uC9C4\uAC70\uB77C\uACE0 \uAC80\uC99D\uC774 \uB41C \uAC83\uC774\uB2E4. \uC218\uC2E0\uCE21\uC5D0\uAC8C \uB370\uC774\uD130\uB97C \uC81C\uB300\uB85C \uBC1B\uC558\uB2E4\uACE0 \uC804\uB2EC\uD558\uAE30 \uC704\uD574\uC11C \uC218\uC2E0\uCE21\uC5D0\uC11C \uBCF4\uB0B8 SYN \uAC12\uC5D0 1\uC744 \uB354\uD574\uC11C ACK\uC5D0 \uB2F4\uC544 \uBCF4\uB0B8\uB2E4.</li></ol><div class="custom-container tip"><p class="custom-container-title">\uCD08\uAE30 \uC21C\uC11C\uBC88\uD638(ISN: Initial Sequence Number)\uB97C \uC815\uD558\uB294 \uBC29\uBC95\uC740?</p><ul><li>\uB09C\uC218 \uBC1C\uC0DD\uAE30</li><li>\uC591 \uBC29\uD5A5\uC774 \uC11C\uB85C \uB2E4\uB978 ISN\uC744 \uC0AC\uC6A9\uD568</li></ul></div><h3 id="tcp-\u110B\u1167\u11AB\u1100\u1167\u11AF-\u110C\u1169\u11BC\u1105\u116D" tabindex="-1"><a class="header-anchor" href="#tcp-\u110B\u1167\u11AB\u1100\u1167\u11AF-\u110C\u1169\u11BC\u1105\u116D" aria-hidden="true">#</a> TCP \uC5F0\uACB0 \uC885\uB8CC</h3><ol><li>\uC1A1\uC2E0\uCE21\uC5D0\uC11C <code>FIN seq = x(random number)</code>\uB97C \uBCF4\uB0C4</li><li>\uC218\uC2E0\uCE21\uC5D0\uC11C FIN+1\uD55C\uAC12\uC744 ACK\uC5D0 \uB2F4\uACE0 \uC218\uC2E0\uCE21\uB9CC\uC758 FIN \uB610\uD55C \uB2F4\uC544\uC11C \uBCF4\uB0C4</li><li>\uC1A1\uC2E0\uCE21\uC740 ACK\uB97C \uAC80\uC99D\uD558\uACE0 \uC218\uC2E0\uCE21\uC5D0\uAC8C FIN+1\uC758 \uAC12\uC744 ACK\uC5D0 \uB2F4\uC544\uC11C \uBCF4\uB0C4</li><li>\uC218\uC2E0\uCE21\uC5D0\uC11C ACK\uB97C \uAC80\uC99D\uD558\uACE0 \uAC80\uC99D\uC774 \uC131\uACF5\uC801\uC73C\uB85C \uB410\uC73C\uBA74 \uD1B5\uC2E0 \uC885\uB8CC</li></ol>',34);function t(r,o){return a}var c=l(e,[["render",t]]);export{c as default};
