import{e as n}from"./app.2ad3e826.js";import{_ as s}from"./plugin-vue_export-helper.21dcd24c.js";const a={},e=n(`<h1 id="\u110B\u1170\u11B8-\u1109\u1173\u1110\u1169\u1105\u1175\u110C\u1175-web-storage" tabindex="-1"><a class="header-anchor" href="#\u110B\u1170\u11B8-\u1109\u1173\u1110\u1169\u1105\u1175\u110C\u1175-web-storage" aria-hidden="true">#</a> \uC6F9 \uC2A4\uD1A0\uB9AC\uC9C0 (Web Storage)</h1><blockquote><p>\uBE0C\uB77C\uC6B0\uC800\uC5D0\uC11C \uD0A4/\uAC12 \uC30D\uC744 \uCFE0\uD0A4\uBCF4\uB2E4 \uD6E8\uC52C \uC9C1\uAD00\uC801\uC73C\uB85C \uC800\uC7A5\uD560 \uC218 \uC788\uB294 \uBC29\uBC95\uC744 \uC81C\uACF5\uD558\uB294 API</p></blockquote><br><h2 id="web-storage\u110B\u1174-\u110C\u1169\u11BC\u1105\u1172" tabindex="-1"><a class="header-anchor" href="#web-storage\u110B\u1174-\u110C\u1169\u11BC\u1105\u1172" aria-hidden="true">#</a> Web Storage\uC758 \uC885\uB958</h2><p>Web Storage\uB294 sessionStorage, localStorage \uB450 \uAC00\uC9C0\uAC00 \uC788\uB294\uB370, \uC774\uB984 \uADF8\uB300\uB85C \uC138\uC158\uACFC \uB85C\uCEEC\uC5D0\uC11C \uB3D9\uC791\uD55C\uB2E4.</p><p>\uB610\uD55C Window \uAC1D\uCCB4\uB97C \uD1B5\uD574 \uC0AC\uC6A9\uD560 \uC218 \uC788\uB2E4.</p><p>ex) Window.sessionStorage, Window.localStorage</p><ul><li><strong>sessionStorage</strong><ul><li>\uD398\uC774\uC9C0 \uC138\uC158\uC5D0 \uD55C\uC815\uD574\uC11C, \uC989 \uBE0C\uB77C\uC6B0\uC800 \uB610\uB294 \uD0ED\uC774 \uB2EB\uD790 \uB54C\uAE4C\uC9C0\uB9CC \uB370\uC774\uD130\uB97C \uC800\uC7A5\uD55C\uB2E4.</li><li>\uB370\uC774\uD130\uB97C \uC808\uB300\uB85C \uC11C\uBC84\uB85C \uC804\uC1A1\uD558\uC9C0 \uC54A\uB294\uB2E4.</li><li>\uC800\uC7A5 \uACF5\uAC04\uC774 \uCFE0\uD0A4\uBCF4\uB2E4 \uD06C\uB2E4. (\uCD5C\uB300 5MB)</li></ul></li><li><strong>localStorage</strong><ul><li>\uC720\uD6A8\uAE30\uAC04 \uC5C6\uC774 \uB370\uC774\uD130\uB97C \uC800\uC7A5\uD558\uACE0, Javascript\uB97C \uC0AC\uC6A9\uD558\uAC70\uB098 \uCE90\uC2DC \uB610\uB294 \uB85C\uCEEC \uC800\uC7A5 \uB370\uC774\uD130\uB97C \uC9C0\uC6CC\uC57C\uB9CC \uC0AC\uB77C\uC9C4\uB2E4.<br> (\uC989, \uB3D9\uC77C\uD55C \uBE0C\uB77C\uC6B0\uC800\uC5D0\uC11C\uB294 \uB370\uC774\uD130\uAC00 \uC601\uC18D\uC131\uC744 \uAC00\uC9C4\uB2E4.)</li><li>\uC800\uC7A5 \uACF5\uAC04\uC774 \uC14B \uC911 \uAC00\uC7A5 \uD06C\uB2E4.</li></ul></li><li><strong>\uACF5\uD1B5\uC810</strong> : \uB370\uC774\uD130\uB97C \uBE0C\uB77C\uC6B0\uC800\uC5D0 \uC800\uC7A5\uD55C\uB2E4.</li></ul><br><h2 id="\u110C\u1166\u1100\u1169\u11BC-api" tabindex="-1"><a class="header-anchor" href="#\u110C\u1166\u1100\u1169\u11BC-api" aria-hidden="true">#</a> \uC81C\uACF5 API</h2><p>\uC6F9 \uC2A4\uD1A0\uB9AC\uC9C0\uB294 getItem\uC744 \uD1B5\uD574 \uAC12\uC744 \uB9AC\uD134\uBC1B\uC744 \uB54C <strong>\uBB34\uC870\uAC74 String\uC744 \uBC18\uD658\uD55C\uB2E4</strong>.</p><p>\uADF8\uB798\uC11C \uD0C0\uC785 \uBCC0\uD658\uC5D0 \uC8FC\uC758\uD558\uBA70 \uC0AC\uC6A9\uD574\uC57C \uD55C\uB2E4.</p><p>\uB300\uC548\uC73C\uB85C\uB294 value\uB97C JSON \uD615\uD0DC\uB85C \uC0AC\uC6A9\uD558\uB294 \uAC83\uC774\uB2E4.</p><div class="language-javascript ext-js line-numbers-mode"><pre class="language-javascript"><code><span class="token comment">// window.localStorage \uD639\uC740 localStorage\uB85C \uC811\uADFC \uAC00\uB2A5\uD558\uB2E4.</span>

<span class="token comment">// \uB370\uC774\uD130 \uC791\uC131</span>
localStorage<span class="token punctuation">.</span><span class="token function">setItem</span><span class="token punctuation">(</span><span class="token string">&#39;key&#39;</span><span class="token punctuation">,</span> value<span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token comment">// \uB370\uC774\uD130 \uC77D\uAE30 (\uC5B4\uB5A4 \uD0C0\uC785\uC774\uB4E0 \uBB34\uC870\uAC74 String\uC73C\uB85C \uB9AC\uD134\uD574\uC900\uB2E4)</span>
localStorage<span class="token punctuation">.</span><span class="token function">getItem</span><span class="token punctuation">(</span><span class="token string">&#39;key&#39;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token comment">// \uB370\uC774\uD130 \uC0AD\uC81C</span>
localStorage<span class="token punctuation">.</span><span class="token function">removeItem</span><span class="token punctuation">(</span><span class="token string">&#39;key&#39;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token comment">// \uC2A4\uD1A0\uB9AC\uC9C0 \uCD08\uAE30\uD654</span>
localStorage<span class="token punctuation">.</span><span class="token function">clear</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token comment">// \uC800\uC7A5\uB41C key\uC758 \uAC1C\uC218</span>
localStorage<span class="token punctuation">.</span>length<span class="token punctuation">;</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br></div></div><br><h2 id="\u110B\u1168\u110C\u1166" tabindex="-1"><a class="header-anchor" href="#\u110B\u1168\u110C\u1166" aria-hidden="true">#</a> \uC608\uC81C</h2><div class="language-javascript ext-js line-numbers-mode"><pre class="language-javascript"><code>window<span class="token punctuation">.</span>localStorage<span class="token punctuation">.</span><span class="token function">setItem</span><span class="token punctuation">(</span><span class="token string">&quot;name&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;giwon&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// &quot;name&quot;: &quot;giwon&quot; \uD615\uC2DD\uC73C\uB85C \uC800\uC7A5\uB428</span>
<span class="token keyword">const</span> name <span class="token operator">=</span> window<span class="token punctuation">.</span>localStorage<span class="token punctuation">.</span><span class="token function">getItem</span><span class="token punctuation">(</span><span class="token string">&quot;name&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>name<span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// &#39;giwon&#39; \uCD9C\uB825</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div>`,17);function t(p,o){return e}var i=s(a,[["render",t]]);export{i as default};
